INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-001', 'Catullus Poem 1', 'Cui dono lepidum novum libellum', 'To whom do I give my new little book?', '{"cui": {"meaning": "to whom", "grammar": "dative of quis"}, "dono": {"meaning": "I give", "grammar": "1st person singular, present indicative"}, "lepidum": {"meaning": "charming", "grammar": "adjective, neuter accusative"}, "novum": {"meaning": "new", "grammar": "adjective, neuter accusative"}, "libellum": {"meaning": "little book", "grammar": "noun, masculine accusative"}}', '- Dative of advantage in "cui dono" - Accusative direct object "novum libellum" - Present tense for ongoing action', 'This opening poem describes Catullus presenting his poetry book to Cornelius Nepos, a patron and literary friend in Rome.', 7, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-002', 'Catullus Poem 2', 'Passer, deliciae meae puellae', 'Sparrow, delight of my girl', '{"passer": {"meaning": "sparrow", "grammar": "noun, masculine nominative"}, "deliciae": {"meaning": "delight, pleasure", "grammar": "noun, feminine plural nominative"}, "meae": {"meaning": "of my", "grammar": "possessive adjective"}, "puellae": {"meaning": "of the girl", "grammar": "noun, feminine genitive singular"}}', '- Vocative case for direct address - Genitive of quality "deliciae meae puellae" - Enclitic "-ne" implied in questioning tone', 'This is the famous opening of the "Sparrow poems" where Catullus addresses his beloved Lesbia''s pet sparrow.', 5, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-003', 'Catullus Poem 4', 'Phaselus ille, quem videtis, hospites', 'That little ship, which you see, strangers', '{"phaselus": {"meaning": "little ship", "grammar": "noun, masculine nominative"}, "ille": {"meaning": "that", "grammar": "demonstrative adjective"}, "quem": {"meaning": "which", "grammar": "relative pronoun, accusative"}, "videtis": {"meaning": "you see", "grammar": "2nd person plural, present indicative"}, "hospites": {"meaning": "strangers", "grammar": "noun, masculine plural vocative"}}', '- Relative clause of characteristic "quem videtis" - Demonstrative and relative pronouns together - Vocative addressing the audience', 'This is the opening of the "Ship Poem" where Catullus describes his vessel''s past achievements.', 6, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-004', 'Vergil Aeneid 1.1', 'Arma virumque cano, Troiae qui primus ab oris', 'I sing of arms and the man, who first from the Trojan shores', '{"arma": {"meaning": "arms", "grammar": "noun, neuter plural accusative"}, "virum": {"meaning": "man", "grammar": "noun, masculine accusative"}, "cano": {"meaning": "I sing", "grammar": "1st person singular, present indicative"}, "Troiae": {"meaning": "of Troy", "grammar": "noun, feminine genitive"}, "qui": {"meaning": "who", "grammar": "relative pronoun, nominative masculine"}, "primus": {"meaning": "first", "grammar": "adjective, nominative masculine"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "oris": {"meaning": "shores", "grammar": "noun, feminine plural ablative"}}', '- Accusative of respect with "arma virumque" - Relative clause explaining "virum" - Ablative of place from which', 'This is the famous opening of Vergil''s Aeneid, the national epic of Rome, describing Aeneas'' exile from Troy.', 8, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-005', 'Vergil Aeneid 1.12', 'Urbs antiqua fuit, Tyrii tenuere coloni', 'There was an ancient city; Tyrian colonists possessed it', '{"urbs": {"meaning": "city", "grammar": "noun, feminine nominative"}, "antiqua": {"meaning": "ancient", "grammar": "adjective, feminine nominative"}, "fuit": {"meaning": "there was", "grammar": "3rd person singular, perfect indicative of sum"}, "Tyrii": {"meaning": "Tyrians", "grammar": "adjective used as noun, masculine plural nominative"}, "tenuere": {"meaning": "they possessed", "grammar": "3rd person plural, perfect indicative"}, "coloni": {"meaning": "colonists", "grammar": "noun, masculine plural nominative"}}', '- Impersonal use of "fuit" - Perfect tense for completed past action - Subject inversion for poetic effect', 'This describes the founding of Carthage by Tyrian colonists, setting up the conflict with Rome.', 6, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-006', 'Ovid Metamorphoses 1.1', 'In nova fert animus mutatas dicere formas', 'My mind is carried to sing of forms changed into new shapes', '{"in": {"meaning": "to", "grammar": "preposition + accusative"}, "nova": {"meaning": "new", "grammar": "adjective, neuter plural accusative"}, "fert": {"meaning": "carries", "grammar": "3rd person singular, present indicative"}, "animus": {"meaning": "mind", "grammar": "noun, masculine nominative"}, "mutatas": {"meaning": "changed", "grammar": "perfect passive participle, feminine plural accusative"}, "dicere": {"meaning": "to tell", "grammar": "infinitive"}, "formas": {"meaning": "shapes", "grammar": "noun, feminine plural accusative"}}', '- Accusative of respect "nova" - Accusative with infinitive "formas...dicere" - Perfect passive participle modifying "formas"', 'This is the opening invocation of Ovid''s Metamorphoses, asking the gods for inspiration to tell transformation stories.', 8, 'Ovid', 'Metamorphoses');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-007', 'Ovid Metamorphoses 1.5', 'Ante mare et terras et quodcumque in ordine', 'Before sea and lands and whatever there is in order', '{"ante": {"meaning": "before", "grammar": "adverb"}, "mare": {"meaning": "sea", "grammar": "noun, neuter singular accusative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "terras": {"meaning": "lands", "grammar": "noun, feminine plural accusative"}, "quodcumque": {"meaning": "whatever", "grammar": "indefinite relative pronoun, neuter singular nominative"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "ordine": {"meaning": "order", "grammar": "noun, masculine ablative singular"}}', '- Accusative of extent "mare et terras" - Indefinite relative pronoun "quodcumque" - Ablative of place or state "in ordine"', 'This describes the primordial chaos before the creation of the ordered world.', 7, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-008', 'Caesar Gallic War 1.1.1', 'Gallia est omnis divisa in partes tres', 'All Gaul is divided into three parts', '{"Gallia": {"meaning": "Gaul", "grammar": "noun, feminine nominative"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "omnis": {"meaning": "whole, all", "grammar": "adjective, feminine nominative"}, "divisa": {"meaning": "divided", "grammar": "perfect passive participle, feminine nominative"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "partes": {"meaning": "parts", "grammar": "noun, feminine plural accusative"}, "tres": {"meaning": "three", "grammar": "adjective, feminine plural accusative"}}', '- Passive voice with present perfect - Accusative of extent "partes tres" - Prepositional phrase "in partes"', 'This is the famous opening of Caesar''s Gallic War, describing the geography of Gaul before his conquest.', 6, 'Caesar', 'De Bello Gallico');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-009', 'Caesar Gallic War 1.2.3', 'Horum omnium fortissimi sunt Belgae', 'Of all these, the bravest are the Belgae', '{"horum": {"meaning": "of these", "grammar": "demonstrative pronoun, neuter plural genitive"}, "omnium": {"meaning": "of all", "grammar": "adjective, neuter plural genitive"}, "fortissimi": {"meaning": "bravest", "grammar": "adjective, masculine plural nominative"}, "sunt": {"meaning": "they are", "grammar": "3rd person plural, present indicative of sum"}, "Belgae": {"meaning": "the Belgae", "grammar": "noun, masculine plural nominative"}}', '- Genitive of comparison "horum omnium" - Superlative degree "fortissimi" - Predicate nominative with sum', 'Caesar provides ethnographic information about the peoples of Gaul, highlighting the Belgae as the most warlike.', 5, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-010', 'Catullus Poem 6', 'Flavi, delicias tuas Catullo, ni sint', 'Flavius, your delights for Catullus, unless they are', '{"Flavii": {"meaning": "Flavius", "grammar": "proper noun, masculine vocative"}, "delicias": {"meaning": "delights", "grammar": "noun, feminine plural accusative"}, "tuas": {"meaning": "your", "grammar": "possessive adjective, feminine plural accusative"}, "Catullo": {"meaning": "for Catullus", "grammar": "noun, masculine dative singular"}, "ni": {"meaning": "unless", "grammar": "conjunction"}, "sint": {"meaning": "they may be", "grammar": "3rd person plural, present subjunctive"}}', '- Dative of advantage "Catullo" - Conditional clause with "ni" - Subjunctive mood expressing contingency', 'This is the beginning of a poem addressed to Flavius, possibly a literary friend, about relationship matters.', 6, 'Classical Author', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-011', 'Catullus Poem 7', 'Quaeris, quot mihi basiationes tuae', 'You ask how many kisses, your kisses for me', '{"quaeris": {"meaning": "you ask", "grammar": "2nd person singular, present indicative"}, "quot": {"meaning": "how many", "grammar": "interrogative adjective, feminine plural nominative"}, "mihi": {"meaning": "for me", "grammar": "personal pronoun, dative singular"}, "basiationes": {"meaning": "kisses", "grammar": "noun, feminine plural nominative"}, "tuae": {"meaning": "your", "grammar": "possessive adjective, feminine plural nominative"}}', '- Interrogative adjective "quot" - Dative of interest "mihi" - Genitive implied in "tuae basiationes"', 'This famous poem discusses the mathematics of love and kissing between Catullus and Lesbia.', 5, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-012', 'Catullus Poem 8', 'Miser Catulle, desinas ineptire, et quod vides', 'Miserable Catullus, stop playing the fool, and what you see', '{"miser": {"meaning": "wretched", "grammar": "adjective, masculine vocative"}, "Catulle": {"meaning": "Catullus", "grammar": "proper noun, masculine vocative"}, "desinas": {"meaning": "stop", "grammar": "2nd person singular, present subjunctive"}, "ineptire": {"meaning": "playing the fool", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quod": {"meaning": "what", "grammar": "relative pronoun, accusative"}, "vides": {"meaning": "you see", "grammar": "2nd person singular, present indicative"}}', '- Vocative for direct address - Jussive subjunctive with "desinas" - Relative pronoun as object of "vides"', 'This is the famous "ad se ipsum" poem where Catullus resolves to stop pursuing an unfaithful lover.', 7, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-013', 'Vergil Aeneid 1.13', 'Karthago, Italiam contra Tiberinaque longae', 'Carthage, opposite Italy and the long Tiberine mouth', '{"Karthago": {"meaning": "Carthage", "grammar": "proper noun, feminine nominative"}, "Italiam": {"meaning": "Italy", "grammar": "noun, feminine accusative"}, "contra": {"meaning": "opposite", "grammar": "preposition + accusative"}, "Tiberina": {"meaning": "of the Tiber", "grammar": "adjective, feminine plural genitive"}, "que": {"meaning": "and", "grammar": "enclitic conjunction"}, "longae": {"meaning": "long", "grammar": "adjective, feminine plural genitive"}}', '- Apposition "Karthago, Italiam contra" - Genitive of quality "Tiberinaque longae" - Enclitic "-que" connecting nouns', 'Vergil describes Carthage''s strategic position opposite Italy across the sea.', 6, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-014', 'Vergil Aeneid 1.33', 'Tantae molis erat Romanam condere gentem', 'So great a task it was to found the Roman race', '{"tantae": {"meaning": "so great", "grammar": "adjective, feminine genitive singular"}, "molis": {"meaning": "of labor", "grammar": "noun, feminine genitive singular"}, "erat": {"meaning": "it was", "grammar": "3rd person singular, imperfect indicative of sum"}, "Romanam": {"meaning": "Roman", "grammar": "adjective, feminine accusative"}, "condere": {"meaning": "to found", "grammar": "infinitive"}, "gentem": {"meaning": "race", "grammar": "noun, feminine accusative singular"}}', '- Genitive of characteristic "Tantae molis" - Impersonal construction "erat" - Accusative with infinitive "Romanam...condere"', 'Vergil emphasizes the heroic nature of Rome''s founding, requiring divine intervention.', 6, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-015', 'Ovid Metamorphoses 1.10', 'Nullus adhuc mundo praebebat lumina Titan', 'None as yet provided light to the world; the Sun', '{"nullus": {"meaning": "none", "grammar": "adjective, masculine nominative singular"}, "adhuc": {"meaning": "as yet", "grammar": "adverb"}, "mundo": {"meaning": "to the world", "grammar": "noun, masculine dative singular"}, "praebebat": {"meaning": "provided", "grammar": "3rd person singular, imperfect indicative"}, "lumina": {"meaning": "light", "grammar": "noun, neuter plural accusative"}, "Titan": {"meaning": "the Sun", "grammar": "proper noun, masculine nominative singular"}}', '- Dative of advantage "mundo" - Imperfect tense for ongoing past action - Subject omitted and resumed with "Titan"', 'Ovid describes the primordial state before the sun was created, emphasizing cosmic chaos.', 6, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-016', 'Ovid Metamorphoses 1.20', 'Frigida pugnabant calidis, umentia siccis', 'Cold fought with hot, moist with dry', '{"frigida": {"meaning": "cold things", "grammar": "adjective used as noun, neuter plural nominative"}, "pugnabant": {"meaning": "fought", "grammar": "3rd person plural, imperfect indicative"}, "calidis": {"meaning": "with hot things", "grammar": "adjective used as noun, neuter plural ablative"}, "umentia": {"meaning": "moist things", "grammar": "adjective used as noun, neuter plural nominative"}, "siccis": {"meaning": "with dry things", "grammar": "adjective used as noun, neuter plural ablative"}}', '- Ablative with cum omitted - Imperfect tense for ongoing conflict - Neuter plural used generically', 'This describes the primordial chaos where opposing elements struggled against each other.', 5, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-017', 'Caesar Gallic War 1.3.2', 'Id hoc facilius iis persuasit, quod undique loci', 'He persuaded them of this more easily, because on every side the nature of place', '{"id": {"meaning": "this", "grammar": "demonstrative pronoun, neuter accusative"}, "hoc": {"meaning": "by this", "grammar": "demonstrative pronoun, neuter ablative"}, "facilius": {"meaning": "more easily", "grammar": "adverb"}, "iis": {"meaning": "to them", "grammar": "demonstrative pronoun, dative plural"}, "persuasit": {"meaning": "he persuaded", "grammar": "3rd person singular, perfect indicative"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "undique": {"meaning": "on every side", "grammar": "adverb"}, "loci": {"meaning": "of place", "grammar": "noun, neuter genitive singular"}}', '- Comparative adjective "facilius" - Dative of person addressed "iis" - Causal clause with "quod"', 'Caesar explains how geographic isolation motivated the Helvetii to seek better lands.', 8, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-018', 'Caesar Gallic War 1.4.1', 'Ea res est Helvetiis per indicium enuntiata', 'That matter was made known to the Helvetii through informers', '{"ea": {"meaning": "that", "grammar": "demonstrative adjective, feminine nominative singular"}, "res": {"meaning": "matter", "grammar": "noun, feminine nominative singular"}, "est": {"meaning": "was made", "grammar": "3rd person singular, present passive"}, "Helvetiis": {"meaning": "to the Helvetii", "grammar": "proper noun, dative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "indicium": {"meaning": "informer", "grammar": "noun, masculine accusative singular"}, "enuntiata": {"meaning": "made known", "grammar": "perfect passive participle, feminine nominative singular"}}', '- Passive construction with est - Dative of advantage "Helvetiis" - Perfect passive participle', 'This describes how the conspiracy of Orgetorix was discovered through informants.', 6, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-019', 'Catullus Poem 9', 'Verani, omnibus e meis amicis, antistans', 'Veranius, among all my friends, you stand out', '{"Verani": {"meaning": "Veranius", "grammar": "proper noun, masculine vocative"}, "omnibus": {"meaning": "among all", "grammar": "adjective, neuter plural ablative"}, "e": {"meaning": "from", "grammar": "preposition + ablative"}, "meis": {"meaning": "my", "grammar": "possessive adjective, neuter plural ablative"}, "amicis": {"meaning": "friends", "grammar": "noun, masculine plural ablative"}, "antistans": {"meaning": "standing out", "grammar": "present participle, masculine nominative singular"}}', '- Vocative addressing a friend - Ablative of place "omnibus e meis amicis" - Present participle "antistans"', 'Catullus welcomes his friend Veranius back from Hispania and praises him highly.', 6, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-020', 'Catullus Poem 10', 'Varus me meus ad suos amores, visum duxerat', 'My Varus had led me to see his loves, to his leisure', '{"Varus": {"meaning": "Varus", "grammar": "proper noun, masculine nominative singular"}, "me": {"meaning": "me", "grammar": "personal pronoun, accusative"}, "meus": {"meaning": "my", "grammar": "possessive adjective, masculine nominative singular"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "suos": {"meaning": "his", "grammar": "possessive adjective, masculine plural accusative"}, "amores": {"meaning": "loves", "grammar": "noun, masculine plural accusative"}, "visum": {"meaning": "to see", "grammar": "supine"}, "duxerat": {"meaning": "he had led", "grammar": "3rd person singular, pluperfect indicative"}}', '- Accusative subject of supine "visum" - Pluperfect tense for completed past action - Dative of agent implied', 'Catullus describes being led by Varus to a tavern scene, leading to humorous encounters.', 8, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-021', 'Vergil Aeneid 1.34', 'Vix e conspectu Siculae telluris in altum', 'Hardly from the sight of the Sicilian land into the deep', '{"vix": {"meaning": "hardly", "grammar": "adverb"}, "e": {"meaning": "from", "grammar": "preposition + ablative"}, "conspectu": {"meaning": "from the sight", "grammar": "noun, masculine ablative singular"}, "Siculae": {"meaning": "of Sicily", "grammar": "adjective, feminine genitive singular"}, "telluris": {"meaning": "of land", "grammar": "noun, feminine genitive singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "altum": {"meaning": "the deep", "grammar": "noun, neuter accusative singular"}}', '- Ablative of separation "e conspectu" - Genitive of quality "Siculae telluris" - Accusative of place "in altum"', 'This describes the Trojans leaving Sicily after the death of Anchises, heading into the Mediterranean.', 7, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-022', 'Vergil Aeneid 1.35', 'Vela dabant laeti, et spumas salis aere ruebant', 'They gave sails gladly, and with bronze they turned back the foaming salt', '{"vela": {"meaning": "sails", "grammar": "noun, neuter plural accusative"}, "dabant": {"meaning": "they gave", "grammar": "3rd person plural, imperfect indicative"}, "laeti": {"meaning": "gladly", "grammar": "adverb"}, "et": {"meaning": "and", "grammar": "conjunction"}, "spumas": {"meaning": "foam", "grammar": "noun, feminine plural accusative"}, "salis": {"meaning": "of salt", "grammar": "noun, masculine genitive singular"}, "aere": {"meaning": "with bronze", "grammar": "noun, neuter ablative singular"}, "ruebant": {"meaning": "they turned", "grammar": "3rd person plural, imperfect indicative"}}', '- Ablative of means "aere" - Genitive of quality "salis" - Imperfect tense for ongoing action', 'Vergil describes the Trojans sailing hopefully, using bronze tools to propel their ships.', 8, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-023', 'Ovid Metamorphoses 1.15', 'Sic erat et tellus illic et pontus et aer', 'So were both the earth there and the sea and the air', '{"sic": {"meaning": "so", "grammar": "adverb"}, "erat": {"meaning": "was", "grammar": "3rd person singular, imperfect indicative of sum"}, "et": {"meaning": "and", "grammar": "conjunction"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "illic": {"meaning": "there", "grammar": "adverb"}, "pontus": {"meaning": "sea", "grammar": "noun, masculine nominative singular"}, "aer": {"meaning": "air", "grammar": "noun, masculine nominative singular"}}', '- Impersonal construction "erat" - Enumeration with repeated "et" - Coordinate clauses', 'Ovid describes the primordial state where elements were in chaotic mixture.', 8, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-024', 'Ovid Metamorphoses 1.25', 'Dissociata locis concordi pace ligavit', 'He separated them from their places and bound them with peaceful harmony', '{"dissociata": {"meaning": "separated", "grammar": "perfect passive participle, neuter plural accusative"}, "locis": {"meaning": "from their places", "grammar": "noun, neuter plural ablative"}, "concordi": {"meaning": "with harmony", "grammar": "adjective, feminine ablative singular"}, "pace": {"meaning": "with peace", "grammar": "noun, feminine ablative singular"}, "ligavit": {"meaning": "he bound", "grammar": "3rd person singular, perfect indicative"}}', '- Ablative of separation "locis" - Ablative of means "pace" - Perfect tense for completed divine action', 'This describes how the divine ordered the primordial chaos into harmonious separation.', 5, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
