INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-023', 'Ovid Metamorphoses 1.15', 'Sic erat et tellus illic et pontus et aer', 'So were both the earth there and the sea and the air', '{"sic": {"meaning": "so", "grammar": "adverb"}, "erat": {"meaning": "was", "grammar": "3rd person singular, imperfect indicative of sum"}, "et": {"meaning": "and", "grammar": "conjunction"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "illic": {"meaning": "there", "grammar": "adverb"}, "pontus": {"meaning": "sea", "grammar": "noun, masculine nominative singular"}, "aer": {"meaning": "air", "grammar": "noun, masculine nominative singular"}}', '- Impersonal construction "erat" - Enumeration with repeated "et" - Coordinate clauses', 'Ovid describes the primordial state where elements were in chaotic mixture.', 8, 'Ovid', 'Classical Work');
