INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-024', 'Ovid Metamorphoses 1.25', 'Dissociata locis concordi pace ligavit', 'He separated them from their places and bound them with peaceful harmony', '{"dissociata": {"meaning": "separated", "grammar": "perfect passive participle, neuter plural accusative"}, "locis": {"meaning": "from their places", "grammar": "noun, neuter plural ablative"}, "concordi": {"meaning": "with harmony", "grammar": "adjective, feminine ablative singular"}, "pace": {"meaning": "with peace", "grammar": "noun, feminine ablative singular"}, "ligavit": {"meaning": "he bound", "grammar": "3rd person singular, perfect indicative"}}', '- Ablative of separation "locis" - Ablative of means "pace" - Perfect tense for completed divine action', 'This describes how the divine ordered the primordial chaos into harmonious separation.', 5, 'Classical Author', 'Classical Work');
