INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-025', 'Caesar Gallic War 1.5.2', 'Ubi iam se ad eam rem paratos esse arbitrati', '', '{"ubi": {"meaning": "when", "grammar": "conjunction"}, "iam": {"meaning": "now", "grammar": "adverb"}, "se": {"meaning": "themselves", "grammar": "reflexive pronoun, accusative"}, "ad": {"meaning": "for", "grammar": "preposition + accusative"}, "eam": {"meaning": "that", "grammar": "demonstrative adjective, feminine accusative"}, "rem": {"meaning": "enterprise", "grammar": "noun, feminine accusative"}, "paratos": {"meaning": "prepared", "grammar": "adjective, masculine accusative plural"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "arbitrati": {"meaning": "they thought", "grammar": "3rd person plural, perfect indicative"}}', '- Temporal clause "ubi...arbitrati" - Accusative with infinitive "se...paratos esse" - Perfect tense for completed thought', 'This describes the Helvetii preparing for their great migration, kindling Caesar''s intervention.', 8, 'Caesar', 'Classical Work');
