INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-027', 'Catullus Poem 12', 'Marrucine Asini, manu sinistra non belle uteris', 'Asinius Marrucinus, you use your left hand unskillfully', '{"Marrucine": {"meaning": "Marrucinus", "grammar": "proper noun, masculine vocative"}, "Asini": {"meaning": "Asinius", "grammar": "proper noun, masculine vocative"}, "manu": {"meaning": "with your hand", "grammar": "noun, feminine ablative singular"}, "sinistra": {"meaning": "left", "grammar": "adjective, feminine ablative singular"}, "non": {"meaning": "not", "grammar": "adverb"}, "belle": {"meaning": "skillfully", "grammar": "adverb"}, "uteris": {"meaning": "you use", "grammar": "2nd person singular, present indicative"}}', '- Double vocative - Ablative of instrument "manu" - Present tense for current action', 'Catullus criticizes Marrucinus for stealing tablecloths, using wit and wordplay.', 7, 'Catullus', 'Poems');
