INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-035', 'Caesar Gallic War 1.7.1', 'Caesari cum id nuntiatum esset, eos per provinciam', '', '{"Caesari": {"meaning": "to Caesar", "grammar": "proper noun, masculine dative singular"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "id": {"meaning": "this", "grammar": "demonstrative pronoun, neuter accusative"}, "nuntiatum": {"meaning": "reported", "grammar": "perfect passive participle, neuter accusative"}, "esset": {"meaning": "would be", "grammar": "3rd person singular, imperfect subjunctive"}, "eos": {"meaning": "they", "grammar": "demonstrative pronoun, masculine accusative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "provinciam": {"meaning": "the province", "grammar": "noun, feminine accusative singular"}}', '- Temporal clause with "cum" - Accusative with infinitive "id...nuntiatum esset" - Subjunctive mood in temporal clause', 'This begins Caesar''s personal involvement, receiving news of the Helvetii''s invasion plan.', 8, 'Caesar', 'Classical Work');
