INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-001', 'Catullus Poem 5', 'Vivamus mea Lesbia, atque amemus, rumoresque senum severiorum, omnes unius aestimemus assis', 'Let us live, my Lesbia, and love; and the rumors of stricter old men, let us value all at one penny', '{"vivamus": {"meaning": "let us live", "grammar": "1st person plural, present subjunctive"}, "mea": {"meaning": "my", "grammar": "possessive adjective, feminine vocative"}, "Lesbia": {"meaning": "Lesbia", "grammar": "proper noun, feminine vocative"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "amemus": {"meaning": "let us love", "grammar": "1st person plural, present subjunctive"}, "rumores": {"meaning": "rumors", "grammar": "noun, masculine plural accusative"}, "que": {"meaning": "and", "grammar": "enclitic conjunction"}, "senum": {"meaning": "of old men", "grammar": "noun, masculine plural genitive"}, "severiorum": {"meaning": "stricter", "grammar": "adjective, masculine plural genitive"}, "omnes": {"meaning": "all", "grammar": "adjective, masculine plural accusative"}, "unius": {"meaning": "of one", "grammar": "adjective, masculine genitive singular"}, "aestimemus": {"meaning": "let us value", "grammar": "1st person plural, present subjunctive"}, "assis": {"meaning": "penny", "grammar": "noun, masculine genitive singular"}}', '- Jussive subjunctives "vivamus, amemus, aestimemus" - Genitive of price "unius assis" - Coordinate main clauses with jussive meaning - Alliteration "vivamus...amemus" for rhetorical effect', 'This is the most famous of Catullus'' love poems, a carpe diem invitation to ignore social convention and embrace passionate love.', 15, 'Catullus', 'Poems');
