INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-005', 'Vergil Aeneid 1.19-22', 'Prōgeniem sed enim Trōiānō ā sanguine dūcī, audierat, Tyriās olim quae verteret arcēs; hinc populum lātē regem bellōque superbum, ventūrum excidiō Libyae', 'But indeed she had heard that a descendant was to be traced to Trojan blood, Tyrian walls which he would overthrow; hence a people widely king in war and proud, destined to come to the destruction of Libya', '{"pr\u014dgeniem": {"meaning": "descendant", "grammar": "noun, feminine accusative singular"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "enim": {"meaning": "indeed", "grammar": "adverb"}, "tr\u014di\u0101n\u014d": {"meaning": "Trojan", "grammar": "adjective, masculine ablative singular"}, "\u0101": {"meaning": "from", "grammar": "preposition + ablative"}, "sanguine": {"meaning": "blood", "grammar": "noun, masculine ablative singular"}, "d\u016bc\u012b": {"meaning": "to be traced", "grammar": "infinitive passive"}, "audierat": {"meaning": "she had heard", "grammar": "3rd person singular, pluperfect indicative"}, "tyri\u0101s": {"meaning": "Tyrian", "grammar": "adjective, feminine plural accusative"}, "olim": {"meaning": "once", "grammar": "adverb"}, "quae": {"meaning": "which", "grammar": "relative pronoun, feminine plural accusative"}, "verteret": {"meaning": "would overthrow", "grammar": "3rd person singular, imperfect subjunctive"}, "arc\u0113s": {"meaning": "walls", "grammar": "noun, feminine plural accusative"}, "hinc": {"meaning": "hence", "grammar": "adverb"}, "populum": {"meaning": "people", "grammar": "noun, masculine accusative singular"}, "l\u0101t\u0113": {"meaning": "widely", "grammar": "adverb"}, "regem": {"meaning": "king", "grammar": "noun, masculine accusative singular"}, "bell\u014d": {"meaning": "in war", "grammar": "noun, neuter ablative singular"}, "que": {"meaning": "and", "grammar": "enclitic conjunction"}, "superbum": {"meaning": "proud", "grammar": "adjective, masculine accusative singular"}, "vent\u016brum": {"meaning": "destined to come", "grammar": "future active participle, masculine accusative singular"}, "excidi\u014d": {"meaning": "to the destruction", "grammar": "noun, neuter dative singular"}, "libyae": {"meaning": "of Libya", "grammar": "noun, feminine genitive singular"}}', '- Accusative with infinitive "prōgeniem...dūcī" - Indirect statement with "audierat...dūcī" - Future active participle "ventūrum" expressing destiny - Subjunctive "verteret" in relative clause expressing purpose - Chiasmus structure "Tyriās olim...hinc populum"', 'Juno''s fear of Rome''s future destiny, the destruction she foresees, drives her to plot against Aeneas despite his piety and noble mission.', 23, 'Classical Author', 'Aeneid');
