INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-006', 'Ovid Metamorphoses 1.20-25', 'Frigida pugnabant calidis, umentia siccis, mollia cum duris, sine pondere, habentia pondus. Hanc deus et melior litem natura direxit', 'Cold fought with hot, moist with dry, soft with hard, weightless things having weight. This quarrel god and better nature directed', '{"frigida": {"meaning": "cold", "grammar": "adjective used as noun, neuter plural nominative"}, "pugnabant": {"meaning": "fought", "grammar": "3rd person plural, imperfect indicative"}, "calidis": {"meaning": "with hot", "grammar": "adjective used as noun, neuter plural ablative"}, "umentia": {"meaning": "moist", "grammar": "adjective used as noun, neuter plural nominative"}, "siccis": {"meaning": "with dry", "grammar": "adjective used as noun, neuter plural ablative"}, "mollia": {"meaning": "soft", "grammar": "adjective used as noun, neuter plural nominative"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "duris": {"meaning": "with hard", "grammar": "adjective used as noun, neuter plural ablative"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "pondere": {"meaning": "weight", "grammar": "noun, neuter ablative singular"}, "habentia": {"meaning": "having", "grammar": "present participle, neuter plural accusative"}, "pondus": {"meaning": "weight", "grammar": "noun, neuter accusative singular"}, "hanc": {"meaning": "this", "grammar": "demonstrative adjective, feminine accusative"}, "deus": {"meaning": "god", "grammar": "noun, masculine nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "melior": {"meaning": "better", "grammar": "adjective, feminine nominative singular"}, "litem": {"meaning": "quarrel", "grammar": "noun, feminine accusative singular"}, "natura": {"meaning": "nature", "grammar": "noun, feminine nominative singular"}, "direxit": {"meaning": "directed", "grammar": "3rd person singular, perfect indicative"}}', '- Ablative of means/comparison omitted - Impersonal construction with neuter plural nouns - Present participle "habentia" modifying earlier nouns - Perfect tense "direxit" for completed divine action - Paradoxical statements highlighting cosmic chaos', 'Ovid describes the primordial conflict of opposing elements before divine intervention established cosmic order and harmony.', 19, 'Ovid', 'Classical Work');
