INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-007', 'Caesar Gallic War 1.2.1-2', 'Apud Helvetios longe nobilissimus fuit et ditissimus Orgetorix. Is M. Messala, [et P.] M. Pisone consulibus regni cupiditate inductus coniurationem nobilitatis fecit', 'Among the Helvetii, far the most noble and wealthiest was Orgetorix. He, being led by desire for kingship during the consulship of M. Messala and [P.] M. Piso, made a conspiracy of the nobility', '{"apud": {"meaning": "among", "grammar": "preposition + accusative"}, "helvetii": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural accusative"}, "longe": {"meaning": "far", "grammar": "adverb"}, "nobilissimus": {"meaning": "most noble", "grammar": "adjective, masculine nominative singular superlative"}, "fuit": {"meaning": "was", "grammar": "3rd person singular, perfect indicative of sum"}, "et": {"meaning": "and", "grammar": "conjunction"}, "ditissimus": {"meaning": "wealthiest", "grammar": "adjective, masculine nominative singular superlative"}, "orgetorix": {"meaning": "Orgetorix", "grammar": "proper noun, masculine nominative singular"}, "is": {"meaning": "he", "grammar": "demonstrative pronoun, masculine nominative singular"}, "messala": {"meaning": "Messala", "grammar": "proper noun, masculine ablative singular"}, "m.": {"meaning": "Marcus", "grammar": "abbreviation"}, "p.": {"meaning": "Publius", "grammar": "abbreviation"}, "pisone": {"meaning": "Piso", "grammar": "proper noun, masculine ablative singular"}, "consulibus": {"meaning": "during the consulship", "grammar": "noun, masculine ablative plural"}, "regni": {"meaning": "for kingship", "grammar": "noun, neuter genitive singular"}, "cupiditate": {"meaning": "by desire", "grammar": "noun, feminine ablative singular"}, "inductus": {"meaning": "being led", "grammar": "perfect passive participle, masculine nominative singular"}, "coniurationem": {"meaning": "conspiracy", "grammar": "noun, feminine accusative singular"}, "nobilitatis": {"meaning": "of the nobility", "grammar": "noun, feminine genitive singular"}, "fecit": {"meaning": "he made", "grammar": "3rd person singular, perfect indicative"}}', '- Ablative of time "consulibus" - Genitive of purpose "regni cupiditate" - Perfect passive participle "inductus" expressing completed state - Double abative construction for time and circumstance', 'This introduces the main antagonist of Book I, Orgetorix, whose conspiracy will trigger the great Helvetian migration that brings Caesar into conflict with them.', 21, 'Caesar', 'Classical Work');
