INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-008', 'Catullus Poem 51.1-5', 'Ille mi par esse deo videtur, ille, si fas est, superare divos, qui sedens adversus identidem te, spectat et audit, dulce ridentem', 'He seems to me equal to a god, he, if it is right, to surpass the gods, who sitting opposite you continually, watches and hears you sweetly laughing', '{"ille": {"meaning": "he", "grammar": "demonstrative pronoun, masculine nominative singular"}, "mi": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "par": {"meaning": "equal", "grammar": "adjective, masculine nominative singular"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "deo": {"meaning": "to god", "grammar": "noun, masculine dative singular"}, "videtur": {"meaning": "he seems", "grammar": "3rd person singular, present indicative"}, "si": {"meaning": "if", "grammar": "conjunction"}, "fas": {"meaning": "right", "grammar": "noun, neuter nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "superare": {"meaning": "to surpass", "grammar": "infinitive"}, "divos": {"meaning": "the gods", "grammar": "noun, masculine plural accusative"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine nominative singular"}, "sedens": {"meaning": "sitting", "grammar": "present participle, masculine nominative singular"}, "adversus": {"meaning": "opposite", "grammar": "preposition + accusative"}, "identidem": {"meaning": "continually", "grammar": "adverb"}, "te": {"meaning": "you", "grammar": "personal pronoun, accusative singular"}, "spectat": {"meaning": "watches", "grammar": "3rd person singular, present indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "audit": {"meaning": "hears", "grammar": "3rd person singular, present indicative"}, "dulce": {"meaning": "sweetly", "grammar": "adverb"}, "ridentem": {"meaning": "laughing", "grammar": "present participle, feminine accusative singular"}}', '- Dative of possession "mi par" - Indirect statement "ille...superare divos" - Conditional clause "si fas est" - Present participles "sedens...ridentem" - Accusative with infinitive construction', 'Catullus adapts Sappho''s famous fragment to describe the overwhelming effect of seeing his beloved Lesbia, the ecstatic yet painful nature of love.', 25, 'Catullus', 'Poems');
