INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-009', 'Vergil Aeneid 1.23-28', 'Id metuēns, veterisque memor Sāturnia bellī, prima quod ad Trōiam prō cārīs gesserat Argīs—necdum etiam causae īrārum saevīque dolōrēs, exciderant animō', 'Fearing this, and remembering the old war of Saturnia, the first which for beloved Argos she had carried on against Troy—and not yet had the causes of her anger and cruel griefs fallen from her mind', '{"id": {"meaning": "this", "grammar": "demonstrative pronoun, neuter accusative"}, "metu\u0113ns": {"meaning": "fearing", "grammar": "present participle, feminine nominative singular"}, "veterisque": {"meaning": "and old", "grammar": "adjective, neuter genitive singular"}, "memor": {"meaning": "remembering", "grammar": "adjective, feminine nominative singular"}, "saturnia": {"meaning": "Saturnia", "grammar": "noun, feminine nominative singular"}, "bell\u012b": {"meaning": "of war", "grammar": "noun, neuter genitive singular"}, "prima": {"meaning": "the first", "grammar": "adjective, feminine accusative singular"}, "quod": {"meaning": "which", "grammar": "relative pronoun, neuter accusative"}, "ad": {"meaning": "against", "grammar": "preposition + accusative"}, "tr\u014diam": {"meaning": "Troy", "grammar": "proper noun, feminine accusative singular"}, "pr\u014d": {"meaning": "for", "grammar": "preposition + ablative"}, "c\u0101r\u012bs": {"meaning": "beloved", "grammar": "adjective, neuter plural ablative"}, "gesserat": {"meaning": "she had carried", "grammar": "3rd person singular, pluperfect indicative"}, "arg\u012bs": {"meaning": "Argos", "grammar": "proper noun, neuter plural ablative"}, "necdum": {"meaning": "and not yet", "grammar": "adverb"}, "etiam": {"meaning": "also", "grammar": "adverb"}, "causae": {"meaning": "the causes", "grammar": "noun, feminine nominative plural"}, "\u012br\u0101rum": {"meaning": "of anger", "grammar": "noun, feminine genitive plural"}, "saev\u012bque": {"meaning": "and cruel", "grammar": "adjective, feminine genitive plural"}, "dol\u014dr\u0113s": {"meaning": "of griefs", "grammar": "noun, masculine genitive plural"}, "exciderant": {"meaning": "had fallen", "grammar": "3rd person plural, pluperfect indicative"}, "anim\u014d": {"meaning": "from her mind", "grammar": "noun, neuter ablative singular"}}', '- Present participle "metuēns" with accusative object - Genitive of memory "veterisque...bellī" - Pluperfect "gesserat" for completed past action - Negative construction "necdum...etiam" - Ablative of separation "animō exciderant"', 'Vergil explains Juno''s personal motivation—her hatred from the Trojan War and rejection of Paris—making her the antagonist in the epic''s divine drama.', 24, 'Vergil', 'Aeneid');
