INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-012', 'Catullus Poem 8.1-4', 'Miser Catulle, desinas ineptire, et quod vides perisse perditum ducas. Fulsere quondam candidi tibi soles, cum ventitabas quo puella ducebat', 'Wretched Catullus, stop playing the fool, and what you see is gone consider lost. Once bright suns shone for you, when you used to go wherever the girl led', '{"miser": {"meaning": "wretched", "grammar": "adjective, masculine vocative"}, "catulle": {"meaning": "Catullus", "grammar": "proper noun, masculine vocative"}, "desinas": {"meaning": "stop", "grammar": "2nd person singular, present subjunctive"}, "ineptire": {"meaning": "playing the fool", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quod": {"meaning": "what", "grammar": "relative pronoun, neuter accusative"}, "vides": {"meaning": "you see", "grammar": "2nd person singular, present indicative"}, "perisse": {"meaning": "is gone", "grammar": "infinitive perfect"}, "perditum": {"meaning": "lost", "grammar": "adjective, neuter accusative singular"}, "ducas": {"meaning": "consider", "grammar": "2nd person singular, present subjunctive"}, "fulsere": {"meaning": "shone", "grammar": "3rd person plural, perfect indicative"}, "quondam": {"meaning": "once", "grammar": "adverb"}, "candidi": {"meaning": "bright", "grammar": "adjective, masculine plural nominative"}, "tibi": {"meaning": "for you", "grammar": "personal pronoun, dative singular"}, "soles": {"meaning": "suns", "grammar": "noun, masculine plural nominative"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "ventitabas": {"meaning": "you used to go", "grammar": "2nd person singular, imperfect indicative"}, "quo": {"meaning": "wherever", "grammar": "relative adverb"}, "puella": {"meaning": "the girl", "grammar": "noun, feminine nominative singular"}, "ducebat": {"meaning": "led", "grammar": "3rd person singular, imperfect indicative"}}', '- Jussive subjunctive "desinas, ducas" - Infinitive "perisse" in indirect statement - Imperfect tense for habitual past action "ventitabas...ducebat" - Relative adverb "quo" with implied antecedent', 'The famous self-admonishment poem where Catullus resolves to stop pursuing an unfaithful lover, describing the past happiness with bitter irony.', 20, 'Catullus', 'Poems');
