INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-013', 'Vergil Aeneid 1.29-33', 'His accensa super, iactatos aequore toto, Troas, reliquias Danaum atque immitis Achillis, arcebat longe Latio, multosque per annos, errabant, acti Fatis, maria omnia circum', 'Being inflamed by these things, she kept away from Latium far the Trojans, remnants of the Danaans and the ruthless Achilles, tossed through the whole sea; for many years they wandered, driven by Fate, around all seas', '{"his": {"meaning": "by these", "grammar": "demonstrative pronoun, neuter plural ablative"}, "accensa": {"meaning": "being inflamed", "grammar": "perfect passive participle, feminine nominative singular"}, "super": {"meaning": "besides", "grammar": "adverb"}, "iactatos": {"meaning": "tossed", "grammar": "perfect passive participle, masculine plural accusative"}, "aequore": {"meaning": "through the sea", "grammar": "noun, neuter ablative singular"}, "toto": {"meaning": "through the whole", "grammar": "adjective, neuter ablative singular"}, "troas": {"meaning": "the Trojans", "grammar": "proper noun, masculine plural accusative"}, "reliquias": {"meaning": "remnants", "grammar": "noun, feminine plural accusative"}, "danam": {"meaning": "of the Danaans", "grammar": "noun, masculine plural genitive"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "immitis": {"meaning": "the ruthless", "grammar": "adjective, masculine plural genitive"}, "achillis": {"meaning": "of Achilles", "grammar": "proper noun, masculine genitive singular"}, "arcebat": {"meaning": "she kept away", "grammar": "3rd person singular, imperfect indicative"}, "longe": {"meaning": "far", "grammar": "adverb"}, "latio": {"meaning": "from Latium", "grammar": "noun, neuter ablative singular"}, "multosque": {"meaning": "and for many", "grammar": "adjective, masculine plural accusative"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "annos": {"meaning": "years", "grammar": "noun, masculine plural accusative"}, "errabant": {"meaning": "they wandered", "grammar": "3rd person plural, imperfect indicative"}, "acti": {"meaning": "driven", "grammar": "perfect passive participle, masculine plural nominative"}, "fatis": {"meaning": "by Fate", "grammar": "noun, neuter ablative plural"}, "maria": {"meaning": "seas", "grammar": "noun, neuter plural accusative"}, "omnia": {"meaning": "all", "grammar": "adjective, neuter plural accusative"}, "circum": {"meaning": "around", "grammar": "adverb"}}', '- Ablative absolute "His...super accensa" - Perfect passive participle "iactatos" with accusative object - Ablative of separation "Latii longe arcebat" - Imperfect tense for ongoing past action - Perfect passive participle "acti" expressing state', 'Vergil explains how Juno''s hatred prolonged the Trojans'' wandering, emphasizing their suffering and divine opposition to their destined founding of Rome.', 23, 'Vergil', 'Aeneid');
