INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-014', 'Ovid Metamorphoses 1.40-45', 'Iussit et extendi campos, subsidere valles, fronde tegi silvas, lapidosos surgere montes, utque duae dextra caelum totidemque sinistra, parte secant zonae, quinta est ardentior illis', 'He ordered fields to be extended, valleys to sink, forests to be covered with leaves, stony mountains to rise; and as two zones on the right cut the sky, and as many on the left, the fifth part is hotter than these', '{"iussit": {"meaning": "he ordered", "grammar": "3rd person singular, perfect indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "extendi": {"meaning": "to be extended", "grammar": "infinitive passive"}, "campos": {"meaning": "fields", "grammar": "noun, masculine plural accusative"}, "subsidere": {"meaning": "to sink", "grammar": "infinitive"}, "valles": {"meaning": "valleys", "grammar": "noun, feminine plural accusative"}, "fronde": {"meaning": "with leaves", "grammar": "noun, feminine ablative singular"}, "tegi": {"meaning": "to be covered", "grammar": "infinitive passive"}, "silvas": {"meaning": "forests", "grammar": "noun, feminine plural accusative"}, "lapidosos": {"meaning": "stony", "grammar": "adjective, masculine plural accusative"}, "surgere": {"meaning": "to rise", "grammar": "infinitive"}, "montes": {"meaning": "mountains", "grammar": "noun, masculine plural accusative"}, "utque": {"meaning": "and as", "grammar": "conjunction"}, "duae": {"meaning": "two", "grammar": "adjective, feminine plural nominative"}, "dextra": {"meaning": "on the right", "grammar": "adjective, feminine plural nominative"}, "caelum": {"meaning": "the sky", "grammar": "noun, neuter accusative singular"}, "totidemque": {"meaning": "and as many", "grammar": "demonstrative adjective, feminine plural nominative"}, "sinistra": {"meaning": "on the left", "grammar": "adjective, feminine plural nominative"}, "parte": {"meaning": "in part", "grammar": "noun, feminine ablative singular"}, "secant": {"meaning": "cut", "grammar": "3rd person plural, present indicative"}, "zonae": {"meaning": "zones", "grammar": "noun, feminine plural nominative"}, "quinta": {"meaning": "the fifth", "grammar": "adjective, feminine nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "ardentior": {"meaning": "hotter", "grammar": "adjective, feminine nominative singular"}, "illis": {"meaning": "than these", "grammar": "demonstrative pronoun, feminine plural ablative"}}', '- Accusative with infinitive passive "campos...extendi...tegi" - Infinitive active "subsidere...surgere" - Comparative adjective "ardentior" with ablative "illis" - Coordinate structure with multiple infinitive objects', 'Ovid describes the divine ordering of the world''s geography, creating the five climate zones from the primordial chaos.', 24, 'Ovid', 'Classical Work');
