INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-015', 'Caesar Gallic War 1.4.1-2', 'Ea res est Helvetiis per indicium enuntiata. Moribus suis Orgetoricem ex vinculis causam dicere coegerunt; damnatum poenam sequi oportebat, ut igni cremaretur', 'That matter was made known to the Helvetii through informers. By their customs they forced Orgetorix to plead his cause from chains; it was necessary that the condemned man suffer punishment, that he be burned with fire', '{"ea": {"meaning": "that", "grammar": "demonstrative adjective, feminine nominative singular"}, "res": {"meaning": "matter", "grammar": "noun, feminine nominative singular"}, "est": {"meaning": "was made", "grammar": "3rd person singular, present passive"}, "helvetiis": {"meaning": "to the Helvetii", "grammar": "proper noun, masculine plural dative"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "indicium": {"meaning": "informer", "grammar": "noun, masculine accusative singular"}, "enuntiata": {"meaning": "made known", "grammar": "perfect passive participle, feminine nominative singular"}, "moribus": {"meaning": "by customs", "grammar": "noun, neuter plural ablative"}, "suis": {"meaning": "their", "grammar": "possessive adjective, neuter plural ablative"}, "orgetoricem": {"meaning": "Orgetorix", "grammar": "proper noun, masculine accusative singular"}, "ex": {"meaning": "from", "grammar": "preposition + ablative"}, "vinculis": {"meaning": "chains", "grammar": "noun, neuter plural ablative"}, "causam": {"meaning": "cause", "grammar": "noun, feminine accusative singular"}, "dicere": {"meaning": "to plead", "grammar": "infinitive"}, "coegerunt": {"meaning": "they forced", "grammar": "3rd person plural, perfect indicative"}, "damnatum": {"meaning": "the condemned", "grammar": "perfect passive participle, masculine accusative singular"}, "poenam": {"meaning": "punishment", "grammar": "noun, feminine accusative singular"}, "sequi": {"meaning": "to suffer", "grammar": "infinitive"}, "oportebat": {"meaning": "it was necessary", "grammar": "impersonal verb, 3rd person singular, imperfect"}, "ut": {"meaning": "that", "grammar": "conjunction"}, "igni": {"meaning": "with fire", "grammar": "noun, masculine ablative singular"}, "cremaretur": {"meaning": "he be burned", "grammar": "3rd person singular, imperfect subjunctive passive"}}', '- Ablative of agent "indicium" - Ablative of means "moribus" - Accusative with infinitive "Orgetoricem...causam dicere" - Impersonal construction "oportebat" - Purpose clause "ut...cremaretur"', 'Caesar describes the discovery of Orgetorix''s conspiracy and the legal procedure against him, showing the seriousness of treason charges.', 22, 'Caesar', 'Classical Work');
