INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-016', 'Catullus Poem 51.6-12', 'Lesbia, aspexi, nihil est super mi, vocis in ore, lingua sed torpet, tenuis sub artus, flamma demanat, sonitu suopte, tintinant aures, gemina teguntur, lumina nocte', 'Lesbia, when I looked at you, nothing remains over me, in the voice''s place; but my tongue is paralyzed, a thin flame flows through my limbs, with its own sound my ears ring, my twin eyes are covered with night', '{"lesbia": {"meaning": "Lesbia", "grammar": "proper noun, feminine vocative"}, "aspexi": {"meaning": "when I looked", "grammar": "1st person singular, perfect indicative"}, "nihil": {"meaning": "nothing", "grammar": "adverb"}, "est": {"meaning": "remains", "grammar": "3rd person singular, present indicative of sum"}, "super": {"meaning": "over", "grammar": "preposition + ablative"}, "mi": {"meaning": "me", "grammar": "personal pronoun, dative singular"}, "vocis": {"meaning": "of the voice", "grammar": "noun, feminine genitive singular"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "ore": {"meaning": "in the place", "grammar": "noun, neuter ablative singular"}, "lingua": {"meaning": "my tongue", "grammar": "noun, feminine nominative singular"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "torpet": {"meaning": "is paralyzed", "grammar": "3rd person singular, present indicative"}, "tenuis": {"meaning": "thin", "grammar": "adjective, feminine nominative singular"}, "sub": {"meaning": "through", "grammar": "preposition + accusative"}, "artus": {"meaning": "limbs", "grammar": "noun, masculine plural accusative"}, "flamma": {"meaning": "flame", "grammar": "noun, feminine nominative singular"}, "demanat": {"meaning": "flows", "grammar": "3rd person singular, present indicative"}, "sonitu": {"meaning": "with sound", "grammar": "noun, masculine ablative singular"}, "suopte": {"meaning": "its own", "grammar": "reflexive possessive adjective, masculine ablative singular"}, "tintinant": {"meaning": "ring", "grammar": "3rd person plural, present indicative"}, "aures": {"meaning": "ears", "grammar": "noun, feminine plural nominative"}, "gemina": {"meaning": "twin", "grammar": "adjective, feminine plural nominative"}, "teguntur": {"meaning": "are covered", "grammar": "3rd person plural, present passive"}, "lumina": {"meaning": "eyes", "grammar": "noun, neuter plural nominative"}, "nocte": {"meaning": "with night", "grammar": "noun, feminine ablative singular"}}', '- Temporal clause "aspexi" introducing flashback - Dative of interest "mi" - Present passive "teguntur" expressing state - Reflexive possessive "suopte" emphasizing personal experience - Metaphorical language for emotional state', 'Catullus describes the overwhelming effect of seeing Lesbia, adapting Sappho''s fragment about love''s paralyzing power on body and mind.', 26, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-017', 'Vergil Aeneid 1.34-39', 'Vix e conspectu Siculae telluris in altum, vela dabant laeti, et spumas salis aere ruebant, cum Iuno, aeternum servans sub pectore volnus, haec secum: ''Mene incepto desistere victam', 'Hardly from the sight of the Sicilian land into the deep, they gave sails gladly, and with bronze turned back the foaming salt, when Juno, keeping an eternal wound in her breast, said these things to herself: ''Am I to be defeated, giving up my undertaking?''', '{"vix": {"meaning": "hardly", "grammar": "adverb"}, "e": {"meaning": "from", "grammar": "preposition + ablative"}, "conspectu": {"meaning": "from the sight", "grammar": "noun, masculine ablative singular"}, "siculae": {"meaning": "of Sicily", "grammar": "adjective, feminine genitive singular"}, "telluris": {"meaning": "of the land", "grammar": "noun, feminine genitive singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "altum": {"meaning": "the deep", "grammar": "noun, neuter accusative singular"}, "vela": {"meaning": "sails", "grammar": "noun, neuter plural accusative"}, "dabant": {"meaning": "they gave", "grammar": "3rd person plural, imperfect indicative"}, "laeti": {"meaning": "gladly", "grammar": "adverb"}, "et": {"meaning": "and", "grammar": "conjunction"}, "spumas": {"meaning": "foam", "grammar": "noun, feminine plural accusative"}, "salis": {"meaning": "of salt", "grammar": "noun, masculine genitive singular"}, "aere": {"meaning": "with bronze", "grammar": "noun, neuter ablative singular"}, "ruebant": {"meaning": "they turned", "grammar": "3rd person plural, imperfect indicative"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "iuno": {"meaning": "Juno", "grammar": "proper noun, feminine nominative singular"}, "aeternum": {"meaning": "eternal", "grammar": "adjective, neuter accusative singular"}, "servans": {"meaning": "keeping", "grammar": "present participle, feminine nominative singular"}, "sub": {"meaning": "in", "grammar": "preposition + ablative"}, "pectore": {"meaning": "in her breast", "grammar": "noun, neuter ablative singular"}, "volnus": {"meaning": "wound", "grammar": "noun, neuter accusative singular"}, "haec": {"meaning": "these things", "grammar": "demonstrative adjective, neuter plural accusative"}, "secum": {"meaning": "to herself", "grammar": "reflexive pronoun + preposition"}, "mene": {"meaning": "am I", "grammar": "interrogative pronoun, accusative"}, "incepto": {"meaning": "my undertaking", "grammar": "noun, neuter ablative singular"}, "desistere": {"meaning": "to give up", "grammar": "infinitive"}, "victam": {"meaning": "defeated", "grammar": "perfect passive participle, feminine accusative singular"}}', '- Temporal clause "cum...haec secum" - Present participle "servans" with accusative object - Accusative "Mene" with infinitive "desistere" - Perfect passive participle "victam" expressing completed state - Internal monologue structure', 'The Trojans'' hopeful sailing contrasts with Juno''s bitter internal monologue, showing the divine conflict driving the epic''s action.', 24, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-018', 'Ovid Metamorphoses 1.50-55', 'Nix tegit alta duas; totidem inter utramque locavit, temperiemque dedit mixta cum frigore flamma. Inminet his aer, qui, quanto est pondere terrae, pondus aquae levius, tanto est onerosior igni', 'Snow covers high two; between each of the two he placed [climates], and he gave temperate air mixed with cold flame. The air overhangs these, which, as it is lighter than the weight of earth by the weight of water, is heavier than fire by so much', '{"nix": {"meaning": "snow", "grammar": "noun, feminine nominative singular"}, "tegit": {"meaning": "covers", "grammar": "3rd person singular, present indicative"}, "alta": {"meaning": "high", "grammar": "adjective, feminine plural accusative"}, "duas": {"meaning": "two", "grammar": "adjective, feminine plural accusative"}, "totidem": {"meaning": "as many", "grammar": "adverb"}, "inter": {"meaning": "between", "grammar": "preposition + accusative"}, "utramque": {"meaning": "each of the two", "grammar": "pronoun, feminine accusative singular"}, "locavit": {"meaning": "he placed", "grammar": "3rd person singular, perfect indicative"}, "temperiemque": {"meaning": "and temperate", "grammar": "noun, feminine accusative singular"}, "dedit": {"meaning": "gave", "grammar": "3rd person singular, perfect indicative"}, "mixta": {"meaning": "mixed", "grammar": "perfect passive participle, feminine nominative singular"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "frigore": {"meaning": "cold", "grammar": "noun, neuter ablative singular"}, "flamma": {"meaning": "flame", "grammar": "noun, feminine nominative singular"}, "inminet": {"meaning": "overhangs", "grammar": "3rd person singular, present indicative"}, "his": {"meaning": "these", "grammar": "demonstrative pronoun, neuter plural ablative"}, "aer": {"meaning": "the air", "grammar": "noun, masculine nominative singular"}, "qui": {"meaning": "which", "grammar": "relative pronoun, masculine nominative singular"}, "quanto": {"meaning": "as", "grammar": "interrogative adverb"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "pondere": {"meaning": "by weight", "grammar": "noun, neuter ablative singular"}, "terrae": {"meaning": "of earth", "grammar": "noun, feminine genitive singular"}, "pondus": {"meaning": "weight", "grammar": "noun, neuter nominative singular"}, "aquae": {"meaning": "of water", "grammar": "noun, feminine genitive singular"}, "levius": {"meaning": "lighter", "grammar": "adjective, neuter nominative singular"}, "tanto": {"meaning": "by so much", "grammar": "adverb"}, "onerosior": {"meaning": "heavier", "grammar": "adjective, neuter nominative singular"}, "igni": {"meaning": "than fire", "grammar": "noun, neutre ablative singular"}}', '- Perfect passive participle "mixta" with ablative - Relative pronoun "qui" introducing explanatory clause - Comparative construction "levius...onerosior" - Ablative of degree "quanto...tanto"', 'Ovid explains the cosmic arrangement of the five climate zones, establishing the physical world structure that will later house human civilization.', 24, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-019', 'Caesar Gallic War 1.5.1-2', 'Post eius mortem nihilo minus Helvetii id quod constituerant facere conantur, ut e finibus suis exeant. Ubi iam se ad eam rem paratos esse arbitrati sunt, oppida sua omnia, numero ad duodecim, vicos ad quadringentos, reliqua privata aedificia incendunt', 'After his death, nevertheless the Helvetii attempt to do that which they had decided, that they should leave their territories. When they thought themselves prepared for that enterprise, they burn all their towns, about twelve in number, villages about four hundred, and their other private buildings', '{"post": {"meaning": "after", "grammar": "preposition + accusative"}, "eius": {"meaning": "his", "grammar": "demonstrative pronoun, neuter genitive singular"}, "mortem": {"meaning": "death", "grammar": "noun, feminine accusative singular"}, "nihilo": {"meaning": "nevertheless", "grammar": "adverb"}, "minus": {"meaning": "less", "grammar": "adverb"}, "helvetii": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural nominative"}, "id": {"meaning": "that", "grammar": "demonstrative pronoun, neuter accusative"}, "quod": {"meaning": "which", "grammar": "relative pronoun, neuter accusative"}, "constituerant": {"meaning": "they had decided", "grammar": "3rd person plural, pluperfect indicative"}, "facere": {"meaning": "to do", "grammar": "infinitive"}, "conantur": {"meaning": "they attempt", "grammar": "3rd person plural, present indicative"}, "ut": {"meaning": "that", "grammar": "conjunction"}, "e": {"meaning": "from", "grammar": "preposition + ablative"}, "finibus": {"meaning": "from territories", "grammar": "noun, neuter plural ablative"}, "suis": {"meaning": "their", "grammar": "possessive adjective, neuter plural ablative"}, "exeant": {"meaning": "they should leave", "grammar": "3rd person plural, present subjunctive"}, "ubi": {"meaning": "when", "grammar": "conjunction"}, "iam": {"meaning": "now", "grammar": "adverb"}, "se": {"meaning": "themselves", "grammar": "reflexive pronoun, accusative"}, "ad": {"meaning": "about", "grammar": "adverb"}, "eam": {"meaning": "that", "grammar": "demonstrative adjective, feminine accusative"}, "rem": {"meaning": "enterprise", "grammar": "noun, feminine accusative"}, "paratos": {"meaning": "prepared", "grammar": "adjective, masculine plural accusative"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "arbitrati": {"meaning": "they thought", "grammar": "3rd person plural, perfect indicative"}, "sunt": {"meaning": "they are", "grammar": "3rd person plural, present indicative of sum"}, "oppida": {"meaning": "towns", "grammar": "noun, neuter plural accusative"}, "sua": {"meaning": "their", "grammar": "possessive adjective, neuter plural accusative"}, "omnia": {"meaning": "all", "grammar": "adjective, neuter plural accusative"}, "numero": {"meaning": "in number", "grammar": "noun, neuter ablative singular"}, "duodecim": {"meaning": "twelve", "grammar": "adjective, neuter plural accusative"}, "vicos": {"meaning": "villages", "grammar": "noun, masculine plural accusative"}, "quadringentos": {"meaning": "four hundred", "grammar": "adjective, masculine plural accusative"}, "reliqua": {"meaning": "other", "grammar": "adjective, neuter plural accusative"}, "privata": {"meaning": "private", "grammar": "adjective, neuter plural accusative"}, "aedificia": {"meaning": "buildings", "grammar": "noun, neuter plural accusative"}, "incendunt": {"meaning": "they burn", "grammar": "3rd person plural, present indicative"}}', '- Temporal clause "Post eius mortem" - Purpose clause "ut...exeant" with subjunctive - Accusative with infinitive "se...paratos esse" - Pluperfect "constituerant" for completed past decision - Apposition with numerical adjectives', 'Despite Orgetorix''s death, the Helvetii proceed with their migration plan, demonstrating their determination and the conspiracy''s far-reaching effects.', 28, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-020', 'Catullus Poem 11.1-4', 'Furi et Aureli, comites Catulli, sive in extremos penetrabit Indos, litus ut longe resonante Eoa, tunditur unda, sive in Hyrcanos Arabesve molles', 'Furius and Aurelius, companions of Catullus, whether to the farthest Indians you go, as the eastern shore with far-resounding waves is beaten, whether to the soft Hyrcani or Arabs', '{"furii": {"meaning": "Furius", "grammar": "proper noun, masculine vocative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "aureli": {"meaning": "Aurelius", "grammar": "proper noun, masculine vocative"}, "comites": {"meaning": "companions", "grammar": "noun, masculine plural nominative"}, "catulli": {"meaning": "of Catullus", "grammar": "proper noun, masculine genitive singular"}, "sive": {"meaning": "whether", "grammar": "conjunction"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "extremos": {"meaning": "to the farthest", "grammar": "adjective, neuter plural accusative"}, "penetrabit": {"meaning": "you will go", "grammar": "2nd person singular, future indicative"}, "indos": {"meaning": "Indians", "grammar": "noun, masculine plural accusative"}, "litus": {"meaning": "as the shore", "grammar": "noun, neuter accusative singular"}, "ut": {"meaning": "as", "grammar": "conjunction"}, "longe": {"meaning": "far", "grammar": "adverb"}, "resonante": {"meaning": "with far-resounding", "grammar": "present participle, feminine ablative singular"}, "eoa": {"meaning": "eastern", "grammar": "adjective, feminine ablative singular"}, "tunditur": {"meaning": "is beaten", "grammar": "3rd person singular, present passive"}, "unda": {"meaning": "waves", "grammar": "noun, feminine nominative plural"}, "hyrcanos": {"meaning": "the Hyrcani", "grammar": "noun, masculine plural accusative"}, "arabasesve": {"meaning": "or Arabs", "grammar": "noun, masculine plural accusative"}, "molles": {"meaning": "soft", "grammar": "adjective, masculine plural accusative"}}', '- Vocative addressing companions - Future tense "penetrabit" for future action - Present passive "tunditur" expressing current state - Alternative conjunction "sive...sive" - Present participle "resonante" with instrumental meaning', 'Catullus imagines sending his friends as messengers to the farthest reaches of the world to tell Lesbia of her cruelty, emphasizing the global scope of his feelings.', 19, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-021', 'Vergil Aeneid 1.50-55', 'Talia flammato secum dea corde volutans, nimborum in patriam, loca feta furentibus austris, Aeoliam venit. Hic vasto rex Aeolus antro, luctantes ventos tempestatesque sonoras, imperio premit ac vinclis et carcere frenat', 'Such things revolving in her inflamed heart, the goddess came to the homeland of storms, places teeming with furious southern winds, Aeolia. Here King Aeolus in a vast cave presses with his power the struggling winds and noisy storms and restrains them with bonds and prison', '{"talia": {"meaning": "such things", "grammar": "demonstrative adjective, neuter plural accusative"}, "flammato": {"meaning": "inflamed", "grammar": "perfect passive participle, neutre ablative"}, "secum": {"meaning": "with herself", "grammar": "reflexive pronoun + preposition"}, "dea": {"meaning": "the goddess", "grammar": "noun, feminine nominative singular"}, "corde": {"meaning": "in her heart", "grammar": "noun, neuter ablative singular"}, "volutans": {"meaning": "revolving", "grammar": "present participle, feminine nominative singular"}, "nimborum": {"meaning": "of storms", "grammar": "noun, neuter plural genitive"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "patriam": {"meaning": "the homeland", "grammar": "noun, feminine accusative singular"}, "loca": {"meaning": "places", "grammar": "noun, neuter plural accusative"}, "feta": {"meaning": "teeming", "grammar": "perfect passive participle, feminine plural accusative"}, "furentibus": {"meaning": "with furious", "grammar": "adjective, masculine plural ablative"}, "austris": {"meaning": "southern winds", "grammar": "noun, masculine plural ablative"}, "aeoliam": {"meaning": "Aeolia", "grammar": "proper noun, feminine accusative singular"}, "venit": {"meaning": "she came", "grammar": "3rd person singular, perfect indicative"}, "hic": {"meaning": "here", "grammar": "adverb"}, "vasto": {"meaning": "in a vast", "grammar": "adjective, neutre ablative singular"}, "rex": {"meaning": "King", "grammar": "noun, masculine nominative singular"}, "aeolus": {"meaning": "Aeolus", "grammar": "proper noun, masculine nominative singular"}, "antro": {"meaning": "in a cave", "grammar": "noun, neutre ablative singular"}, "luctantes": {"meaning": "struggling", "grammar": "present participle, feminine plural accusative"}, "ventos": {"meaning": "winds", "grammar": "noun, masculine plural accusative"}, "tempestatesque": {"meaning": "and storms", "grammar": "noun, feminine plural accusative"}, "sonoras": {"meaning": "noisy", "grammar": "adjective, feminine plural accusative"}, "imperio": {"meaning": "with his power", "grammar": "noun, neutre ablative singular"}, "premit": {"meaning": "presses", "grammar": "3rd person singular, present indicative"}, "ac": {"meaning": "and", "grammar": "conjunction"}, "vinclis": {"meaning": "with bonds", "grammar": "noun, neutre plural ablative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "carcere": {"meaning": "with prison", "grammar": "noun, neutre ablative singular"}, "frenat": {"meaning": "restrains", "grammar": "3rd person singular, present indicative"}}', '- Ablative absolute "flammato corde" - Present participle "volutans" with accusative object - Perfect passive participle "feta" with instrumental ablative - Ablative of means "imperio, vinclis, carcere" - Chiasmus structure in storm description', 'Vergil introduces Aeolus, the keeper of winds, setting up the divine hierarchy where Jupiter must use storms as weapons against human heroes.', 28, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-022', 'Ovid Metamorphoses 1.70-75', 'Vix ita limitibus dissaepserat omnia certis, cum, quae pressa diu fuerant caligine caeca, sidera coeperunt toto effervescere caelo; neu regio foret ulla suis animalibus orba, astra tenent caeleste solum formaeque deorum, cesserunt nitidis habitandae piscibus undae', 'Hardly thus had he divided all things by fixed boundaries, when those which had been long pressed by blind darkness began to blaze with stars throughout the sky; so that no region would be devoid of its creatures, the stars occupy heavenly ground and forms of the gods; the clear waters were yielded for fish to inhabit', '{"vix": {"meaning": "hardly", "grammar": "adverb"}, "ita": {"meaning": "thus", "grammar": "adverb"}, "limitibus": {"meaning": "by boundaries", "grammar": "noun, neutre plural ablative"}, "dissaepserat": {"meaning": "he had divided", "grammar": "3rd person singular, pluperfect indicative"}, "omnia": {"meaning": "all things", "grammar": "adjective, neutre plural accusative"}, "certis": {"meaning": "fixed", "grammar": "adjective, neutre plural ablative"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "quae": {"meaning": "which", "grammar": "relative pronoun, neutre plural accusative"}, "pressa": {"meaning": "pressed", "grammar": "perfect passive participle, neutre plural accusative"}, "diu": {"meaning": "long", "grammar": "adverb"}, "fuerant": {"meaning": "had been", "grammar": "3rd person plural, pluperfect indicative of sum"}, "caligine": {"meaning": "by darkness", "grammar": "noun, feminine ablative singular"}, "caeca": {"meaning": "blind", "grammar": "adjective, feminine ablative singular"}, "sidera": {"meaning": "stars", "grammar": "noun, neutre plural nominative"}, "coeperunt": {"meaning": "began", "grammar": "3rd person plural, perfect indicative"}, "toto": {"meaning": "throughout", "grammar": "adjective, neutre ablative singular"}, "effervescere": {"meaning": "to blaze", "grammar": "infinitive"}, "caelo": {"meaning": "the sky", "grammar": "noun, masculine ablative singular"}, "neu": {"meaning": "so that not", "grammar": "conjunction"}, "regio": {"meaning": "region", "grammar": "noun, feminine nominative singular"}, "foret": {"meaning": "would be", "grammar": "3rd person singular, imperfect subjunctive of sum"}, "ulla": {"meaning": "any", "grammar": "adjective, feminine nominative singular"}, "suis": {"meaning": "its", "grammar": "possessive adjective, neutre plural ablative"}, "animalibus": {"meaning": "creatures", "grammar": "noun, neutre plural ablative"}, "orba": {"meaning": "devoid", "grammar": "adjective, feminine nominative singular"}, "astra": {"meaning": "the stars", "grammar": "noun, neutre plural nominative"}, "tenent": {"meaning": "occupy", "grammar": "3rd person plural, present indicative"}, "caeleste": {"meaning": "heavenly", "grammar": "adjective, neutre accusative singular"}, "solum": {"meaning": "ground", "grammar": "noun, neutre accusative singular"}, "formae": {"meaning": "forms", "grammar": "noun, feminine plural nominative"}, "que": {"meaning": "and", "grammar": "enclitic conjunction"}, "deorum": {"meaning": "of gods", "grammar": "noun, masculine plural genitive"}, "cesserunt": {"meaning": "were yielded", "grammar": "3rd person plural, perfect indicative"}, "nitidis": {"meaning": "for clear", "grammar": "adjective, neutre plural ablative"}, "habitandae": {"meaning": "to inhabit", "grammar": "gerundive, feminine plural ablative"}, "piscibus": {"meaning": "fish", "grammar": "noun, masculine plural ablative"}, "undae": {"meaning": "waters", "grammar": "noun, feminine plural nominative"}}', '- Pluperfect "dissaepserat" for completed past divine action - Temporal clause "cum...coeperunt" - Purpose clause "neu...orba" with subjunctive - Gerundive "habitandae" with dative of agent - Passive construction with genitive of agent "deorum"', 'Ovid describes the cosmic ordering where stars appear in the sky and waters become homes for fish, completing the divine organization of the physical world.', 28, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-023', 'Caesar Gallic War 1.6.1-2', 'Erant omnino itinera duo, quibus itineribus domo exire possent: unum per Sequanos, angustum et difficile, inter montem Iuram et flumen Rhodanum, vix qua singuli carri ducerentur', 'There were altogether two roads by which they could leave from home: one through the Sequani, narrow and difficult, between Mount Jura and the Rhone river, where barely single wagons could be led', '{"erant": {"meaning": "there were", "grammar": "3rd person plural, imperfect indicative of sum"}, "omnino": {"meaning": "altogether", "grammar": "adverb"}, "itinera": {"meaning": "roads", "grammar": "noun, neutre plural nominative"}, "duo": {"meaning": "two", "grammar": "adjective, neutre plural nominative"}, "quibus": {"meaning": "by which", "grammar": "relative pronoun, neutre plural ablative"}, "itineribus": {"meaning": "roads", "grammar": "noun, neutre plural ablative"}, "domo": {"meaning": "from home", "grammar": "noun, feminine ablative singular"}, "exire": {"meaning": "to leave", "grammar": "infinitive"}, "possent": {"meaning": "they could", "grammar": "3rd person plural, imperfect subjunctive"}, "unum": {"meaning": "one", "grammar": "adjective, neutre accusative singular"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "sequanos": {"meaning": "the Sequani", "grammar": "proper noun, masculine plural accusative"}, "angustum": {"meaning": "narrow", "grammar": "adjective, neutre accusative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "difficile": {"meaning": "difficult", "grammar": "adjective, neutre accusative singular"}, "inter": {"meaning": "between", "grammar": "preposition + accusative"}, "montem": {"meaning": "Mount", "grammar": "noun, masculine accusative singular"}, "iuram": {"meaning": "Jura", "grammar": "proper noun, feminine accusative singular"}, "flumen": {"meaning": "the river", "grammar": "noun, neutre accusative singular"}, "rhodanum": {"meaning": "the Rhone", "grammar": "proper noun, feminine ablative singular"}, "vix": {"meaning": "barely", "grammar": "adverb"}, "qua": {"meaning": "where", "grammar": "relative adverb"}, "singuli": {"meaning": "single", "grammar": "adjective, masculine plural nominative"}, "carri": {"meaning": "wagons", "grammar": "noun, masculine plural nominative"}, "ducerentur": {"meaning": "could be led", "grammar": "3rd person plural, imperfect subjunctive passive"}}', '- Impersonal construction "Erant...possent" - Subjunctive "possent, ducerentur" expressing potentiality - Relative adverb "qua" introducing locative clause - Passive construction in "ducerentur" - Geographic apposition with proper nouns', 'Caesar establishes the strategic geography of Gaul, showing the Helvetii''s limited options and the importance of mountain passes for migration and military control.', 23, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-024', 'Catullus Poem 16.1-3', 'Pedicabo ego vos et irrumabo, Aureli pathice et cinaede Furi, qui me ex versiculis meis putastis, quod sunt molliculi, parum pudicum', 'I will bugger you and sodomize you, effeminate Aurelius and deviant Furius, you who have thought from my little verses, because they are a bit soft, that I am unchaste', '{"pedicabo": {"meaning": "I will bugger", "grammar": "1st person singular, future indicative"}, "ego": {"meaning": "I", "grammar": "personal pronoun, nominative singular"}, "vos": {"meaning": "you", "grammar": "personal pronoun, accusative plural"}, "et": {"meaning": "and", "grammar": "conjunction"}, "irrumabo": {"meaning": "I will sodomize", "grammar": "1st person singular, future indicative"}, "aureli": {"meaning": "Aurelius", "grammar": "proper noun, masculine vocative"}, "pathice": {"meaning": "effeminate", "grammar": "adjective, masculine vocative singular"}, "cinaede": {"meaning": "deviant", "grammar": "noun, masculine vocative singular"}, "furii": {"meaning": "Furius", "grammar": "proper noun, masculine vocative"}, "qui": {"meaning": "you who", "grammar": "relative pronoun, masculine plural nominative"}, "me": {"meaning": "me", "grammar": "personal pronoun, accusative singular"}, "ex": {"meaning": "from", "grammar": "preposition + ablative"}, "versiculis": {"meaning": "from my little verses", "grammar": "noun, masculine plural ablative"}, "meis": {"meaning": "my", "grammar": "possessive adjective, masculine plural ablative"}, "putastis": {"meaning": "you have thought", "grammar": "2nd person plural, perfect indicative"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "sunt": {"meaning": "they are", "grammar": "3rd person plural, present indicative of sum"}, "molliculi": {"meaning": "are soft", "grammar": "adjective, masculine plural nominative"}, "parum": {"meaning": "a bit", "grammar": "adverb"}, "pudicum": {"meaning": "unchaste", "grammar": "adjective, masculine accusative singular"}}', '- Future indicative for threat "pedicabo, irrumabo" - Vocative addressing two people with mixed case forms - Relative clause "qui...putastis" - Causal clause "quod...parum pudicum" - Accusative with infinitive implied', 'Catullus responds to criticism about his poetry''s content, defending his masculine virtue while using the coarsest Roman invective for comedic effect.', 18, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-025', 'Vergil Aeneid 1.64-69', 'Ad quem tum Iuno supplex his vocibus usa est: ''Aeole, namque tibi divom pater atque hominum rex et mulcere dedit fluctus et tollere vento, gens inimica mihi Tyrrhenum navigat aequor, Ilium in Italiam portans victosque Penates', 'To whom then Juno, as a suppliant, used these words: ''Aeolus, for indeed the father of the gods and king of men has given you the power to calm the waves and raise them by wind; an enemy people sails the Tyrrhenian sea, carrying Troy to Italy and the conquered household gods', '{"ad": {"meaning": "to", "grammar": "preposition + accusative"}, "quem": {"meaning": "whom", "grammar": "relative pronoun, masculine accusative singular"}, "tum": {"meaning": "then", "grammar": "adverb"}, "iuno": {"meaning": "Juno", "grammar": "proper noun, feminine nominative singular"}, "supplex": {"meaning": "as a suppliant", "grammar": "adjective, feminine nominative singular"}, "his": {"meaning": "these", "grammar": "demonstrative adjective, feminine ablative plural"}, "vocibus": {"meaning": "with words", "grammar": "noun, feminine ablative plural"}, "usa": {"meaning": "used", "grammar": "perfect passive participle, feminine nominative singular"}, "est": {"meaning": "she is", "grammar": "3rd person singular, present indicative of sum"}, "aeole": {"meaning": "Aeolus", "grammar": "proper noun, masculine vocative"}, "namque": {"meaning": "for indeed", "grammar": "adverb"}, "tibi": {"meaning": "to you", "grammar": "personal pronoun, dative singular"}, "divom": {"meaning": "of gods", "grammar": "noun, masculine plural genitive"}, "pater": {"meaning": "father", "grammar": "noun, masculine nominative singular"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "hominum": {"meaning": "of men", "grammar": "noun, masculine plural genitive"}, "rex": {"meaning": "king", "grammar": "noun, masculine nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "mulcere": {"meaning": "to calm", "grammar": "infinitive"}, "dedit": {"meaning": "has given", "grammar": "3rd person singular, perfect indicative"}, "fluctus": {"meaning": "waves", "grammar": "noun, masculine plural accusative"}, "tollere": {"meaning": "to raise", "grammar": "infinitive"}, "vento": {"meaning": "by wind", "grammar": "noun, masculine ablative singular"}, "gens": {"meaning": "people", "grammar": "noun, feminine nominative singular"}, "inimica": {"meaning": "enemy", "grammar": "adjective, feminine nominative singular"}, "mihi": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "tyrrhenum": {"meaning": "Tyrrhenian", "grammar": "adjective, neutre accusative singular"}, "navigat": {"meaning": "sails", "grammar": "3rd person singular, present indicative"}, "aequor": {"meaning": "the sea", "grammar": "noun, neutre accusative singular"}, "ilium": {"meaning": "Troy", "grammar": "proper noun, neutre accusative singular"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "italiam": {"meaning": "Italy", "grammar": "proper noun, feminine accusative singular"}, "portans": {"meaning": "carrying", "grammar": "present participle, masculine nominative singular"}, "victosque": {"meaning": "and conquered", "grammar": "perfect passive participle, masculine plural accusative"}, "penates": {"meaning": "household gods", "grammar": "noun, masculine plural accusative"}}', '- Ablative absolute "his vocibus...usa est" - Perfect passive participle "usa" with instrumental ablative - Accusative with infinitive "fluctus...tollere" - Present participle "portans" with double accusative - Direct speech introduction', 'Juno''s plea to Aeolus reveals her strategy for divine intervention against Aeneas, highlighting her power and cunning in the epic''s cosmic drama.', 24, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-026', 'Ovid Metamorphoses 1.75-80', 'Sanctius his animal mentisque capacius altae deerat adhuc et quod dominari in cetera posset: natus homo est, sive hunc divino semine fecit ille opifex rerum, mundi melioris origo, sive recens tellus seductaque nuper ab alto aethere cognati retinebat semina caeli', 'Hitherto a more sacred animal and one with a capacity for a lofty mind was lacking, and what might rule over the rest: man was born, whether the creator of things made him from divine seed, the origin of a better world, or the fresh earth, lately separated from the high ether, retained the kindred seeds of heaven', '{"sanctius": {"meaning": "more sacred", "grammar": "adjective, neutre nominative singular comparative"}, "his": {"meaning": "than these", "grammar": "demonstrative adjective, neutre plural ablative"}, "animal": {"meaning": "animal", "grammar": "noun, neutre nominative singular"}, "mentisque": {"meaning": "and of mind", "grammar": "noun, feminine genitive singular"}, "capacius": {"meaning": "one with capacity", "grammar": "adjective, neutre accusative singular comparative"}, "altae": {"meaning": "for lofty", "grammar": "adjective, feminine genitive singular"}, "deerat": {"meaning": "was lacking", "grammar": "3rd person singular, pluperfect indicative"}, "adhuc": {"meaning": "hitherto", "grammar": "adverb"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quod": {"meaning": "what", "grammar": "relative pronoun, neutre accusative"}, "dominari": {"meaning": "to rule", "grammar": "infinitive"}, "in": {"meaning": "over", "grammar": "preposition + accusative"}, "cetera": {"meaning": "the rest", "grammar": "adjective, neutre plural accusative"}, "posset": {"meaning": "might", "grammar": "3rd person singular, imperfect subjunctive"}, "natus": {"meaning": "was born", "grammar": "perfect passive participle, masculine nominative singular"}, "homo": {"meaning": "man", "grammar": "noun, masculine nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "sive": {"meaning": "whether", "grammar": "conjunction"}, "hunc": {"meaning": "this", "grammar": "demonstrative pronoun, masculine accusative"}, "divino": {"meaning": "from divine", "grammar": "adjective, neutre ablative singular"}, "semine": {"meaning": "seed", "grammar": "noun, neutre ablative singular"}, "fecit": {"meaning": "made", "grammar": "3rd person singular, perfect indicative"}, "ille": {"meaning": "that", "grammar": "demonstrative pronoun, masculine nominative singular"}, "opifex": {"meaning": "creator", "grammar": "noun, masculine nominative singular"}, "rerum": {"meaning": "of things", "grammar": "noun, feminine genitive plural"}, "mundi": {"meaning": "of the world", "grammar": "noun, neutre genitive singular"}, "melioris": {"meaning": "better", "grammar": "adjective, neutre genitive singular"}, "origo": {"meaning": "origin", "grammar": "noun, feminine nominative singular"}, "recens": {"meaning": "fresh", "grammar": "adjective, feminine nominative singular"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "seductaque": {"meaning": "and separated", "grammar": "perfect passive participle, feminine nominative singular"}, "nuper": {"meaning": "lately", "grammar": "adverb"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "alto": {"meaning": "from the high", "grammar": "adjective, neutre ablative singular"}, "aethere": {"meaning": "ether", "grammar": "noun, neutre ablative singular"}, "cognati": {"meaning": "kindred", "grammar": "adjective, neutre plural accusative"}, "retinebat": {"meaning": "retained", "grammar": "3rd person singular, imperfect indicative"}, "semina": {"meaning": "seeds", "grammar": "noun, neutre plural accusative"}, "caeli": {"meaning": "of heaven", "grammar": "noun, neutre genitive singular"}}', '- Comparative adjective "sanctius, capacius" with ablative of comparison - Infinitive "dominari" with genitive object "cetera" - Subjunctive "posset" expressing possibility - Alternative construction "sive...sive" - Perfect passive participle "natus" with genitive of agent', 'Ovid describes humanity''s creation, considering both divine and naturalistic origins, marking the transition from cosmic to human history.', 30, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-027', 'Caesar Gallic War 1.7.1-2', 'Caesari cum id nuntiatum esset, eos per provinciam nostram iter facere conari, maturat ab urbe proficisci et quam maximis potest itineribus in Galliam ulteriorem contendit', 'When this had been reported to Caesar, that they were attempting to march through our province, he hastened to depart from the city and hurried into further Gaul by the greatest possible marches', '{"caesari": {"meaning": "to Caesar", "grammar": "proper noun, masculine dative singular"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "id": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative"}, "nuntiatum": {"meaning": "reported", "grammar": "perfect passive participle, neutre accusative"}, "esset": {"meaning": "had been", "grammar": "3rd person singular, imperfect subjunctive"}, "eos": {"meaning": "they", "grammar": "demonstrative pronoun, masculine accusative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "provinciam": {"meaning": "the province", "grammar": "noun, feminine accusative singular"}, "nostram": {"meaning": "our", "grammar": "possessive adjective, feminine accusative singular"}, "iter": {"meaning": "to march", "grammar": "noun, neutre accusative singular"}, "facere": {"meaning": "to make", "grammar": "infinitive"}, "conari": {"meaning": "to be attempting", "grammar": "infinitive"}, "maturat": {"meaning": "he hastened", "grammar": "3rd person singular, present indicative"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "urbe": {"meaning": "from the city", "grammar": "noun, feminine ablative singular"}, "proficisci": {"meaning": "to depart", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quam": {"meaning": "as", "grammar": "adverb"}, "maximis": {"meaning": "greatest", "grammar": "adjective, neutre plural ablative"}, "potest": {"meaning": "he can", "grammar": "3rd person singular, present indicative"}, "itineribus": {"meaning": "by marches", "grammar": "noun, neutre plural ablative"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "galliam": {"meaning": "Gaul", "grammar": "proper noun, feminine accusative singular"}, "ulteriorem": {"meaning": "further", "grammar": "adjective, feminine accusative singular"}, "contendit": {"meaning": "he hurried", "grammar": "3rd person singular, present indicative"}}', '- Temporal clause "Caesari cum id nuntiatum esset" - Accusative with infinitive "eos...iter facere conari" - Ablative of means "itineribus" - Superlative "maximis" expressing degree - Parallel infinitive constructions', 'Caesar''s immediate response to the Helvetii''s threat to Roman territory demonstrates his military decisiveness and concern for Roman security.', 20, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-028', 'Catullus Poem 31.1-4', 'Paene insularum, Sirmio, insularumque ocelle, quascumque in liquentibus stagnis marique vasto fert uterque Neptunus, quam te libenter quamque laetus inviso', 'Almost of islands, Sirmio, and of islands'' little eye, which so many as in liquid lakes and the vast sea the two Neptunes carry, how gladly and joyfully I see you', '{"paene": {"meaning": "almost", "grammar": "adverb"}, "insularum": {"meaning": "of islands", "grammar": "noun, feminine plural genitive"}, "sirmio": {"meaning": "Sirmio", "grammar": "proper noun, feminine vocative"}, "insularumque": {"meaning": "and of islands", "grammar": "noun, feminine plural genitive"}, "ocelle": {"meaning": "little eye", "grammar": "noun, neutre vocative singular"}, "quascumque": {"meaning": "which so many", "grammar": "relative pronoun, feminine plural accusative"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "liquentibus": {"meaning": "in liquid", "grammar": "adjective, neutre plural ablative"}, "stagnis": {"meaning": "lakes", "grammar": "noun, neutre plural ablative"}, "marique": {"meaning": "and in the sea", "grammar": "noun, neutre ablative singular"}, "vasto": {"meaning": "in vast", "grammar": "adjective, neutre ablative singular"}, "fert": {"meaning": "carries", "grammar": "3rd person singular, present indicative"}, "uterque": {"meaning": "the two", "grammar": "adjective, masculine nominative singular"}, "neptunus": {"meaning": "Neptunes", "grammar": "noun, masculine plural nominative"}, "quam": {"meaning": "how", "grammar": "interrogative adverb"}, "te": {"meaning": "you", "grammar": "personal pronoun, accusative singular"}, "libenter": {"meaning": "gladly", "grammar": "adverb"}, "quamque": {"meaning": "and how", "grammar": "interrogative adverb"}, "laetus": {"meaning": "joyfully", "grammar": "adverb"}, "inviso": {"meaning": "I see", "grammar": "1st person singular, present indicative"}}', '- Vocative "Sirmio" in apostrophe - Genitive of quality "insularum ocelle" - Relative clause "quascumque...fert" - Interrogative adverbs "quam...quamque" - Geographic metaphor "ocelle" for Sirmio''s position', 'Catullus celebrates his return to his beloved villa on Lake Garda, expressing the joy of coming home after arduous journeys and separations.', 19, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-029', 'Vergil Aeneid 1.76-80', 'Aeolus haec contra: ''Tuus, O regina, quid optes, explorare labor; mihi iussa capessere fas est. Tu mihi, quodcumque hoc regni, tu sceptra Iovemque, concilias, tu das epulis accumbere divom', 'Aeolus answered thus: ''Yours, O queen, is the task of exploring what you desire; for me it is right to undertake commands. You give me whatever of this kingdom, you conciliate the scepter and Jupiter, you grant me to dine with the gods', '{"aeolus": {"meaning": "Aeolus", "grammar": "proper noun, masculine nominative singular"}, "haec": {"meaning": "thus", "grammar": "demonstrative adjective, neutre accusative plural"}, "contra": {"meaning": "answered", "grammar": "adverb"}, "tuus": {"meaning": "yours", "grammar": "possessive adjective, neutre accusative singular"}, "o": {"meaning": "O", "grammar": "interjection"}, "regina": {"meaning": "queen", "grammar": "noun, feminine vocative"}, "quid": {"meaning": "what", "grammar": "interrogative pronoun, neutre accusative singular"}, "optes": {"meaning": "you desire", "grammar": "2nd person singular, present subjunctive"}, "explorare": {"meaning": "to explore", "grammar": "infinitive"}, "labor": {"meaning": "task", "grammar": "noun, masculine nominative singular"}, "mihi": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "iussa": {"meaning": "commands", "grammar": "noun, neutre plural accusative"}, "capessere": {"meaning": "to undertake", "grammar": "infinitive"}, "fas": {"meaning": "right", "grammar": "noun, neutre nominative singular"}, "est": {"meaning": "it is", "grammar": "3rd person singular, present indicative of sum"}, "tu": {"meaning": "you", "grammar": "personal pronoun, nominative singular"}, "quodcumque": {"meaning": "whatever", "grammar": "indefinite pronoun, neutre accusative singular"}, "hoc": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative singular"}, "regni": {"meaning": "of kingdom", "grammar": "noun, neutre genitive singular"}, "sceptra": {"meaning": "the scepter", "grammar": "noun, neutre accusative singular"}, "iovemque": {"meaning": "and Jupiter", "grammar": "proper noun, masculine accusative singular"}, "concilias": {"meaning": "you conciliate", "grammar": "2nd person singular, present indicative"}, "das": {"meaning": "you grant", "grammar": "2nd person singular, present indicative"}, "epulis": {"meaning": "to dine", "grammar": "noun, neutre plural dative"}, "accumbere": {"meaning": "to sit", "grammar": "infinitive"}, "divom": {"meaning": "with gods", "grammar": "noun, masculine plural dative"}}', '- Indirect statement with infinitive "explorare" - Subjunctive "optes" in purpose clause - Accusative with infinitive "sceptra...concilias" - Dative of purpose "epulis accumbere" - Imperative tone implied in address', 'Aeolus acknowledges Juno''s power and his subordinate role, demonstrating the divine hierarchy where Jupiter ultimately commands all other gods.', 20, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-030', 'Ovid Metamorphoses 1.80-85', 'Quam satus Iapeto, mixtam pluvialibus undis, finxit in effigiem moderantum cuncta deorum, pronaque cum spectent animalia cetera terram, nos homini sublime dedit caelumque videre iussit et erectos ad sidera tollere vultus', 'Whom Iapetus, having mixed with rainy waters, shaped in the likeness of those who rule all gods; and as other animals look down toward the earth, he gave man lofty sight and ordered him to look at the sky and raise his face erect to the stars', '{"quam": {"meaning": "whom", "grammar": "relative pronoun, masculine accusative singular"}, "satus": {"meaning": "having been born", "grammar": "perfect passive participle, masculine nominative singular"}, "iapeto": {"meaning": "Iapetus", "grammar": "proper noun, masculine ablative singular"}, "mixtam": {"meaning": "having mixed", "grammar": "perfect passive participle, feminine accusative singular"}, "pluvialibus": {"meaning": "with rainy", "grammar": "adjective, neutre plural ablative"}, "undis": {"meaning": "with waters", "grammar": "noun, neutre plural ablative"}, "finxit": {"meaning": "shaped", "grammar": "3rd person singular, perfect indicative"}, "in": {"meaning": "in", "grammar": "preposition + accusative"}, "effigiem": {"meaning": "in the likeness", "grammar": "noun, feminine accusative singular"}, "moderantum": {"meaning": "of those who rule", "grammar": "present participle, masculine plural genitive"}, "cuncta": {"meaning": "all", "grammar": "adjective, neutre plural accusative"}, "deorum": {"meaning": "of gods", "grammar": "noun, masculine plural genitive"}, "pronaque": {"meaning": "and as they look down", "grammar": "present participle, feminine nominative singular"}, "cum": {"meaning": "as", "grammar": "conjunction"}, "spectent": {"meaning": "look down", "grammar": "3rd person plural, present subjunctive"}, "animalia": {"meaning": "animals", "grammar": "noun, neutre plural nominative"}, "cetera": {"meaning": "other", "grammar": "adjective, neutre plural nominative"}, "terram": {"meaning": "toward the earth", "grammar": "noun, feminine accusative singular"}, "nos": {"meaning": "he gave us", "grammar": "1st person plural accusative"}, "homini": {"meaning": "to man", "grammar": "noun, masculine dative singular"}, "sublime": {"meaning": "lofty", "grammar": "adjective, neutre accusative singular"}, "dedit": {"meaning": "gave", "grammar": "3rd person singular, perfect indicative"}, "caelumque": {"meaning": "and the sky", "grammar": "noun, neutre accusative singular"}, "videre": {"meaning": "to see", "grammar": "infinitive"}, "iussit": {"meaning": "ordered", "grammar": "3rd person singular, perfect indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "erectos": {"meaning": "erect", "grammar": "adjective, masculine plural accusative"}, "ad": {"meaning": "toward", "grammar": "preposition + accusative"}, "sidera": {"meaning": "the stars", "grammar": "noun, neutre plural accusative"}, "tollere": {"meaning": "to raise", "grammar": "infinitive"}, "vultus": {"meaning": "his face", "grammar": "noun, masculine plural accusative"}}', '- Perfect passive participle "satus" with ablative of agent - Perfect passive participle "mixtam" with instrumental ablative - Present participle "moderantum" with genitive of description - Concessive clause "cum...spectent" with subjunctive - Accusative with infinitive "nos...caelumque videre...tollere"', 'Ovid describes humanity''s creation with divine wisdom, distinguishing humans from animals by giving them the capacity for abstract thought and cosmic awareness.', 25, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-031', 'Caesar Gallic War 1.8.1-2', 'Interea ea legione quam secum habebat militibusque, qui ex provincia convenerant, a lacu Lemanno, qui in flumen Rhodanum influit, ad montem Iuram, qui fines Sequanorum ab Helvetiis dividit, milia passuum XVIIII murum in altitudinem pedum sedecim fossamque perducit', 'Meanwhile with that legion which he had with him and the soldiers who had come together from the province, from Lake Leman, which flows into the Rhone river, to Mount Jura, which separates the territory of the Sequani from the Helvetii, he extends a wall nineteen miles in length to a height of sixteen feet and a ditch', '{"interea": {"meaning": "meanwhile", "grammar": "adverb"}, "ea": {"meaning": "that", "grammar": "demonstrative adjective, feminine ablative singular"}, "legione": {"meaning": "with the legion", "grammar": "noun, feminine ablative singular"}, "quam": {"meaning": "which", "grammar": "relative pronoun, feminine ablative singular"}, "secum": {"meaning": "with him", "grammar": "reflexive pronoun + preposition"}, "habebat": {"meaning": "he had", "grammar": "3rd person singular, imperfect indicative"}, "militibusque": {"meaning": "and with the soldiers", "grammar": "noun, masculine plural ablative"}, "qui": {"meaning": "which", "grammar": "relative pronoun, masculine nominative singular"}, "ex": {"meaning": "from", "grammar": "preposition + ablative"}, "provincia": {"meaning": "the province", "grammar": "noun, feminine ablative singular"}, "convenerant": {"meaning": "had come together", "grammar": "3rd person plural, pluperfect indicative"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "lacu": {"meaning": "from Lake", "grammar": "noun, masculine ablative singular"}, "lemanno": {"meaning": "Leman", "grammar": "proper noun, masculine ablative singular"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "flumen": {"meaning": "the river", "grammar": "noun, neutre accusative singular"}, "rhodanum": {"meaning": "the Rhone", "grammar": "proper noun, feminine ablative singular"}, "influit": {"meaning": "flows", "grammar": "3rd person singular, present indicative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "montem": {"meaning": "Mount", "grammar": "noun, masculine accusative singular"}, "iuram": {"meaning": "Jura", "grammar": "proper noun, feminine accusative singular"}, "fines": {"meaning": "the territory", "grammar": "noun, masculine plural accusative"}, "sequanorum": {"meaning": "of the Sequani", "grammar": "proper noun, masculine plural genitive"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "helvetiis": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural ablative"}, "dividit": {"meaning": "separates", "grammar": "3rd person singular, present indicative"}, "milia": {"meaning": "miles", "grammar": "noun, neutre plural accusative"}, "passuum": {"meaning": "of paces", "grammar": "noun, masculine plural genitive"}, "xviiii": {"meaning": "nineteen", "grammar": "adjective, neutre plural accusative"}, "murum": {"meaning": "a wall", "grammar": "noun, masculine accusative singular"}, "altitudinem": {"meaning": "in height", "grammar": "noun, feminine accusative singular"}, "pedum": {"meaning": "of feet", "grammar": "noun, masculine plural genitive"}, "sedecim": {"meaning": "sixteen", "grammar": "adjective, neutre plural accusative"}, "fossamque": {"meaning": "and a ditch", "grammar": "noun, feminine accusative singular"}, "perducit": {"meaning": "he extends", "grammar": "3rd person singular, present indicative"}}', '- Ablative absolute "ea legione...habebat" - Relative clause "qui...convenerant" - Ablative of separation "a lacu...dividit" - Accusative of extent "milia passuum" - Perfect passive participle "convenerant" with sense of gathering', 'Caesar describes his strategic fortification against the Helvetii, using the natural barrier of the Rhone-Jura line to protect Roman territory and control the migration route.', 33, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-032', 'Catullus Poem 85', 'Odi et amo. Quare id faciam, fortasse requiris. Nescio, sed fieri sentio et excrucior', 'I hate and love. Why I do this, perhaps you ask. I do not know, but I feel it happening and am tormented', '{"odi": {"meaning": "I hate", "grammar": "1st person singular, perfect indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "amo": {"meaning": "I love", "grammar": "1st person singular, present indicative"}, "quare": {"meaning": "why", "grammar": "interrogative adverb"}, "id": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative singular"}, "faciam": {"meaning": "I do", "grammar": "1st person singular, present subjunctive"}, "fortasse": {"meaning": "perhaps", "grammar": "adverb"}, "requiris": {"meaning": "you ask", "grammar": "2nd person singular, present indicative"}, "nescio": {"meaning": "I do not know", "grammar": "1st person singular, present indicative"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "fieri": {"meaning": "to happen", "grammar": "infinitive"}, "sentio": {"meaning": "I feel", "grammar": "1st person singular, present indicative"}, "excrucior": {"meaning": "I am tormented", "grammar": "1st person singular, present passive"}}', '- Present subjunctive "faciam" expressing habitual action - Perfect indicative "odi" for past completed action - Present indicative "amo" for ongoing love - Accusative with infinitive "id...fieri" - Present passive "excrucior" expressing current state', 'This is perhaps the most famous line in Latin poetry, expressing the paradoxical nature of passionate love - the simultaneous hatred and love that torn loving hearts apart.', 10, 'Classical Author', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-033', 'Vergil Aeneid 1.81-84', 'Haec ubi dicta, cavum conversa cuspide montem, impulit in latus: ac venti, velut agmine facto, qua data porta, ruunt et terras turbine perflant', 'When these words were spoken, turning the hollow mountain with the spear''s point, he struck it on the side: and the winds, as if in formation, where the opening was given, rush and blow across the lands with a whirlwind', '{"haec": {"meaning": "these words", "grammar": "demonstrative adjective, neutre plural accusative"}, "ubi": {"meaning": "when", "grammar": "conjunction"}, "dicta": {"meaning": "were spoken", "grammar": "perfect passive participle, neutre plural accusative"}, "cavum": {"meaning": "the hollow", "grammar": "adjective, neutre accusative singular"}, "conversa": {"meaning": "turning", "grammar": "perfect passive participle, feminine ablative singular"}, "cuspide": {"meaning": "with the spear''s point", "grammar": "noun, feminine ablative singular"}, "montem": {"meaning": "the mountain", "grammar": "noun, masculine accusative singular"}, "impulit": {"meaning": "he struck", "grammar": "3rd person singular, perfect indicative"}, "in": {"meaning": "on", "grammar": "preposition + accusative"}, "latus": {"meaning": "the side", "grammar": "noun, neutre accusative singular"}, "ac": {"meaning": "and", "grammar": "conjunction"}, "venti": {"meaning": "the winds", "grammar": "noun, masculine plural nominative"}, "velut": {"meaning": "as if", "grammar": "conjunction"}, "agmine": {"meaning": "in formation", "grammar": "noun, neutre ablative singular"}, "facto": {"meaning": "formed", "grammar": "perfect passive participle, neutre ablative singular"}, "qua": {"meaning": "where", "grammar": "relative adverb"}, "data": {"meaning": "was given", "grammar": "perfect passive participle, feminine ablative singular"}, "porta": {"meaning": "the opening", "grammar": "noun, feminine ablative singular"}, "ruunt": {"meaning": "they rush", "grammar": "3rd person plural, present indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "terras": {"meaning": "across the lands", "grammar": "noun, feminine plural accusative"}, "turbine": {"meaning": "with a whirlwind", "grammar": "noun, masculine ablative singular"}, "perflant": {"meaning": "they blow across", "grammar": "3rd person plural, present indicative"}}', '- Temporal clause "Haec ubi dicta" - Perfect passive participle "dicta, conversa, facto, data" - Ablative of means "cuspide, turbine" - Concessive clause "velut agmine facto" - Present tense "ruunt, perflant" for immediate action', 'The moment Aeolus releases the winds, triggering the storm that will drive the Trojans toward Carthage, demonstrating divine control over natural forces.', 20, 'Classical Author', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-034', 'Ovid Metamorphoses 1.85-90', 'Sic, modo quae fuerat rudis et sine imagine, tellus, induit ignotas hominum conversa figuras. Aurea prima sata est aetas, quae vindice nullo, sponte sua, sine lege fidem rectumque colebat', 'So, earth, which had been crude and without form, after being changed puts on unknown shapes of men. The first golden age was sown, which without an avenger, of its own accord, without law, cultivated faith and right', '{"sic": {"meaning": "so", "grammar": "adverb"}, "modo": {"meaning": "after", "grammar": "conjunction"}, "quae": {"meaning": "which", "grammar": "relative pronoun, feminine nominative singular"}, "fuerat": {"meaning": "had been", "grammar": "3rd person singular, pluperfect indicative of sum"}, "rudis": {"meaning": "crude", "grammar": "adjective, feminine nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "imagine": {"meaning": "form", "grammar": "noun, feminine ablative singular"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "induit": {"meaning": "puts on", "grammar": "3rd person singular, present indicative"}, "ignotas": {"meaning": "unknown", "grammar": "adjective, feminine plural accusative"}, "hominum": {"meaning": "of men", "grammar": "noun, masculine plural genitive"}, "conversa": {"meaning": "after being changed", "grammar": "perfect passive participle, feminine nominative singular"}, "figuras": {"meaning": "shapes", "grammar": "noun, feminine plural accusative"}, "aurea": {"meaning": "golden", "grammar": "adjective, feminine nominative singular"}, "prima": {"meaning": "first", "grammar": "adjective, feminine nominative singular"}, "sata": {"meaning": "was sown", "grammar": "perfect passive participle, feminine nominative singular"}, "est": {"meaning": "was", "grammar": "3rd person singular, present passive"}, "aetas": {"meaning": "age", "grammar": "noun, feminine nominative singular"}, "vindice": {"meaning": "without an avenger", "grammar": "noun, masculine ablative singular"}, "nullo": {"meaning": "without", "grammar": "adjective, neutre ablative singular"}, "sponte": {"meaning": "of its own accord", "grammar": "noun, feminine ablative singular"}, "sua": {"meaning": "its own", "grammar": "possessive adjective, feminine ablative singular"}, "lege": {"meaning": "law", "grammar": "noun, feminine ablative singular"}, "fidem": {"meaning": "faith", "grammar": "noun, feminine accusative singular"}, "rectumque": {"meaning": "and right", "grammar": "noun, neutre accusative singular"}, "colebat": {"meaning": "cultivated", "grammar": "3rd person singular, imperfect indicative"}}', '- Temporal clause "modo...fuerat" - Perfect passive participle "conversa" with accusative object - Relative clause "quae...colebat" with subjunctive sense - Ablative of manner "sponte sua" - Imperfect tense "colebat" for ongoing past custom', 'Ovid describes the transformation of Earth''s surface and the beginning of human civilization with the Golden Age of innocence and natural virtue.', 23, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-035', 'Caesar Gallic War 1.9.1-2', 'Relinquebatur una per Sequanos via, qua Sequanis invitis propter angustias ire non poterant. His cum sua sponte persuadere non possent, legatos ad Dumnorigem Haeduum mittunt, ut eo deprecatore a Sequanis impetrarent', 'One road remained through the Sequani, by which they could not go because of the narrowness when the Sequani were unwilling. Since they could not persuade them of their own accord, they send ambassadors to Dumnorix of the Haedui, so that through him as an intercessor they might obtain from the Sequani', '{"relinquebatur": {"meaning": "remained", "grammar": "3rd person singular, imperfect passive"}, "una": {"meaning": "one", "grammar": "adjective, feminine nominative singular"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "sequanos": {"meaning": "through the Sequani", "grammar": "proper noun, masculine plural accusative"}, "via": {"meaning": "road", "grammar": "noun, feminine nominative singular"}, "qua": {"meaning": "by which", "grammar": "relative adverb"}, "sequanis": {"meaning": "from the Sequani", "grammar": "proper noun, masculine plural ablative"}, "invitis": {"meaning": "when unwilling", "grammar": "perfect passive participle, masculine plural ablative"}, "propter": {"meaning": "because of", "grammar": "preposition + accusative"}, "angustias": {"meaning": "narrowness", "grammar": "noun, feminine accusative singular"}, "ire": {"meaning": "to go", "grammar": "infinitive"}, "non": {"meaning": "not", "grammar": "adverb"}, "poterant": {"meaning": "they could", "grammar": "3rd person plural, imperfect indicative"}, "his": {"meaning": "them", "grammar": "demonstrative pronoun, masculine plural dative"}, "cum": {"meaning": "since", "grammar": "conjunction"}, "sua": {"meaning": "their own", "grammar": "possessive adjective, neutre plural ablative"}, "sponte": {"meaning": "of their own accord", "grammar": "noun, feminine ablative singular"}, "persuadere": {"meaning": "to persuade", "grammar": "infinitive"}, "possent": {"meaning": "they could", "grammar": "3rd person plural, imperfect subjunctive"}, "legatos": {"meaning": "ambassadors", "grammar": "noun, masculine plural accusative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "dumnorigem": {"meaning": "Dumnorix", "grammar": "proper noun, masculine accusative singular"}, "haeduum": {"meaning": "of the Haedui", "grammar": "proper noun, masculine plural genitive"}, "mittunt": {"meaning": "they send", "grammar": "3rd person plural, present indicative"}, "ut": {"meaning": "so that", "grammar": "conjunction"}, "eo": {"meaning": "through him", "grammar": "demonstrative pronoun, masculine ablative singular"}, "deprecatore": {"meaning": "as an intercessor", "grammar": "noun, masculine ablative singular"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "impetrarent": {"meaning": "they might obtain", "grammar": "3rd person plural, imperfect subjunctive"}}', '- Passive construction "Relinquebatur" - Temporal clause "qua...poterant" - Concessive clause "His cum...possent" - Purpose clause "ut...impetrarent" - Subjunctive mood in both negative possibilities', 'Caesar shows the Helvetii''s diplomatic challenges and their need for allies, revealing the complex political alliances among Gallic tribes.', 26, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-001', 'Vergil Aeneid 1.1-11', 'Arma virumque cano, Troiae qui primus ab oris taliam, ft profugus, Lvniaque vnit ltora, multum ille et terrs iacttus et alt v superum saevae memorem Innis ob ram; multa quoque et bell passus, dum conderet urbem, inferretque des Lati, genus unde Latnum, Albnque patrs, atque altae moenia Rmae. Msa, mih causs memor, qu nmine laes, quidve dolns, rgna deum tot volvere css nsgnem piette virum, tot adre labrs impulerit. Tantaene anims caelestibus rae?', 'I sing of arms and the man, who first from the Trojan shores, exile by fate, came to Italy and Lavinian shores, much tossed by land and sea and by the will of the gods because of Junos mindful wrath; having endured many things in war too, while he founded a city and brought gods to Latium, whence the Latin race and Alban fathers and the walls of great Rome. Muse, remember the causes to me, with what divinity offended, or grieving, queen of the gods, why she impelled the man renowned for piety to endure so many labors. Are such anger in celestial minds?', '{"arma": {"meaning": "arms", "grammar": "noun, neuter plural accusative"}, "virumque": {"meaning": "and the man", "grammar": "noun, masculine accusative"}, "cano": {"meaning": "I sing", "grammar": "1st person singular, present indicative"}, "troiae": {"meaning": "from Troy", "grammar": "noun, feminine genitive singular"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine nominative singular"}, "primus": {"meaning": "first", "grammar": "adjective, masculine nominative singular"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "oris": {"meaning": "shores", "grammar": "noun, feminine plural ablative"}, "\u012btaliam": {"meaning": "to Italy", "grammar": "noun, feminine accusative singular"}, "f\u0101t\u014d": {"meaning": "by fate", "grammar": "noun, neuter ablative singular"}, "profugus": {"meaning": "exile", "grammar": "adjective, masculine nominative singular"}, "l\u0101v\u012bniaque": {"meaning": "and Lavinian", "grammar": "adjective, feminine accusative singular"}, "v\u0113nit": {"meaning": "came", "grammar": "3rd person singular, perfect indicative"}, "l\u012btora": {"meaning": "to shores", "grammar": "noun, feminine plural accusative"}, "multum": {"meaning": "much", "grammar": "adverb"}, "ille": {"meaning": "he", "grammar": "demonstrative pronoun, masculine nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "terr\u012bs": {"meaning": "by land", "grammar": "noun, feminine plural ablative"}, "iact\u0101tus": {"meaning": "tossed", "grammar": "perfect passive participle, masculine nominative singular"}, "alt\u014d": {"meaning": "by sea", "grammar": "noun, neutre ablative singular"}, "v\u012b": {"meaning": "by the will", "grammar": "noun, neuter ablative singular"}, "superum": {"meaning": "of the gods", "grammar": "noun, masculine plural genitive"}, "saevae": {"meaning": "of savage", "grammar": "adjective, feminine genitive singular"}, "memorem": {"meaning": "mindful", "grammar": "adjective, feminine accusative singular"}, "i\u016bn\u014dnis": {"meaning": "of Juno", "grammar": "noun, feminine genitive singular"}, "ob": {"meaning": "because of", "grammar": "preposition + accusative"}, "\u012bram;": {"meaning": "wrath", "grammar": "noun, feminine accusative singular"}, "multa": {"meaning": "many", "grammar": "adjective, neuter plural accusative"}, "quoque": {"meaning": "too", "grammar": "adverb"}, "bell\u014d": {"meaning": "in war", "grammar": "noun, neuter ablative singular"}, "passus": {"meaning": "having endured", "grammar": "perfect passive participle, masculine nominative singular"}, "dum": {"meaning": "while", "grammar": "conjunction"}, "conderet": {"meaning": "he founded", "grammar": "3rd person singular, imperfect subjunctive"}, "urbem,": {"meaning": "a city", "grammar": "noun, feminine accusative singular"}, "inferretque": {"meaning": "and he brought", "grammar": "3rd person singular, imperfect subjunctive"}, "de\u014ds": {"meaning": "gods", "grammar": "noun, masculine plural accusative"}, "lati\u014d": {"meaning": "to Latium", "grammar": "noun, neutre ablative singular"}, "genus": {"meaning": "whence", "grammar": "relative adverb"}, "lat\u012bnum,": {"meaning": "Latin", "grammar": "noun, neuter accusative singular"}, "alb\u0101n\u012bque": {"meaning": "and Alban", "grammar": "adjective, masculine plural nominative"}, "patr\u0113s,": {"meaning": "fathers", "grammar": "noun, masculine plural nominative"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "altae": {"meaning": "of great", "grammar": "adjective, feminine genitive singular"}, "moenia": {"meaning": "walls", "grammar": "noun, neuter plural accusative"}, "r\u014dmae.": {"meaning": "of Rome", "grammar": "noun, feminine genitive singular"}, "m\u016bsa,": {"meaning": "Muse", "grammar": "noun, feminine vocative"}, "mih\u012b": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "caus\u0101s": {"meaning": "causes", "grammar": "noun, feminine plural accusative"}, "memor\u0101,": {"meaning": "remember", "grammar": "2nd person singular, present imperative"}, "qu\u014d": {"meaning": "with what", "grammar": "interrogative adjective, neutre ablative singular"}, "n\u016bmine": {"meaning": "divinity", "grammar": "noun, neutre ablative singular"}, "laes\u014d,": {"meaning": "having offended", "grammar": "perfect passive participle, neutre ablative singular"}, "quidve": {"meaning": "or what", "grammar": "interrogative pronoun, neutre accusative"}, "dol\u0113ns,": {"meaning": "grieving", "grammar": "present participle, feminine nominative singular"}, "r\u0113g\u012bna": {"meaning": "queen", "grammar": "noun, feminine nominative singular"}, "deum": {"meaning": "of the gods", "grammar": "noun, masculine plural genitive"}, "tot": {"meaning": "so many", "grammar": "adjective, masculine plural accusative"}, "volvere": {"meaning": "to endure", "grammar": "infinitive"}, "c\u0101s\u016bs": {"meaning": "fates", "grammar": "noun, masculine plural accusative"}, "\u012bns\u012bgnem": {"meaning": "renowned", "grammar": "adjective, masculine accusative singular"}, "piet\u0101te": {"meaning": "for piety", "grammar": "noun, feminine ablative singular"}, "virum,": {"meaning": "man", "grammar": "noun, masculine accusative singular"}, "ad\u012bre": {"meaning": "to undergo", "grammar": "infinitive"}, "lab\u014dr\u0113s": {"meaning": "labors", "grammar": "noun, masculine plural accusative"}, "impulerit.": {"meaning": "she impelled", "grammar": "3rd person singular, perfect subjunctive"}, "tantaene": {"meaning": "so great", "grammar": "interrogative adjective, neuter plural nominative"}, "anim\u012bs": {"meaning": "in minds", "grammar": "noun, masculine plural ablative"}, "caelestibus": {"meaning": "celestial", "grammar": "adjective, masculine plural ablative"}, "\u012brae?": {"meaning": "anger?", "grammar": "noun, feminine nominative singular"}}', '- Epic opening formula "Arma virumque cano" - Relative clause structure "qui...vnit" - Ablative absolute "saevae memorem Innis ob ram" - Accusative with infinitive "tot volvere css" - Perfect subjunctive "impulerit" expressing completed action - Direct address to Muse with imperative "memor" - Rhetorical question ending "Tantaene...rae?"', 'The most famous opening in Latin literature, establishing the epic''s themes: divine intervention, human endurance, and Rome''s destined founding. Vergil invokes the Muse to explain Juno''s eternal hatred, setting up the central conflict between fate and divine will.', 66, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-002', 'Ovid Metamorphoses 1.1-15', 'In nova fert animus mutatas dicere formas corpora; di, coeptis (nam vos mutastis et illas), adspirate meis primaque ab origine mundi ad mea perpetuum deducite tempora carmen! Ante mare et terras et quod tegit omnia caelum, unus erat toto naturae vultus in orbe, quem dixere chaos: rudis indigestaque moles, nec quicquam nisi pondus iners congestaque eodem non bene iunctarum discordia semina rerum. Nullus adhuc mundo praebebat lumina Titan, nec nova crescendo reparabat cornua Phoebe, nec circumfuso pendebat in aere tellus ponderibus librata suis, nec bracchia longo margine terrarum porrexerat Amphitrite; utque erat et tellus illic et pontus et aer, sic erat instabilis tellus, innabilis unda, lucis egens aer; nulli sua forma manebat, obstabatque aliis aliud, quia corpore in uno frigida pugnabant calidis, umentia siccis, mollia cum duris, sine pondere, habentia pondus. Hanc deus et melior litem natura direxit.', 'My mind is carried to sing of forms changed into new bodies; gods, inspire my enterprises (for you have changed those too), and from the very beginning of the world draw my perpetual song through all times!', '{"in": {"meaning": "in", "grammar": "preposition + ablative"}, "nova": {"meaning": "new", "grammar": "adjective, feminine nominative singular"}, "fert": {"meaning": "carries", "grammar": "3rd person singular, present indicative"}, "animus": {"meaning": "mind", "grammar": "noun, masculine nominative singular"}, "mutatas": {"meaning": "changed", "grammar": "perfect passive participle, feminine plural accusative"}, "dicere": {"meaning": "to tell", "grammar": "infinitive"}, "formas": {"meaning": "shapes", "grammar": "noun, feminine plural accusative"}, "corpora;": {"meaning": "bodies", "grammar": "noun, neuter plural accusative"}, "di,": {"meaning": "gods", "grammar": "noun, masculine plural vocative"}, "coeptis": {"meaning": "enterprises", "grammar": "noun, neuter plural ablative"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "vos": {"meaning": "you", "grammar": "personal pronoun, nominative plural"}, "mutastis": {"meaning": "you have changed", "grammar": "2nd person plural, perfect indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "illas),": {"meaning": "those", "grammar": "demonstrative pronoun, feminine plural accusative"}, "adspirate": {"meaning": "inspire", "grammar": "2nd person plural, present imperative"}, "meis": {"meaning": "my", "grammar": "possessive adjective, neuter plural ablative"}, "primaque": {"meaning": "and first", "grammar": "adjective, feminine ablative singular"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "origine": {"meaning": "beginning", "grammar": "noun, feminine ablative singular"}, "mundi": {"meaning": "of the world", "grammar": "noun, masculine genitive singular"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "mea": {"meaning": "my", "grammar": "possessive adjective, neuter plural accusative"}, "perpetuum": {"meaning": "perpetual", "grammar": "adjective, neuter accusative singular"}, "deducite": {"meaning": "draw", "grammar": "2nd person plural, present imperative"}, "tempora": {"meaning": "times", "grammar": "noun, neuter plural accusative"}, "carmen!": {"meaning": "song", "grammar": "noun, neuter accusative singular"}, "ante": {"meaning": "before", "grammar": "adverb"}, "mare": {"meaning": "sea", "grammar": "noun, neuter accusative singular"}, "terras": {"meaning": "lands", "grammar": "noun, feminine plural accusative"}, "quod": {"meaning": "what", "grammar": "relative pronoun, neuter accusative singular"}, "tegit": {"meaning": "covers", "grammar": "3rd person singular, present indicative"}, "omnia": {"meaning": "all", "grammar": "adjective, neuter plural accusative"}, "caelum,": {"meaning": "the sky", "grammar": "noun, neuter accusative singular"}, "unus": {"meaning": "one", "grammar": "adjective, masculine nominative singular"}, "erat": {"meaning": "was", "grammar": "3rd person singular, imperfect indicative of sum"}, "toto": {"meaning": "in all", "grammar": "adjective, neutre ablative singular"}, "naturae": {"meaning": "of nature", "grammar": "noun, feminine genitive singular"}, "vultus": {"meaning": "appearance", "grammar": "noun, masculine nominative singular"}, "orbe,": {"meaning": "world", "grammar": "noun, neutre ablative singular"}, "quem": {"meaning": "which", "grammar": "relative pronoun, masculine accusative singular"}, "dixere": {"meaning": "they called", "grammar": "3rd person plural, perfect indicative"}, "chaos:": {"meaning": "chaos", "grammar": "noun, neuter accusative singular"}, "rudis": {"meaning": "rough", "grammar": "adjective, feminine nominative singular"}, "indigestaque": {"meaning": "and unorganized", "grammar": "adjective, feminine nominative singular"}, "moles,": {"meaning": "mass", "grammar": "noun, feminine nominative singular"}, "nec": {"meaning": "nor", "grammar": "conjunction"}, "quicquam": {"meaning": "nothing", "grammar": "indefinite pronoun, neuter accusative singular"}, "nisi": {"meaning": "but", "grammar": "conjunction"}, "pondus": {"meaning": "but inert", "grammar": "noun, neuter accusative singular"}, "iners": {"meaning": "inert", "grammar": "adjective, neuter accusative singular"}, "congestaque": {"meaning": "and gathered", "grammar": "perfect passive participle, neuter plural accusative"}, "eodem": {"meaning": "in the same", "grammar": "adjective, neutre ablative singular"}, "non": {"meaning": "not", "grammar": "adverb"}, "bene": {"meaning": "well", "grammar": "adverb"}, "iunctarum": {"meaning": "of joined", "grammar": "perfect passive participle, feminine plural genitive"}, "discordia": {"meaning": "discordant", "grammar": "adjective, feminine plural accusative"}, "semina": {"meaning": "seeds", "grammar": "noun, neuter plural accusative"}, "rerum.": {"meaning": "of things", "grammar": "noun, feminine genitive plural"}, "nullus": {"meaning": "none", "grammar": "adjective, masculine nominative singular"}, "adhuc": {"meaning": "as yet", "grammar": "adverb"}, "mundo": {"meaning": "to the world", "grammar": "noun, masculine dative singular"}, "praebebat": {"meaning": "gave", "grammar": "3rd person singular, imperfect indicative"}, "lumina": {"meaning": "light", "grammar": "noun, neuter plural accusative"}, "titan,": {"meaning": "the Sun", "grammar": "proper noun, masculine nominative singular"}, "crescendo": {"meaning": "growing", "grammar": "adverb"}, "reparabat": {"meaning": "repaired", "grammar": "3rd person singular, imperfect indicative"}, "cornua": {"meaning": "horns", "grammar": "noun, neuter plural accusative"}, "phoebe,": {"meaning": "Phoebe", "grammar": "proper noun, feminine nominative singular"}, "circumfuso": {"meaning": "surrounded by", "grammar": "perfect passive participle, neutre ablative singular"}, "pendebat": {"meaning": "hung", "grammar": "3rd person singular, imperfect indicative"}, "aere": {"meaning": "air", "grammar": "noun, neutre ablative singular"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "ponderibus": {"meaning": "by weights", "grammar": "noun, neutre plural ablative"}, "librata": {"meaning": "balanced", "grammar": "perfect passive participle, feminine nominative singular"}, "suis,": {"meaning": "by its own", "grammar": "possessive adjective, neutre plural ablative"}, "bracchia": {"meaning": "arms", "grammar": "noun, neuter plural accusative"}, "longo": {"meaning": "along the long", "grammar": "adjective, neutre ablative singular"}, "margine": {"meaning": "edge", "grammar": "noun, neutre ablative singular"}, "terrarum": {"meaning": "of lands", "grammar": "noun, feminine plural genitive"}, "porrexerat": {"meaning": "had extended", "grammar": "3rd person singular, pluperfect indicative"}, "amphitrite;": {"meaning": "Amphitrite", "grammar": "proper noun, feminine ablative singular"}, "utque": {"meaning": "and as", "grammar": "conjunction"}, "illic": {"meaning": "there", "grammar": "adverb"}, "pontus": {"meaning": "sea", "grammar": "noun, masculine nominative singular"}, "aer,": {"meaning": "air", "grammar": "noun, masculine nominative singular"}, "sic": {"meaning": "so", "grammar": "adverb"}, "instabilis": {"meaning": "unstable", "grammar": "adjective, feminine nominative singular"}, "tellus,": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "innabilis": {"meaning": "unseaworthy", "grammar": "adjective, feminine nominative singular"}, "unda,": {"meaning": "wave", "grammar": "noun, feminine nominative singular"}, "lucis": {"meaning": "light", "grammar": "noun, neuter genitive singular"}, "egens": {"meaning": "lacking", "grammar": "present participle, masculine nominative singular"}, "aer;": {"meaning": "air", "grammar": "noun, masculine nominative singular"}, "nulli": {"meaning": "each", "grammar": "indefinite pronoun, nominative singular"}, "sua": {"meaning": "its own", "grammar": "possessive adjective, neuter plural accusative"}, "forma": {"meaning": "form", "grammar": "noun, feminine nominative singular"}, "manebat,": {"meaning": "kept", "grammar": "3rd person singular, imperfect indicative"}, "obstabatque": {"meaning": "and hindered", "grammar": "3rd person singular, imperfect indicative"}, "aliis": {"meaning": "another", "grammar": "demonstrative pronoun, neuter plural ablative"}, "aliud,": {"meaning": "one thing", "grammar": "demonstrative pronoun, neuter accusative singular"}, "quia": {"meaning": "because", "grammar": "conjunction"}, "corpore": {"meaning": "in body", "grammar": "noun, neutre ablative singular"}, "uno": {"meaning": "one", "grammar": "adjective, neutre ablative singular"}, "frigida": {"meaning": "cold", "grammar": "adjective, neuter plural nominative"}, "pugnabant": {"meaning": "fought", "grammar": "3rd person plural, imperfect indicative"}, "calidis,": {"meaning": "with hot", "grammar": "adjective, neuter plural ablative"}, "umentia": {"meaning": "moist", "grammar": "adjective, neuter plural nominative"}, "siccis,": {"meaning": "with dry", "grammar": "adjective, neuter plural ablative"}, "mollia": {"meaning": "soft", "grammar": "adjective, neuter plural nominative"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "duris,": {"meaning": "with hard", "grammar": "adjective, neuter plural ablative"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "pondere,": {"meaning": "weight", "grammar": "noun, neutre ablative singular"}, "habentia": {"meaning": "having", "grammar": "present participle, neuter plural nominative"}, "pondus.": {"meaning": "weight", "grammar": "noun, neuter accusative singular"}, "hanc": {"meaning": "this", "grammar": "demonstrative adjective, feminine accusative singular"}, "deus": {"meaning": "god", "grammar": "noun, masculine nominative singular"}, "melior": {"meaning": "better", "grammar": "adjective, feminine nominative singular"}, "litem": {"meaning": "quarrel", "grammar": "noun, feminine accusative singular"}, "natura": {"meaning": "nature", "grammar": "noun, feminine nominative singular"}, "direxit.": {"meaning": "directed", "grammar": "3rd person singular, perfect indicative"}}', '- Accusative with infinitive "nova...mutatas dicere" - Imperatives "adspirate, deducite" to gods - Impersonal construction "erat" - Perfect passive participles "mutastis, congesta, librata, circumfuso" - Present participle "egens, habentia" - Ablative absolute "pugnabant...calidis" - Imperfect tense for ongoing past description', 'Ovid''s cosmic creation story describes the primordial chaos before divine order, establishing the metamorphosis theme that will run through the entire epic from chaos to humanity.', 103, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-003', 'Caesar Gallic War 1.1-3', '[1] 1 Gallia est omnis divisa in partes tres, quarum unam incolunt Belgae, aliam Aquitani, tertiam qui ipsorum lingua Celtae, nostra Galli appellantur. 2 Hi omnes lingua, institutis, legibus inter se differunt. Gallos ab Aquitanis Garumna flumen, a Belgis Matrona et Sequana dividit. 3 Horum omnium fortissimi sunt Belgae, propterea quod a cultu atque humanitate provinciae longissime absunt, minimeque ad eos mercatores saepe commeant atque ea quae ad effeminandos animos pertinent important, 4 proximique sunt Germanis, qui trans Rhenum incolunt, quibuscum continenter bellum gerunt.', '[1] 1 All Gaul is divided into three parts, of which one inhabit the Belgae, the Aquitani another, the third those who in their own language are called Celts, in our language Gauls. 2 All these differ from each other in language, customs, and laws. The Gauls are separated from the Aquitani by the Garonne river, from the Belgae by the Marne and Seine. 3 Of all these, the bravest are the Belgae, because they are furthest from the culture and civilization of the province, and merchants least often come to them and bring those things which tend to weaken their minds, 4 and they are nearest to the Germans, who live across the Rhine, with whom they wage continuous war.', '{"gallia": {"meaning": "Gaul", "grammar": "noun, feminine nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "omnis": {"meaning": "whole", "grammar": "adjective, feminine nominative singular"}, "divisa": {"meaning": "divided", "grammar": "perfect passive participle, feminine nominative singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "partes": {"meaning": "parts", "grammar": "noun, feminine plural accusative"}, "tres,": {"meaning": "three", "grammar": "adjective, feminine plural accusative"}, "quarum": {"meaning": "of which", "grammar": "relative pronoun, feminine plural genitive"}, "unam": {"meaning": "one", "grammar": "adjective, feminine accusative singular"}, "incolunt": {"meaning": "inhabit", "grammar": "3rd person plural, present indicative"}, "belgae,": {"meaning": "the Belgae", "grammar": "proper noun, masculine plural nominative"}, "aliam": {"meaning": "another", "grammar": "adjective, feminine accusative singular"}, "aquitani,": {"meaning": "the Aquitani", "grammar": "proper noun, masculine plural accusative"}, "tertiam": {"meaning": "the third", "grammar": "adjective, feminine accusative singular"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine plural nominative"}, "ipsorum": {"meaning": "of their own", "grammar": "possessive adjective, masculine plural genitive"}, "lingua": {"meaning": "in language", "grammar": "noun, feminine ablative singular"}, "celtae,": {"meaning": "Celts", "grammar": "noun, masculine plural nominative"}, "nostra": {"meaning": "in our language", "grammar": "noun, feminine ablative singular"}, "galli": {"meaning": "Gauls", "grammar": "noun, masculine plural nominative"}, "appellantur.": {"meaning": "are called", "grammar": "3rd person plural, present passive"}, "hi": {"meaning": "these", "grammar": "demonstrative pronoun, masculine plural nominative"}, "omnes": {"meaning": "all", "grammar": "adjective, masculine plural nominative"}, "lingua,": {"meaning": "in language", "grammar": "noun, feminine ablative singular"}, "institutis,": {"meaning": "in customs", "grammar": "noun, neuter plural ablative"}, "legibus": {"meaning": "in laws", "grammar": "noun, neuter plural ablative"}, "inter": {"meaning": "among", "grammar": "preposition + accusative"}, "se": {"meaning": "themselves", "grammar": "reflexive pronoun, accusative"}, "differunt.": {"meaning": "differ", "grammar": "3rd person plural, present indicative"}, "gallos": {"meaning": "the Gauls", "grammar": "noun, masculine plural accusative"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "aquitanis": {"meaning": "the Aquitani", "grammar": "proper noun, masculine plural ablative"}, "garumna": {"meaning": "Garonne", "grammar": "proper noun, feminine ablative singular"}, "flumen,": {"meaning": "river", "grammar": "noun, neutre accusative singular"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "belgis": {"meaning": "the Belgae", "grammar": "proper noun, masculine plural ablative"}, "matrona": {"meaning": "Marne", "grammar": "proper noun, feminine ablative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "sequana": {"meaning": "Seine", "grammar": "proper noun, feminine ablative singular"}, "dividit.": {"meaning": "separates", "grammar": "3rd person singular, present indicative"}, "horum": {"meaning": "of these", "grammar": "demonstrative pronoun, neuter plural genitive"}, "omnium": {"meaning": "of all", "grammar": "adjective, neuter plural genitive"}, "fortissimi": {"meaning": "bravest", "grammar": "adjective, masculine plural nominative superlative"}, "sunt": {"meaning": "are", "grammar": "3rd person plural, present indicative of sum"}, "propterea": {"meaning": "because", "grammar": "adverb"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "cultu": {"meaning": "culture", "grammar": "noun, masculine ablative singular"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "humanitate": {"meaning": "civilization", "grammar": "noun, feminine ablative singular"}, "provinciae": {"meaning": "of the province", "grammar": "noun, feminine genitive singular"}, "longissime": {"meaning": "furthest", "grammar": "adverb superlative"}, "absunt,": {"meaning": "are absent", "grammar": "3rd person plural, present indicative"}, "minimeque": {"meaning": "and least", "grammar": "adverb superlative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "eos": {"meaning": "to them", "grammar": "demonstrative pronoun, masculine plural accusative"}, "mercatores": {"meaning": "merchants", "grammar": "noun, masculine plural nominative"}, "saepe": {"meaning": "often", "grammar": "adverb"}, "commeant": {"meaning": "come", "grammar": "3rd person plural, present indicative"}, "ea": {"meaning": "those things", "grammar": "demonstrative adjective, neuter plural accusative"}, "quae": {"meaning": "which", "grammar": "relative pronoun, neuter plural accusative"}, "effeminandos": {"meaning": "to weaken", "grammar": "gerundive, masculine plural accusative"}, "animos": {"meaning": "minds", "grammar": "noun, masculine plural accusative"}, "pertinent": {"meaning": "tend", "grammar": "3rd person plural, present indicative"}, "important,": {"meaning": "bring", "grammar": "3rd person plural, present indicative"}, "proximique": {"meaning": "and nearest", "grammar": "adjective, masculine plural nominative superlative"}, "germanis,": {"meaning": "to the Germans", "grammar": "proper noun, masculine plural dative"}, "trans": {"meaning": "across", "grammar": "preposition + accusative"}, "rhenum": {"meaning": "the Rhine", "grammar": "proper noun, masculine accusative singular"}, "incolunt,": {"meaning": "live", "grammar": "3rd person plural, present indicative"}, "quibuscum": {"meaning": "with whom", "grammar": "relative pronoun + preposition, masculine plural ablative"}, "continenter": {"meaning": "continuously", "grammar": "adverb"}, "bellum": {"meaning": "war", "grammar": "noun, neuter accusative singular"}, "gerunt.": {"meaning": "wage", "grammar": "3rd person plural, present indicative"}}', '- Passive construction "appellantur" - Ablative of respect "lingua, institutis, legibus" - Relative clauses "quarum...appellantur" and "qui...incolunt" - Causal clause "propterea quod...absunt" - Gerundive "effeminandos" - Reflexive "inter se" for reciprocal action - Ablative of separation "a cultura"', 'Caesar''s ethnographic introduction establishes the military and cultural context of his conquest, emphasizing the Belgae as the most warlike and remote of the Gallic peoples.', 82, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-004', 'Catullus Poem 5.1-8', 'Vivamus mea Lesbia, atque amemus, rumoresque senum severiorum omnes unius aestimemus assis! soles occidere et redire possunt: nobis cum semel occidit brevis lux, nox est perpetua una dormienda. da mi basia mille, deinde centum, dein mille altera, dein secunda centum, deinde usque altera mille, deinde centum. dein, cum milia multa fecerimus, conturbabimus illa, ne sciamus, aut ne quis malus inuidere possit, cum tantum sciat esse basiorum.', 'Let us live, my Lesbia, and love,', '{"vivamus": {"meaning": "let us live", "grammar": "1st person plural, present subjunctive"}, "mea": {"meaning": "my", "grammar": "possessive adjective, feminine vocative"}, "lesbia,": {"meaning": "Lesbia", "grammar": "proper noun, feminine vocative"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "amemus,": {"meaning": "let us love", "grammar": "1st person plural, present subjunctive"}, "rumoresque": {"meaning": "and rumors", "grammar": "noun, masculine plural accusative"}, "senum": {"meaning": "of old men", "grammar": "noun, masculine plural genitive"}, "severiorum": {"meaning": "stricter", "grammar": "adjective, masculine plural genitive"}, "omnes": {"meaning": "all", "grammar": "adjective, masculine plural accusative"}, "unius": {"meaning": "of one", "grammar": "adjective, masculine genitive singular"}, "aestimemus!": {"meaning": "let us value", "grammar": "1st person plural, present subjunctive"}, "soles": {"meaning": "suns", "grammar": "noun, masculine plural nominative"}, "occidere": {"meaning": "to set", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "redire": {"meaning": "to return", "grammar": "infinitive"}, "possunt:": {"meaning": "can", "grammar": "3rd person plural, present indicative"}, "nobis": {"meaning": "for us", "grammar": "personal pronoun, dative plural"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "semel": {"meaning": "once", "grammar": "adverb"}, "occidit": {"meaning": "has set", "grammar": "3rd person singular, perfect indicative"}, "brevis": {"meaning": "brief", "grammar": "adjective, feminine nominative singular"}, "lux,": {"meaning": "light", "grammar": "noun, feminine nominative singular"}, "nox": {"meaning": "night", "grammar": "noun, feminine nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "perpetua": {"meaning": "perpetual", "grammar": "adjective, feminine nominative singular"}, "una": {"meaning": "one", "grammar": "adjective, feminine nominative singular"}, "dormienda.": {"meaning": "must be slept", "grammar": "gerundive, feminine nominative singular"}, "da": {"meaning": "give", "grammar": "2nd person singular, present imperative"}, "mi": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "basia": {"meaning": "kisses", "grammar": "noun, neuter plural accusative"}, "mille,": {"meaning": "a thousand", "grammar": "adjective, neuter plural accusative"}, "deinde": {"meaning": "then", "grammar": "adverb"}, "centum,": {"meaning": "a hundred", "grammar": "adjective, neuter plural accusative"}, "dein": {"meaning": "then", "grammar": "adverb"}, "mille": {"meaning": "a thousand", "grammar": "adjective, neuter plural accusative"}, "altera,": {"meaning": "another", "grammar": "adjective, feminine accusative singular"}, "secunda": {"meaning": "second", "grammar": "adjective, feminine accusative singular"}, "usque": {"meaning": "yet", "grammar": "adverb"}, "altera": {"meaning": "another", "grammar": "adjective, feminine accusative singular"}, "centum.": {"meaning": "a hundred", "grammar": "adjective, neuter plural accusative"}, "dein,": {"meaning": "then", "grammar": "adverb"}, "milia": {"meaning": "thousands", "grammar": "noun, neuter plural accusative"}, "multa": {"meaning": "many", "grammar": "adjective, neuter plural accusative"}, "fecerimus,": {"meaning": "we have made", "grammar": "1st person plural, perfect subjunctive"}, "conturbabimus": {"meaning": "we shall confuse", "grammar": "1st person plural, future indicative"}, "illa,": {"meaning": "them", "grammar": "demonstrative pronoun, neuter plural accusative"}, "ne": {"meaning": "lest", "grammar": "conjunction"}, "sciamus,": {"meaning": "we do not know", "grammar": "1st person plural, present subjunctive"}, "aut": {"meaning": "or", "grammar": "conjunction"}, "quis": {"meaning": "any", "grammar": "indefinite pronoun, masculine nominative singular"}, "malus": {"meaning": "evil", "grammar": "adjective, masculine nominative singular"}, "inuidere": {"meaning": "to envy", "grammar": "infinitive"}, "possit,": {"meaning": "can", "grammar": "3rd person singular, present subjunctive"}, "tantum": {"meaning": "only", "grammar": "adverb"}, "sciat": {"meaning": "knows", "grammar": "3rd person singular, present subjunctive"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "basiorum.": {"meaning": "of kisses", "grammar": "noun, neutre plural genitive"}}', '- Jussive subjunctives "vivamus, amemus, aestimemus" - Gerundive "dormienda" expressing necessity - Imperative "da" for direct command - Perfect subjunctive "fecerimus" in temporal clause - Negative purpose clauses "ne sciamus, ne...possit" - Subjunctive in subordinate clauses', 'The most famous love poem in Latin, a carpe diem manifesto against social convention, urging total abandonment to passionate love in defiance of Roman moralists.', 74, 'Classical Author', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-005', 'Vergil Aeneid 1.12-23', 'Urbs antqua fuit, Tyri tenure coln, Karthg, taliam contr Tibernaque long stia, dves opum studisque asperrima bell, quam In fertur terrs magis omnibus nam posthabit coluisse Sam; hc illius arma, hc currus fuit; hc rgnum dea gentibus esse, s qu Fta sinant, iam tum tenditque fovetque. Prgeniem sed enim Trin  sanguine dc audierat, Tyris olim quae verteret arcs; hinc populum lt regem bellque superbum ventrum excidi Libyae: sc volvere Parcs. Id metuns, veterisque memor Sturnia bell, prma quod ad Triam pr crs gesserat Args necdum etiam causae rrum saevque dolrs exciderant anim: manet alt mente repostum idicium Paridis sprtaeque iniria frmae, et genus invsum, et rapt Ganymdis honrs.', 'There was an ancient city, the Tyrian colonists held Carthage, opposite Italy and the long Tiberine mouth, rich in resources and most fierce in the pursuits of war, which Juno is said to have cherished above all lands, preferring it to Samos; here were her arms, here her chariot; she desired this kingdom for the nations, if the Fates would permit, even then she tended and cherished it.', '{"urbs": {"meaning": "city", "grammar": "noun, feminine nominative singular"}, "ant\u012bqua": {"meaning": "ancient", "grammar": "adjective, feminine nominative singular"}, "fuit,": {"meaning": "there was", "grammar": "3rd person singular, perfect indicative of sum"}, "tyri\u012b": {"meaning": "Tyrian", "grammar": "adjective, masculine plural nominative"}, "tenu\u0113re": {"meaning": "held", "grammar": "3rd person plural, perfect indicative"}, "col\u014dn\u012b,": {"meaning": "colonists", "grammar": "noun, masculine plural nominative"}, "karth\u0101g\u014d,": {"meaning": "Carthage", "grammar": "proper noun, feminine nominative singular"}, "\u012btaliam": {"meaning": "opposite Italy", "grammar": "noun, feminine accusative"}, "contr\u0101": {"meaning": "opposite", "grammar": "preposition + accusative"}, "tiber\u012bnaque": {"meaning": "and the Tiberine", "grammar": "adjective, feminine genitive singular"}, "long\u0113": {"meaning": "long", "grammar": "adjective, feminine plural genitive"}, "\u014dstia,": {"meaning": "mouths", "grammar": "noun, neuter plural nominative"}, "d\u012bves": {"meaning": "rich", "grammar": "adjective, feminine nominative singular"}, "opum": {"meaning": "in resources", "grammar": "noun, feminine plural genitive"}, "studi\u012bsque": {"meaning": "and in the pursuits", "grammar": "noun, neutre plural ablative"}, "asperrima": {"meaning": "most fierce", "grammar": "adjective, feminine nominative singular superlative"}, "bell\u012b,": {"meaning": "of war", "grammar": "noun, neutre genitive singular"}, "quam": {"meaning": "which", "grammar": "relative pronoun, feminine accusative singular"}, "i\u016bn\u014d": {"meaning": "Juno", "grammar": "proper noun, feminine nominative singular"}, "fertur": {"meaning": "is said", "grammar": "3rd person singular, present passive"}, "terr\u012bs": {"meaning": "above lands", "grammar": "noun, feminine plural dative"}, "magis": {"meaning": "more", "grammar": "adverb"}, "omnibus": {"meaning": "all", "grammar": "adjective, feminine plural dative"}, "\u016bnam": {"meaning": "above", "grammar": "adverb"}, "posthabit\u0101": {"meaning": "preferring", "grammar": "perfect passive participle, feminine ablative singular"}, "coluisse": {"meaning": "to have cherished", "grammar": "infinitive"}, "sam\u014d;": {"meaning": "Samos", "grammar": "proper noun, feminine ablative singular"}, "h\u012bc": {"meaning": "here", "grammar": "adverb"}, "illius": {"meaning": "her", "grammar": "possessive adjective, feminine genitive singular"}, "arma,": {"meaning": "arms", "grammar": "noun, neuter plural accusative"}, "currus": {"meaning": "chariot", "grammar": "noun, masculine plural accusative"}, "fuit;": {"meaning": "was", "grammar": "3rd person singular, perfect indicative of sum"}, "h\u014dc": {"meaning": "this", "grammar": "demonstrative adjective, neutre accusative singular"}, "r\u0113gnum": {"meaning": "kingdom", "grammar": "noun, neutre accusative singular"}, "dea": {"meaning": "the goddess", "grammar": "noun, feminine nominative singular"}, "gentibus": {"meaning": "for nations", "grammar": "noun, feminine plural dative"}, "esse,": {"meaning": "to be", "grammar": "infinitive"}, "s\u012b": {"meaning": "if", "grammar": "conjunction"}, "qu\u0101": {"meaning": "if", "grammar": "indefinite pronoun, feminine ablative singular"}, "f\u0101ta": {"meaning": "the Fates", "grammar": "noun, neuter plural nominative"}, "sinant,": {"meaning": "would permit", "grammar": "3rd person plural, present subjunctive"}, "iam": {"meaning": "even", "grammar": "adverb"}, "tum": {"meaning": "then", "grammar": "adverb"}, "tenditque": {"meaning": "and she tended", "grammar": "3rd person singular, present indicative"}, "fovetque.": {"meaning": "and she cherished", "grammar": "3rd person singular, present indicative"}, "pr\u014dgeniem": {"meaning": "descendant", "grammar": "noun, feminine accusative singular"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "enim": {"meaning": "indeed", "grammar": "adverb"}, "tr\u014di\u0101n\u014d": {"meaning": "Trojan", "grammar": "adjective, masculine ablative singular"}, "\u0101": {"meaning": "from", "grammar": "preposition + ablative"}, "sanguine": {"meaning": "blood", "grammar": "noun, masculine ablative singular"}, "d\u016bc\u012b": {"meaning": "to be traced", "grammar": "infinitive passive"}, "audierat,": {"meaning": "she had heard", "grammar": "3rd person singular, pluperfect indicative"}, "tyri\u0101s": {"meaning": "Tyrian", "grammar": "adjective, feminine plural accusative"}, "olim": {"meaning": "once", "grammar": "adverb"}, "quae": {"meaning": "which", "grammar": "relative pronoun, feminine plural accusative"}, "verteret": {"meaning": "would overthrow", "grammar": "3rd person singular, imperfect subjunctive"}, "arc\u0113s;": {"meaning": "walls", "grammar": "noun, feminine plural accusative"}, "hinc": {"meaning": "hence", "grammar": "adverb"}, "populum": {"meaning": "people", "grammar": "noun, masculine accusative singular"}, "l\u0101t\u0113": {"meaning": "widely", "grammar": "adverb"}, "regem": {"meaning": "king", "grammar": "noun, masculine accusative singular"}, "bell\u014dque": {"meaning": "and in war", "grammar": "noun, neutre ablative singular"}, "superbum": {"meaning": "proud", "grammar": "adjective, masculine accusative singular"}, "vent\u016brum": {"meaning": "destined to come", "grammar": "future active participle, masculine accusative singular"}, "excidi\u014d": {"meaning": "to the destruction", "grammar": "noun, neutre dative singular"}, "libyae:": {"meaning": "of Libya", "grammar": "noun, feminine genitive singular"}, "s\u012bc": {"meaning": "so", "grammar": "adverb"}, "volvere": {"meaning": "to spin", "grammar": "infinitive"}, "parc\u0101s.": {"meaning": "the Fates", "grammar": "noun, feminine plural accusative"}, "id": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative singular"}, "metu\u0113ns,": {"meaning": "fearing", "grammar": "present participle, feminine nominative singular"}, "veterisque": {"meaning": "and old", "grammar": "adjective, neutre genitive singular"}, "memor": {"meaning": "remembering", "grammar": "adjective, feminine nominative singular"}, "s\u0101turnia": {"meaning": "Saturnia", "grammar": "noun, feminine nominative singular"}, "pr\u012bma": {"meaning": "the first", "grammar": "adjective, feminine accusative singular"}, "quod": {"meaning": "which", "grammar": "relative pronoun, neutre accusative singular"}, "ad": {"meaning": "against", "grammar": "preposition + accusative"}, "tr\u014diam": {"meaning": "Troy", "grammar": "proper noun, feminine accusative singular"}, "pr\u014d": {"meaning": "for", "grammar": "preposition + ablative"}, "c\u0101r\u012bs": {"meaning": "beloved", "grammar": "adjective, neutre plural ablative"}, "gesserat": {"meaning": "she had carried", "grammar": "3rd person singular, pluperfect indicative"}, "arg\u012bs\u2014": {"meaning": "Argos", "grammar": "proper noun, neutre plural ablative"}, "necdum": {"meaning": "and not yet", "grammar": "adverb"}, "etiam": {"meaning": "also", "grammar": "adverb"}, "causae": {"meaning": "the causes", "grammar": "noun, feminine nominative plural"}, "\u012br\u0101rum": {"meaning": "of anger", "grammar": "noun, feminine genitive plural"}, "saev\u012bque": {"meaning": "and cruel", "grammar": "adjective, feminine genitive plural"}, "dol\u014dr\u0113s": {"meaning": "of griefs", "grammar": "noun, masculine genitive plural"}, "exciderant": {"meaning": "had fallen", "grammar": "3rd person plural, pluperfect indicative"}, "anim\u014d:": {"meaning": "from her mind", "grammar": "noun, neutre ablative singular"}, "manet": {"meaning": "remains", "grammar": "3rd person singular, present indicative"}, "alt\u0101": {"meaning": "deep", "grammar": "adjective, feminine ablative singular"}, "mente": {"meaning": "in her heart", "grammar": "noun, neutre ablative singular"}, "repostum": {"meaning": "stored away", "grammar": "perfect passive participle, neutre accusative singular"}, "i\u016bdicium": {"meaning": "judgment", "grammar": "noun, neutre accusative singular"}, "paridis": {"meaning": "of Paris", "grammar": "proper noun, masculine genitive singular"}, "spr\u0113taeque": {"meaning": "and the insult", "grammar": "noun, neutre accusative singular"}, "ini\u016bria": {"meaning": "to her spurned", "grammar": "noun, feminine ablative singular"}, "f\u014drmae,": {"meaning": "beauty", "grammar": "noun, feminine genitive singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "genus": {"meaning": "the race", "grammar": "noun, neutre accusative singular"}, "inv\u012bsum,": {"meaning": "hated", "grammar": "adjective, neutre accusative singular"}, "rapt\u012b": {"meaning": "of ravished", "grammar": "perfect passive participle, masculine genitive singular"}, "ganym\u0113dis": {"meaning": "Ganymede", "grammar": "proper noun, masculine genitive singular"}, "hon\u014dr\u0113s.": {"meaning": "honors", "grammar": "noun, masculine plural accusative"}}', '- Passive construction "fertur" with infinitive - Ablative absolute "posthabit coluisse" - Future active participle "ventrum" expressing destiny - Perfect passive participle "repostum" with accusative object - Pluperfect indicative "audierat, gesserat, exciderant" - Subjunctive in conditional "s qu...sinant"', 'Vergil establishes Carthage''s foundation and Juno''s personal motivationher hatred from the Trojan War and divine rejectionsmaking her the epic''s central antagonist.', 95, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-006', 'Ovid Metamorphoses 1.15-35', 'Nullus adhuc mundo praebebat lumina Titan, nec nova crescendo reparabat cornua Phoebe, nec circumfuso pendebat in aere tellus ponderibus librata suis, nec bracchia longo margine terrarum porrexerat Amphitrite; utque erat et tellus illic et pontus et aer, sic erat instabilis tellus, innabilis unda, lucis egens aer; nulli sua forma manebat, obstabatque aliis aliud, quia corpore in uno frigida pugnabant calidis, umentia siccis, mollia cum duris, sine pondere, habentia pondus. Hanc deus et melior litem natura direxit. Nam caelo terras et terris abscidit undas et liquidum spisso secrevit ab aere caelum. Quae postquam evolvit caecoque exemit acervo, dissociata locis concordi pace ligavit: ignea convexi vis et sine pondere caeli emicuit summaque locum sibi fecit in arce; proximus est aer illi levitate locoque; densior his tellus elementaque grandia traxit et pressa est gravitate sua; circumfluus umor ultima possedit solidumque coercuit orbem.', 'No Titan as yet gave light to the growing world, nor did the new Phoebe repair her growing horns, nor did earth, surrounded by air, hang in the air, balanced by its own weights, nor had Amphitrite extended arms along the long edge of the lands;', '{"nullus": {"meaning": "none", "grammar": "adjective, masculine nominative singular"}, "adhuc": {"meaning": "as yet", "grammar": "adverb"}, "mundo": {"meaning": "to the world", "grammar": "noun, masculine dative singular"}, "praebebat": {"meaning": "gave", "grammar": "3rd person singular, imperfect indicative"}, "lumina": {"meaning": "light", "grammar": "noun, neutre plural accusative"}, "titan,": {"meaning": "the Sun", "grammar": "proper noun, masculine nominative singular"}, "nec": {"meaning": "nor", "grammar": "conjunction"}, "nova": {"meaning": "new", "grammar": "adjective, feminine nominative singular"}, "crescendo": {"meaning": "growing", "grammar": "adverb"}, "reparabat": {"meaning": "repaired", "grammar": "3rd person singular, imperfect indicative"}, "cornua": {"meaning": "horns", "grammar": "noun, neutre plural accusative"}, "phoebe,": {"meaning": "Phoebe", "grammar": "proper noun, feminine nominative singular"}, "circumfuso": {"meaning": "surrounded by", "grammar": "perfect passive participle, neutre ablative singular"}, "pendebat": {"meaning": "hung", "grammar": "3rd person singular, imperfect indicative"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "aere": {"meaning": "air", "grammar": "noun, neutre ablative singular"}, "tellus": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "ponderibus": {"meaning": "by weights", "grammar": "noun, neutre plural ablative"}, "librata": {"meaning": "balanced", "grammar": "perfect passive participle, feminine nominative singular"}, "suis,": {"meaning": "by its own", "grammar": "possessive adjective, neutre plural ablative"}, "bracchia": {"meaning": "arms", "grammar": "noun, neutre plural accusative"}, "longo": {"meaning": "along the long", "grammar": "adjective, neutre ablative singular"}, "margine": {"meaning": "edge", "grammar": "noun, neutre ablative singular"}, "terrarum": {"meaning": "of lands", "grammar": "noun, feminine plural genitive"}, "porrexerat": {"meaning": "had extended", "grammar": "3rd person singular, pluperfect indicative"}, "amphitrite;": {"meaning": "Amphitrite", "grammar": "proper noun, feminine ablative singular"}, "utque": {"meaning": "and as", "grammar": "conjunction"}, "erat": {"meaning": "was", "grammar": "3rd person singular, imperfect indicative of sum"}, "et": {"meaning": "and", "grammar": "conjunction"}, "illic": {"meaning": "there", "grammar": "adverb"}, "pontus": {"meaning": "sea", "grammar": "noun, masculine nominative singular"}, "aer,": {"meaning": "air", "grammar": "noun, masculine nominative singular"}, "sic": {"meaning": "so", "grammar": "adverb"}, "instabilis": {"meaning": "unstable", "grammar": "adjective, feminine nominative singular"}, "tellus,": {"meaning": "earth", "grammar": "noun, feminine nominative singular"}, "innabilis": {"meaning": "unseaworthy", "grammar": "adjective, feminine nominative singular"}, "unda,": {"meaning": "wave", "grammar": "noun, feminine nominative singular"}, "lucis": {"meaning": "light", "grammar": "noun, neutre genitive singular"}, "egens": {"meaning": "lacking", "grammar": "present participle, masculine nominative singular"}, "aer;": {"meaning": "air", "grammar": "noun, masculine nominative singular"}, "nulli": {"meaning": "each", "grammar": "indefinite pronoun, nominative singular"}, "sua": {"meaning": "its own", "grammar": "possessive adjective, neutre plural accusative"}, "forma": {"meaning": "form", "grammar": "noun, feminine nominative singular"}, "manebat,": {"meaning": "kept", "grammar": "3rd person singular, imperfect indicative"}, "obstabatque": {"meaning": "and hindered", "grammar": "3rd person singular, imperfect indicative"}, "aliis": {"meaning": "another", "grammar": "demonstrative pronoun, neutre plural ablative"}, "aliud,": {"meaning": "one thing", "grammar": "demonstrative pronoun, neutre accusative singular"}, "quia": {"meaning": "because", "grammar": "conjunction"}, "corpore": {"meaning": "in body", "grammar": "noun, neutre ablative singular"}, "uno": {"meaning": "one", "grammar": "adjective, neutre ablative singular"}, "frigida": {"meaning": "cold", "grammar": "adjective, neutre plural nominative"}, "pugnabant": {"meaning": "fought", "grammar": "3rd person plural, imperfect indicative"}, "calidis,": {"meaning": "with hot", "grammar": "adjective, neutre plural ablative"}, "umentia": {"meaning": "moist", "grammar": "adjective, neutre plural nominative"}, "siccis,": {"meaning": "with dry", "grammar": "adjective, neutre plural ablative"}, "mollia": {"meaning": "soft", "grammar": "adjective, neutre plural nominative"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "duris,": {"meaning": "with hard", "grammar": "adjective, neutre plural ablative"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "pondere,": {"meaning": "weight", "grammar": "noun, neutre ablative singular"}, "habentia": {"meaning": "having", "grammar": "present participle, neutre plural nominative"}, "pondus.": {"meaning": "weight", "grammar": "noun, neutre accusative singular"}, "hanc": {"meaning": "this", "grammar": "demonstrative adjective, feminine accusative singular"}, "deus": {"meaning": "god", "grammar": "noun, masculine nominative singular"}, "melior": {"meaning": "better", "grammar": "adjective, feminine nominative singular"}, "litem": {"meaning": "quarrel", "grammar": "noun, feminine accusative singular"}, "natura": {"meaning": "nature", "grammar": "noun, feminine nominative singular"}, "direxit.": {"meaning": "directed", "grammar": "3rd person singular, perfect indicative"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "caelo": {"meaning": "from heaven", "grammar": "noun, neutre ablative singular"}, "terras": {"meaning": "earth", "grammar": "noun, feminine accusative singular"}, "terris": {"meaning": "from earth", "grammar": "noun, feminine plural ablative"}, "abscidit": {"meaning": "cut off", "grammar": "3rd person singular, perfect indicative"}, "undas": {"meaning": "waters", "grammar": "noun, feminine plural accusative"}, "liquidum": {"meaning": "the liquid", "grammar": "adjective, neutre accusative singular"}, "spisso": {"meaning": "from the thick", "grammar": "adjective, neutre ablative singular"}, "secrevit": {"meaning": "separated", "grammar": "3rd person singular, perfect indicative"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "caelum.": {"meaning": "sky", "grammar": "noun, neutre accusative singular"}, "quae": {"meaning": "when she", "grammar": "relative pronoun, feminine nominative singular"}, "postquam": {"meaning": "after", "grammar": "conjunction"}, "evolvit": {"meaning": "had unrolled", "grammar": "3rd person singular, pluperfect indicative"}, "caecoque": {"meaning": "and from the blind", "grammar": "adjective, neutre ablative singular"}, "exemit": {"meaning": "removed", "grammar": "3rd person singular, perfect indicative"}, "acervo,": {"meaning": "heap", "grammar": "noun, neutre ablative singular"}, "dissociata": {"meaning": "the separated", "grammar": "perfect passive participle, neutre plural accusative"}, "locis": {"meaning": "from their places", "grammar": "noun, neutre plural ablative"}, "concordi": {"meaning": "with harmonious", "grammar": "adjective, feminine ablative singular"}, "pace": {"meaning": "peace", "grammar": "noun, feminine ablative singular"}, "ligavit:": {"meaning": "[he] bound", "grammar": "3rd person singular, perfect indicative"}, "ignea": {"meaning": "the fiery", "grammar": "adjective, feminine nominative singular"}, "convexi": {"meaning": "of the convex", "grammar": "adjective, neutre genitive singular"}, "vis": {"meaning": "force", "grammar": "noun, feminine nominative singular"}, "pondere": {"meaning": "weight", "grammar": "noun, neutre ablative singular"}, "caeli": {"meaning": "of the sky", "grammar": "noun, neutre genitive singular"}, "emicuit": {"meaning": "flashed out", "grammar": "3rd person singular, perfect indicative"}, "summaque": {"meaning": "and in the highest", "grammar": "adjective, feminine ablative singular"}, "locum": {"meaning": "place", "grammar": "noun, masculine accusative singular"}, "sibi": {"meaning": "for itself", "grammar": "reflexive pronoun, dative singular"}, "fecit": {"meaning": "made", "grammar": "3rd person singular, perfect indicative"}, "arce;": {"meaning": "realm", "grammar": "noun, feminine ablative singular"}, "proximus": {"meaning": "next", "grammar": "adjective, masculine nominative singular"}, "est": {"meaning": "was", "grammar": "3rd person singular, present passive"}, "aer": {"meaning": "air", "grammar": "noun, masculine nominative singular"}, "illi": {"meaning": "to it", "grammar": "demonstrative pronoun, masculine dative singular"}, "levitate": {"meaning": "in substance", "grammar": "noun, feminine ablative singular"}, "locoque;": {"meaning": "and in place", "grammar": "noun, neutre ablative singular"}, "densior": {"meaning": "denser", "grammar": "adjective, feminine nominative singular"}, "his": {"meaning": "than these", "grammar": "demonstrative pronoun, neutre plural ablative"}, "elementaque": {"meaning": "and elements", "grammar": "noun, neutre plural accusative"}, "grandia": {"meaning": "massive", "grammar": "adjective, neutre plural accusative"}, "traxit": {"meaning": "drew", "grammar": "3rd person singular, perfect indicative"}, "pressa": {"meaning": "pressed", "grammar": "perfect passive participle, feminine nominative singular"}, "gravitate": {"meaning": "by weight", "grammar": "noun, feminine ablative singular"}, "sua;": {"meaning": "by its own", "grammar": "possessive adjective, feminine ablative singular"}, "circumfluus": {"meaning": "surrounding", "grammar": "adjective, feminine nominative singular"}, "umor": {"meaning": "water", "grammar": "noun, masculine nominative singular"}, "ultima": {"meaning": "last", "grammar": "adjective, feminine nominative singular"}, "possedit": {"meaning": "possessed", "grammar": "3rd person singular, perfect indicative"}, "solidumque": {"meaning": "and solid", "grammar": "adjective, neutre accusative singular"}, "coercuit": {"meaning": "restrained", "grammar": "3rd person singular, perfect indicative"}, "orbem.": {"meaning": "world", "grammar": "noun, masculine accusative singular"}}', '- Imperfect indicative "praebebat, pendebat, manebat, obstabatque, pugnabant" - Pluperfect "porrexerat, evolvit" - Perfect passive participles "librata, circumfuso, dissociata, pressa" - Present participle "egens, habentia" - Ablative absolute "caecoque...acervo" - Comparative adjective "densior" with ablative "his" - Accusative with infinitive construction', 'Ovid describes the cosmic ordering from chaos to structured cosmos, showing divine intelligence imposing harmony on primordial conflict between opposing elements.', 102, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-007', 'Caesar Gallic War 1.5-6', 'Post eius mortem nihilo minus Helvetii id quod constituerant facere conantur, ut e finibus suis exeant. Ubi iam se ad eam rem paratos esse arbitrati sunt, oppida sua omnia, numero ad duodecim, vicos ad quadringentos, reliqua privata aedificia incendunt; frumentum omne, praeter quod secum portaturi erant, comburunt, ut domum reditionis spe sublata paratiores ad omnia pericula subeunda essent; trium mensum molita cibaria sibi quemque domo efferre iubent. Erant omnino itinera duo, quibus itineribus domo exire possent: unum per Sequanos, angustum et difficile, inter montem Iuram et flumen Rhodanum, vix qua singuli carri ducerentur, mons autem altissimus impendebat, ut facile perpauci prohibere possent; alterum per provinciam nostram, multo facilius atque expeditius, propterea quod inter fines Helvetiorum et Allobrogum, qui nuper pacati erant, Rhodanus fluit isque non nullis locis vado transitur.', 'After his death, nevertheless the Helvetii attempt to do that which they had decided, that they should leave their territories. When they thought themselves prepared for that enterprise, they burn all their towns, about twelve in number, villages about four hundred, and their other private buildings; they burn all grain except what they were about to carry with them, so that hope of return home being removed, they would be more ready to undergo all dangers; they order each to carry from home ground grain for three months prepared.', '{"post": {"meaning": "after", "grammar": "preposition + accusative"}, "eius": {"meaning": "his", "grammar": "demonstrative pronoun, neutre genitive singular"}, "mortem": {"meaning": "death", "grammar": "noun, feminine accusative singular"}, "nihilo": {"meaning": "nevertheless", "grammar": "adverb"}, "minus": {"meaning": "less", "grammar": "adverb"}, "helvetii": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural nominative"}, "id": {"meaning": "that", "grammar": "demonstrative pronoun, neutre accusative"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "constituerant": {"meaning": "they had decided", "grammar": "3rd person plural, pluperfect indicative"}, "facere": {"meaning": "to do", "grammar": "infinitive"}, "conantur,": {"meaning": "they attempt", "grammar": "3rd person plural, present indicative"}, "ut": {"meaning": "so that", "grammar": "conjunction"}, "e": {"meaning": "from", "grammar": "preposition + ablative"}, "finibus": {"meaning": "from territories", "grammar": "noun, neutre plural ablative"}, "suis": {"meaning": "their", "grammar": "possessive adjective, neutre plural ablative"}, "exeant.": {"meaning": "they should leave", "grammar": "3rd person plural, present subjunctive"}, "ubi": {"meaning": "when", "grammar": "conjunction"}, "iam": {"meaning": "now", "grammar": "adverb"}, "se": {"meaning": "themselves", "grammar": "reflexive pronoun, accusative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "eam": {"meaning": "that", "grammar": "demonstrative adjective, feminine accusative"}, "rem": {"meaning": "enterprise", "grammar": "noun, feminine accusative"}, "paratos": {"meaning": "prepared", "grammar": "adjective, masculine plural accusative"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "arbitrati": {"meaning": "they thought", "grammar": "3rd person plural, perfect indicative"}, "sunt,": {"meaning": "they are", "grammar": "3rd person plural, present indicative of sum"}, "oppida": {"meaning": "towns", "grammar": "noun, neutre plural accusative"}, "sua": {"meaning": "their", "grammar": "possessive adjective, neutre plural accusative"}, "omnia,": {"meaning": "all", "grammar": "adjective, neutre plural accusative"}, "numero": {"meaning": "in number", "grammar": "noun, neutre ablative singular"}, "duodecim,": {"meaning": "twelve", "grammar": "adjective, neutre plural accusative"}, "vicos": {"meaning": "villages", "grammar": "noun, masculine plural accusative"}, "quadringentos,": {"meaning": "four hundred", "grammar": "adjective, masculine plural accusative"}, "reliqua": {"meaning": "other", "grammar": "adjective, neutre plural accusative"}, "privata": {"meaning": "private", "grammar": "adjective, neutre plural accusative"}, "aedificia": {"meaning": "buildings", "grammar": "noun, neutre plural accusative"}, "incendunt;": {"meaning": "they burn", "grammar": "3rd person plural, present indicative"}, "frumentum": {"meaning": "grain", "grammar": "noun, neutre accusative singular"}, "omne,": {"meaning": "all", "grammar": "adjective, neutre accusative singular"}, "praeter": {"meaning": "except", "grammar": "preposition + accusative"}, "secum": {"meaning": "with them", "grammar": "reflexive pronoun + preposition"}, "portaturi": {"meaning": "about to carry", "grammar": "future active participle, masculine plural nominative"}, "erant,": {"meaning": "had been", "grammar": "3rd person plural, pluperfect indicative of sum"}, "comburunt,": {"meaning": "they burn", "grammar": "3rd person plural, present indicative"}, "domum": {"meaning": "home", "grammar": "adverb"}, "reditionis": {"meaning": "of return", "grammar": "noun, feminine genitive singular"}, "spe": {"meaning": "hope", "grammar": "noun, feminine ablative singular"}, "sublata": {"meaning": "being removed", "grammar": "perfect passive participle, feminine ablative singular"}, "paratiores": {"meaning": "more ready", "grammar": "adjective, masculine plural accusative comparative"}, "omnia": {"meaning": "all", "grammar": "adjective, neutre plural accusative"}, "pericula": {"meaning": "dangers", "grammar": "noun, neutre plural accusative"}, "subeunda": {"meaning": "to undergo", "grammar": "gerundive, neutre plural accusative"}, "essent;": {"meaning": "they would be", "grammar": "3rd person plural, imperfect subjunctive of sum"}, "trium": {"meaning": "for three", "grammar": "adjective, neuter plural genitive"}, "mensum": {"meaning": "months", "grammar": "noun, masculine plural genitive"}, "molita": {"meaning": "prepared", "grammar": "perfect passive participle, neutre plural accusative"}, "cibaria": {"meaning": "grain", "grammar": "noun, neutre plural accusative"}, "sibi": {"meaning": "each", "grammar": "indefinite pronoun, dative singular"}, "quemque": {"meaning": "from home", "grammar": "noun, neutre accusative singular"}, "domo": {"meaning": "from home", "grammar": "noun, feminine ablative singular"}, "efferre": {"meaning": "to carry", "grammar": "infinitive"}, "iubent.": {"meaning": "to be carried", "grammar": "infinitive passive"}, "erant": {"meaning": "there were", "grammar": "3rd person plural, imperfect indicative of sum"}, "omnino": {"meaning": "altogether", "grammar": "adverb"}, "itinera": {"meaning": "roads", "grammar": "noun, neutre plural nominative"}, "duo,": {"meaning": "two", "grammar": "adjective, neutre plural nominative"}, "quibus": {"meaning": "by which", "grammar": "relative pronoun, neutre plural ablative"}, "itineribus": {"meaning": "roads", "grammar": "noun, neutre plural ablative"}, "exire": {"meaning": "to leave", "grammar": "infinitive"}, "possent:": {"meaning": "they could", "grammar": "3rd person plural, imperfect subjunctive"}, "unum": {"meaning": "one", "grammar": "adjective, neutre accusative singular"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "sequanos,": {"meaning": "the Sequani", "grammar": "proper noun, masculine plural accusative"}, "angustum": {"meaning": "narrow", "grammar": "adjective, neutre accusative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "difficile,": {"meaning": "difficult", "grammar": "adjective, neutre accusative singular"}, "inter": {"meaning": "between", "grammar": "preposition + accusative"}, "montem": {"meaning": "Mount", "grammar": "noun, masculine accusative singular"}, "iuram": {"meaning": "Jura", "grammar": "proper noun, feminine accusative singular"}, "flumen": {"meaning": "the river", "grammar": "noun, neutre accusative singular"}, "rhodanum,": {"meaning": "the Rhone", "grammar": "proper noun, feminine ablative singular"}, "vix": {"meaning": "barely", "grammar": "adverb"}, "qua": {"meaning": "where", "grammar": "relative adverb"}, "singuli": {"meaning": "single", "grammar": "adjective, masculine plural nominative"}, "carri": {"meaning": "wagons", "grammar": "noun, masculine plural nominative"}, "ducerentur,": {"meaning": "could be led", "grammar": "3rd person plural, imperfect subjunctive passive"}, "mons": {"meaning": "mountain", "grammar": "noun, masculine nominative singular"}, "autem": {"meaning": "and", "grammar": "adverb"}, "altissimus": {"meaning": "very high", "grammar": "adjective, masculine nominative singular superlative"}, "impendebat,": {"meaning": "overhangs", "grammar": "3rd person singular, imperfect indicative"}, "facile": {"meaning": "easily", "grammar": "adverb"}, "perpauci": {"meaning": "very few", "grammar": "adjective, masculine plural nominative"}, "prohibere": {"meaning": "to prevent", "grammar": "infinitive"}, "possent;": {"meaning": "could", "grammar": "3rd person plural, imperfect subjunctive"}, "alterum": {"meaning": "the other", "grammar": "adjective, neutre accusative singular"}, "provinciam": {"meaning": "the province", "grammar": "noun, feminine accusative singular"}, "nostram,": {"meaning": "our", "grammar": "possessive adjective, feminine accusative singular"}, "multo": {"meaning": "much", "grammar": "adverb"}, "facilius": {"meaning": "easier", "grammar": "adjective, neutre accusative singular comparative"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "expeditius,": {"meaning": "more direct", "grammar": "adjective, neutre accusative singular comparative"}, "propterea": {"meaning": "because", "grammar": "adverb"}, "fines": {"meaning": "the territory", "grammar": "noun, masculine plural accusative"}, "helvetiorum": {"meaning": "of the Helvetii", "grammar": "proper noun, masculine plural genitive"}, "allobrogum,": {"meaning": "the Allobroges", "grammar": "proper noun, masculine plural genitive"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine plural nominative"}, "nuper": {"meaning": "recently", "grammar": "adverb"}, "pacati": {"meaning": "made peaceful", "grammar": "perfect passive participle, masculine plural nominative"}, "rhodanus": {"meaning": "the Rhone", "grammar": "proper noun, masculine nominative singular"}, "fluit": {"meaning": "flows", "grammar": "3rd person singular, present indicative"}, "isque": {"meaning": "and it", "grammar": "demonstrative pronoun, masculine nominative singular"}, "non": {"meaning": "not", "grammar": "adverb"}, "nullis": {"meaning": "several", "grammar": "adjective, neutre plural ablative"}, "locis": {"meaning": "at places", "grammar": "noun, neutre plural ablative"}, "vado": {"meaning": "by ford", "grammar": "noun, neutre ablative singular"}, "transitur.": {"meaning": "can be crossed", "grammar": "3rd person singular, present passive"}}', '- Temporal clause "Ubi...arbitrati sunt" - Purpose clause "ut...exeant" with subjunctive - Accusative with infinitive "se...paratos esse" - Gerundive "subeunda" - Subjunctive "essent" in result clause - Relative clause "quibus...possent" - Subjunctive "possent" expressing possibility - Passive construction "transitur"', 'Caesar describes the Helvetii''s desperate preparations for migration, showing their resolve and the strategic importance of the geography they must cross.', 120, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-008', 'Catullus Poem 51.1-8', 'Ille mi par esse deo videtur, ille, si fas est, superare divos, qui sedens adversus identidem te spectat et audit dulce ridentem, misero quod omnis eripit sensus mihi: nam simul te, Lesbia, aspexi, nihil est super mi vocis in ore, lingua sed torpet, tenuis sub artus flamma demanat, sonitu suopte tintinant aures, gemina teguntur lumina nocte. Otium, Catulle, tibi molestum est: otio exsultas nimiumque gestis: otium et reges prius et beatas perdidit urbes.', 'He seems to me equal to a god,', '{"ille": {"meaning": "he", "grammar": "demonstrative pronoun, masculine nominative singular"}, "mi": {"meaning": "me", "grammar": "personal pronoun, dative singular"}, "par": {"meaning": "equal", "grammar": "adjective, masculine nominative singular"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "deo": {"meaning": "to god", "grammar": "noun, masculine dative singular"}, "videtur,": {"meaning": "he seems", "grammar": "3rd person singular, present indicative"}, "si": {"meaning": "if", "grammar": "conjunction"}, "fas": {"meaning": "right", "grammar": "noun, neutre nominative singular"}, "est,": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "superare": {"meaning": "to surpass", "grammar": "infinitive"}, "divos,": {"meaning": "the gods", "grammar": "noun, masculine plural accusative"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine nominative singular"}, "sedens": {"meaning": "sitting", "grammar": "present participle, masculine nominative singular"}, "adversus": {"meaning": "opposite", "grammar": "preposition + accusative"}, "identidem": {"meaning": "continually", "grammar": "adverb"}, "te": {"meaning": "you", "grammar": "personal pronoun, accusative singular"}, "spectat": {"meaning": "watches", "grammar": "3rd person singular, present indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "audit": {"meaning": "hears", "grammar": "3rd person singular, present indicative"}, "dulce": {"meaning": "sweetly", "grammar": "adverb"}, "ridentem,": {"meaning": "laughing", "grammar": "present participle, feminine accusative singular"}, "misero": {"meaning": "wretched", "grammar": "adjective, masculine vocative"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "omnis": {"meaning": "all", "grammar": "adjective, neuter plural nominative"}, "eripit": {"meaning": "are taken", "grammar": "3rd person plural, present indicative"}, "sensus": {"meaning": "senses", "grammar": "noun, masculine plural nominative"}, "mihi:": {"meaning": "from me", "grammar": "personal pronoun, dative singular"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "simul": {"meaning": "as soon as", "grammar": "conjunction"}, "te,": {"meaning": "you", "grammar": "personal pronoun, accusative singular"}, "lesbia,": {"meaning": "Lesbia", "grammar": "proper noun, feminine vocative"}, "aspexi,": {"meaning": "I looked", "grammar": "1st person singular, perfect indicative"}, "nihil": {"meaning": "nothing", "grammar": "adverb"}, "est": {"meaning": "remains", "grammar": "3rd person singular, present indicative of sum"}, "super": {"meaning": "over", "grammar": "preposition + ablative"}, "vocis": {"meaning": "in the voice''s", "grammar": "noun, feminine genitive singular"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "ore,": {"meaning": "place", "grammar": "noun, neutre ablative singular"}, "lingua": {"meaning": "my tongue", "grammar": "noun, feminine nominative singular"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "torpet,": {"meaning": "is paralyzed", "grammar": "3rd person singular, present indicative"}, "tenuis": {"meaning": "thin", "grammar": "adjective, feminine nominative singular"}, "sub": {"meaning": "through", "grammar": "preposition + accusative"}, "artus": {"meaning": "limbs", "grammar": "noun, masculine plural accusative"}, "flamma": {"meaning": "flame", "grammar": "noun, feminine nominative singular"}, "demanat,": {"meaning": "flows", "grammar": "3rd person singular, present indicative"}, "sonitu": {"meaning": "with sound", "grammar": "noun, masculine ablative singular"}, "suopte": {"meaning": "its own", "grammar": "reflexive possessive adjective, masculine ablative singular"}, "tintinant": {"meaning": "ring", "grammar": "3rd person plural, present indicative"}, "aures,": {"meaning": "ears", "grammar": "noun, feminine plural nominative"}, "gemina": {"meaning": "twin", "grammar": "adjective, feminine plural nominative"}, "teguntur": {"meaning": "are covered", "grammar": "3rd person plural, present passive"}, "lumina": {"meaning": "eyes", "grammar": "noun, neutre plural nominative"}, "nocte.": {"meaning": "with night", "grammar": "noun, feminine ablative singular"}, "otium,": {"meaning": "Leisure", "grammar": "noun, neutre vocative"}, "catulle,": {"meaning": "Catullus", "grammar": "proper noun, masculine vocative"}, "tibi": {"meaning": "to you", "grammar": "personal pronoun, dative singular"}, "molestum": {"meaning": "troublesome", "grammar": "adjective, neutre vocative singular"}, "est:": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "otio": {"meaning": "in leisure", "grammar": "noun, neutre ablative singular"}, "exsultas": {"meaning": "you exult", "grammar": "2nd person singular, present indicative"}, "nimiumque": {"meaning": "too much", "grammar": "adverb"}, "gestis:": {"meaning": "you behave", "grammar": "2nd person singular, present indicative"}, "otium": {"meaning": "leisure", "grammar": "noun, neutre nominative singular"}, "reges": {"meaning": "kings", "grammar": "noun, masculine plural accusative"}, "prius": {"meaning": "before", "grammar": "adverb"}, "beatas": {"meaning": "blessed", "grammar": "adjective, feminine plural accusative"}, "perdidit": {"meaning": "has destroyed", "grammar": "3rd person singular, perfect indicative"}, "urbes.": {"meaning": "cities", "grammar": "noun, feminine plural accusative"}}', '- Dative of possession "mi par" - Indirect statement "ille...superare divos" - Conditional clause "si fas est" - Present participles "sedens, ridentem" - Temporal clause "simul...aspexi" - Present passive "teguntur" - Vocative addressing self and beloved - Chiasmus structure in final stanzas', 'Catullus adapts Sappho''s famous fragment about love''s overwhelming power, then adds his own bitter meditation on leisure''s destructive effects, showing his literary sophistication.', 89, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-009', 'Vergil Aeneid 1.50-65', 'Talia flammato secum dea corde volutans, nimborum in patriam, loca feta furentibus austris, Aeoliam venit. Hic vasto rex Aeolus antro luctantes ventos tempestatesque sonoras imperio premit ac vinclis et carcere frenat. Illi indignantes magno cum murmure montis circum claustra fremunt; celsa sedet Aeolus arce sceptra tenens, mollitque animos et temperat iras. Ni faciat, maria ac terras caelumque profundum quippe ferant rapidi secum verrantque per auras. Sed pater omnipotens speluncis abdidit atris, hoc metuens, molemque et montis insuper altos imposuit, regemque dedit, qui foedere certo et premere et laxas sciret dare iussus habenas. Ad quem tum Iuno supplex his vocibus usa est: ''Aeole, namque tibi divom pater atque hominum rex et mulcere dedit fluctus et tollere vento, gens inimica mihi Tyrrhenum navigat aequor, Ilium in Italiam portans victosque Penates:', 'Such things revolving in her inflamed heart, the goddess came to the homeland of storms, places teeming with furious southern winds, Aeolia. Here King Aeolus in a vast cave presses with his power the struggling winds and noisy storms and restrains them with bonds and prison.', '{"talia": {"meaning": "such things", "grammar": "demonstrative adjective, neutre plural accusative"}, "flammato": {"meaning": "inflamed", "grammar": "perfect passive participle, neutre ablative"}, "secum": {"meaning": "with them", "grammar": "reflexive pronoun + preposition"}, "dea": {"meaning": "the goddess", "grammar": "noun, feminine nominative singular"}, "corde": {"meaning": "in her heart", "grammar": "noun, neutre ablative singular"}, "volutans,": {"meaning": "revolving", "grammar": "present participle, feminine nominative singular"}, "nimborum": {"meaning": "of storms", "grammar": "noun, neutre plural genitive"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "patriam": {"meaning": "the homeland", "grammar": "noun, feminine accusative singular"}, "loca": {"meaning": "places", "grammar": "noun, neutre plural accusative"}, "feta": {"meaning": "teeming", "grammar": "perfect passive participle, feminine plural accusative"}, "furentibus": {"meaning": "with furious", "grammar": "adjective, masculine plural ablative"}, "austris,": {"meaning": "southern winds", "grammar": "noun, masculine plural ablative"}, "aeoliam": {"meaning": "Aeolia", "grammar": "proper noun, feminine accusative singular"}, "venit.": {"meaning": "she came", "grammar": "3rd person singular, perfect indicative"}, "hic": {"meaning": "here", "grammar": "adverb"}, "vasto": {"meaning": "in a vast", "grammar": "adjective, neutre ablative singular"}, "rex": {"meaning": "king", "grammar": "noun, masculine nominative singular"}, "aeolus": {"meaning": "Aeolus", "grammar": "proper noun, masculine nominative singular"}, "antro": {"meaning": "in a cave", "grammar": "noun, neutre ablative singular"}, "luctantes": {"meaning": "struggling", "grammar": "present participle, feminine plural accusative"}, "ventos": {"meaning": "winds", "grammar": "noun, masculine plural accusative"}, "tempestatesque": {"meaning": "and storms", "grammar": "noun, feminine plural accusative"}, "sonoras": {"meaning": "noisy", "grammar": "adjective, feminine plural accusative"}, "imperio": {"meaning": "with his power", "grammar": "noun, neutre ablative singular"}, "premit": {"meaning": "presses", "grammar": "3rd person singular, present indicative"}, "ac": {"meaning": "and", "grammar": "conjunction"}, "vinclis": {"meaning": "with bonds", "grammar": "noun, neutre plural ablative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "carcere": {"meaning": "with prison", "grammar": "noun, neutre ablative singular"}, "frenat.": {"meaning": "restrains", "grammar": "3rd person singular, present indicative"}, "illi": {"meaning": "those", "grammar": "demonstrative pronoun, masculine plural nominative"}, "indignantes": {"meaning": "indignant", "grammar": "present participle, masculine plural nominative"}, "magno": {"meaning": "with great", "grammar": "adjective, neutre ablative singular"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "murmure": {"meaning": "murmur", "grammar": "noun, neutre ablative singular"}, "montis": {"meaning": "mountains", "grammar": "noun, masculine plural accusative"}, "circum": {"meaning": "around", "grammar": "adverb"}, "claustra": {"meaning": "barriers", "grammar": "noun, neutre plural accusative"}, "fremunt;": {"meaning": "rage", "grammar": "3rd person plural, present indicative"}, "celsa": {"meaning": "high", "grammar": "adjective, feminine ablative singular"}, "sedet": {"meaning": "sits", "grammar": "3rd person singular, present indicative"}, "arce": {"meaning": "on a citadel", "grammar": "noun, feminine ablative singular"}, "sceptra": {"meaning": "a scepter", "grammar": "noun, neutre accusative singular"}, "tenens,": {"meaning": "holding", "grammar": "present participle, masculine nominative singular"}, "mollitque": {"meaning": "and softens", "grammar": "3rd person singular, present indicative"}, "animos": {"meaning": "spirits", "grammar": "noun, masculine plural accusative"}, "temperat": {"meaning": "tempers", "grammar": "3rd person singular, present indicative"}, "iras.": {"meaning": "their anger", "grammar": "noun, feminine plural accusative"}, "ni": {"meaning": "unless", "grammar": "conjunction"}, "faciat,": {"meaning": "he [makes them]", "grammar": "3rd person singular, present subjunctive"}, "maria": {"meaning": "seas", "grammar": "noun, neutre plural nominative"}, "terras": {"meaning": "lands", "grammar": "noun, feminine plural accusative"}, "caelumque": {"meaning": "and heaven", "grammar": "noun, neutre accusative singular"}, "profundum": {"meaning": "deep", "grammar": "adjective, neutre accusative singular"}, "quippe": {"meaning": "indeed", "grammar": "adverb"}, "ferant": {"meaning": "would carry away", "grammar": "3rd person plural, present subjunctive"}, "rapidi": {"meaning": "swift", "grammar": "adjective, masculine plural nominative"}, "verrantque": {"meaning": "and sweep away", "grammar": "3rd person plural, present subjunctive"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "auras.": {"meaning": "the air", "grammar": "noun, feminine plural accusative"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "pater": {"meaning": "father", "grammar": "noun, masculine nominative singular"}, "omnipotens": {"meaning": "omnipotent", "grammar": "adjective, masculine nominative singular"}, "speluncis": {"meaning": "in black caves", "grammar": "noun, feminine plural ablative"}, "abdidit,": {"meaning": "hid", "grammar": "3rd person singular, perfect indicative"}, "hoc": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative singular"}, "metuens,": {"meaning": "fearing", "grammar": "present participle, masculine nominative singular"}, "molemque": {"meaning": "and a mass", "grammar": "noun, feminine accusative singular"}, "insuper": {"meaning": "above", "grammar": "adverb"}, "altos": {"meaning": "high", "grammar": "adjective, masculine plural accusative"}, "imposuit,": {"meaning": "placed over", "grammar": "3rd person singular, perfect indicative"}, "regemque": {"meaning": "king", "grammar": "noun, masculine accusative singular"}, "dedit,": {"meaning": "who", "grammar": "3rd person singular, perfect indicative"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine nominative singular"}, "foedere": {"meaning": "by a covenant", "grammar": "noun, neutre ablative singular"}, "certo": {"meaning": "certain", "grammar": "adjective, neutre ablative singular"}, "premere": {"meaning": "to press", "grammar": "infinitive"}, "laxas": {"meaning": "slack", "grammar": "adjective, feminine plural accusative"}, "sciret": {"meaning": "would know", "grammar": "3rd person singular, imperfect subjunctive"}, "dare": {"meaning": "to give", "grammar": "infinitive"}, "iussus": {"meaning": "when ordered", "grammar": "perfect passive participle, masculine nominative singular"}, "habenas.": {"meaning": "reins", "grammar": "noun, feminine plural accusative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "quem": {"meaning": "whom", "grammar": "relative pronoun, masculine accusative singular"}, "tum": {"meaning": "then", "grammar": "adverb"}, "iuno": {"meaning": "Juno", "grammar": "proper noun, feminine nominative singular"}, "supplex": {"meaning": "as a suppliant", "grammar": "adjective, feminine nominative singular"}, "his": {"meaning": "these", "grammar": "demonstrative adjective, feminine ablative plural"}, "vocibus": {"meaning": "with words", "grammar": "noun, feminine ablative plural"}, "usa": {"meaning": "used", "grammar": "perfect passive participle, feminine nominative singular"}, "est:": {"meaning": "she is", "grammar": "3rd person singular, present indicative of sum"}, "aeole,": {"meaning": "Aeolus", "grammar": "proper noun, masculine vocative"}, "namque": {"meaning": "for indeed", "grammar": "adverb"}, "tibi": {"meaning": "to you", "grammar": "personal pronoun, dative singular"}, "divom": {"meaning": "of gods", "grammar": "noun, masculine plural genitive"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "hominum": {"meaning": "of men", "grammar": "noun, masculine plural genitive"}, "mulcere": {"meaning": "to calm", "grammar": "infinitive"}, "dedit": {"meaning": "has given", "grammar": "3rd person singular, perfect indicative"}, "fluctus": {"meaning": "waves", "grammar": "noun, masculine plural accusative"}, "tollere": {"meaning": "to raise", "grammar": "infinitive"}, "vento": {"meaning": "by wind", "grammar": "noun, masculine ablative singular"}, "gens": {"meaning": "people", "grammar": "noun, feminine nominative singular"}, "inimica": {"meaning": "enemy", "grammar": "adjective, feminine nominative singular"}, "mihi": {"meaning": "to me", "grammar": "personal pronoun, dative singular"}, "tyrrhenum": {"meaning": "Tyrrhenian", "grammar": "adjective, neutre accusative singular"}, "navigat": {"meaning": "sails", "grammar": "3rd person singular, present indicative"}, "aequor,": {"meaning": "the sea", "grammar": "noun, neutre accusative singular"}, "ilium": {"meaning": "Troy", "grammar": "proper noun, neutre accusative singular"}, "italiam": {"meaning": "Italy", "grammar": "proper noun, feminine accusative singular"}, "portans": {"meaning": "carrying", "grammar": "present participle, masculine nominative singular"}, "victosque": {"meaning": "and conquered", "grammar": "perfect passive participle, masculine plural accusative"}, "penates:": {"meaning": "household gods", "grammar": "noun, masculine plural accusative"}}', '- Ablative absolute "flammato corde" - Present participles "volutans, luctantes, indignantes, metuens, portans" - Perfect passive participles "feta, usa, iussus" - Ablative of means "imperio, vinclis, carcere, vento" - Subjunctive in conditional "ni...faciat, ferant, verrantque" - Perfect active participles "abdidi, imposuit, dedit" - Accusative with infinitive "ferant, verrantque"', 'Vergil introduces the divine hierarchy with Aeolus as Jupiter''s lieutenant, showing Juno''s strategic manipulation of supernatural forces against Aeneas.', 117, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-010', 'Ovid Metamorphoses 1.35-55', 'Sic ubi dispositam quisquis fuit ille deorum congeriem secuit sectamque in membra coegit, principio terram, ne non aequalis ab omni parte foret, magni speciem glomeravit in orbis. tum freta diffundi rapidisque tumescere ventis iussit et ambitae circumdare litora terrae; addidit et fontes et stagna inmensa lacusque fluminaque obliquis cinxit declivia ripis, quae, diversa locis, partim sorbentur ab ipsa, in mare perveniunt partim campoque recepta liberioris aquae pro ripis litora pulsant. iussit et extendi campos, subsidere valles, fronde tegi silvas, lapidosos surgere montes, utque duae dextra caelum totidemque sinistra parte secant zonae, quinta est ardentior illis, sic onus inclusum numero distinxit eodem cura dei, totidemque plagae tellure premuntur. Quarum quae media est, non est habitabilis aestu; nix tegit alta duas; totidem inter utramque locavit temperiemque dedit mixta cum frigore flamma.', 'So when whoever of the gods had arranged the gathered mass, he cut it and forced the divided into parts, first the earth, that it might not be unequal from any side, he gathered into the appearance of a great sphere.', '{"sic": {"meaning": "so", "grammar": "adverb"}, "ubi": {"meaning": "when", "grammar": "conjunction"}, "dispositam": {"meaning": "had arranged", "grammar": "perfect passive participle, feminine accusative singular"}, "quisquis": {"meaning": "whoever", "grammar": "indefinite pronoun, masculine nominative singular"}, "fuit": {"meaning": "had been", "grammar": "3rd person singular, pluperfect indicative of sum"}, "ille": {"meaning": "that", "grammar": "demonstrative pronoun, masculine nominative singular"}, "deorum": {"meaning": "of the gods", "grammar": "noun, masculine plural genitive"}, "congeriem": {"meaning": "the gathered", "grammar": "noun, feminine accusative singular"}, "secuit": {"meaning": "cut", "grammar": "3rd person singular, perfect indicative"}, "sectamque": {"meaning": "and divided", "grammar": "perfect passive participle, feminine accusative singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "membra": {"meaning": "parts", "grammar": "noun, neuter plural accusative"}, "coegit,": {"meaning": "forced", "grammar": "3rd person singular, perfect indicative"}, "principio": {"meaning": "first", "grammar": "adverb"}, "terram,": {"meaning": "earth", "grammar": "noun, feminine accusative singular"}, "ne": {"meaning": "that not", "grammar": "conjunction"}, "non": {"meaning": "habitabilis", "grammar": "adverb"}, "aequalis": {"meaning": "unequal", "grammar": "adjective, feminine nominative singular"}, "ab": {"meaning": "by", "grammar": "preposition + ablative"}, "omni": {"meaning": "any", "grammar": "adjective, feminine ablative singular"}, "parte": {"meaning": "in part", "grammar": "noun, feminine ablative singular"}, "foret,": {"meaning": "might be", "grammar": "3rd person singular, imperfect subjunctive of sum"}, "magni": {"meaning": "into a great", "grammar": "adjective, neutre genitive singular"}, "speciem": {"meaning": "appearance", "grammar": "noun, feminine accusative singular"}, "glomeravit": {"meaning": "he gathered", "grammar": "3rd person singular, perfect indicative"}, "orbis.": {"meaning": "sphere", "grammar": "noun, masculine genitive singular"}, "tum": {"meaning": "then", "grammar": "adverb"}, "freta": {"meaning": "the seas", "grammar": "noun, neutre plural accusative"}, "diffundi": {"meaning": "to spread", "grammar": "infinitive"}, "rapidisque": {"meaning": "and with swift", "grammar": "adjective, masculine plural ablative"}, "tumescere": {"meaning": "to swell", "grammar": "infinitive"}, "ventis": {"meaning": "winds", "grammar": "noun, masculine plural ablative"}, "iussit": {"meaning": "ordered", "grammar": "3rd person singular, perfect indicative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "ambitae": {"meaning": "surrounding", "grammar": "perfect passive participle, feminine ablative singular"}, "circumdare": {"meaning": "to surround", "grammar": "infinitive"}, "litora": {"meaning": "shores", "grammar": "noun, neutre plural accusative"}, "terrae;": {"meaning": "of the land", "grammar": "noun, feminine genitive singular"}, "addidit": {"meaning": "added", "grammar": "3rd person singular, perfect indicative"}, "fontes": {"meaning": "springs", "grammar": "noun, masculine plural accusative"}, "stagna": {"meaning": "pools", "grammar": "noun, neutre plural accusative"}, "inmensa": {"meaning": "immense", "grammar": "adjective, neutre plural accusative"}, "lacusque": {"meaning": "and lakes", "grammar": "noun, masculine plural accusative"}, "fluminaque": {"meaning": "and rivers", "grammar": "noun, neutre plural accusative"}, "obliquis": {"meaning": "with sloping", "grammar": "adjective, neutre plural ablative"}, "cinxit": {"meaning": "surrounded", "grammar": "3rd person singular, perfect indicative"}, "declivia": {"meaning": "banks", "grammar": "noun, neutre plural accusative"}, "ripis,": {"meaning": "with banks", "grammar": "noun, feminine plural ablative"}, "quae,": {"meaning": "which", "grammar": "relative pronoun, neuter plural nominative"}, "diversa": {"meaning": "different", "grammar": "adjective, neuter plural nominative"}, "locis,": {"meaning": "in places", "grammar": "noun, neutre plural ablative"}, "partim": {"meaning": "partly", "grammar": "adverb"}, "sorbentur": {"meaning": "are absorbed", "grammar": "3rd person plural, present passive"}, "ipsa,": {"meaning": "itself", "grammar": "reflexive pronoun, feminine ablative singular"}, "mare": {"meaning": "the sea", "grammar": "noun, neutre accusative singular"}, "perveniunt": {"meaning": "reach", "grammar": "3rd person plural, present indicative"}, "campoque": {"meaning": "and into the field", "grammar": "noun, neutre ablative singular"}, "recepta": {"meaning": "having been received", "grammar": "perfect passive participle, neuter ablative singular"}, "liberioris": {"meaning": "of freer", "grammar": "adjective, neuter genitive singular"}, "aquae": {"meaning": "water", "grammar": "noun, neuter genitive singular"}, "pro": {"meaning": "as", "grammar": "preposition + ablative"}, "ripis": {"meaning": "banks", "grammar": "noun, feminine plural ablative"}, "pulsant.": {"meaning": "beat", "grammar": "3rd person plural, present indicative"}, "extendi": {"meaning": "to be extended", "grammar": "infinitive passive"}, "campos,": {"meaning": "fields", "grammar": "noun, masculine plural accusative"}, "subsidere": {"meaning": "to sink", "grammar": "infinitive"}, "valles,": {"meaning": "valleys", "grammar": "noun, feminine plural accusative"}, "fronde": {"meaning": "with leaves", "grammar": "noun, feminine ablative singular"}, "tegi": {"meaning": "to be covered", "grammar": "infinitive passive"}, "silvas,": {"meaning": "forests", "grammar": "noun, feminine plural accusative"}, "lapidosos": {"meaning": "stony", "grammar": "adjective, masculine plural accusative"}, "surgere": {"meaning": "to rise", "grammar": "infinitive"}, "montes,": {"meaning": "mountains", "grammar": "noun, masculine plural accusative"}, "utque": {"meaning": "and as", "grammar": "conjunction"}, "duae": {"meaning": "two", "grammar": "adjective, feminine plural nominative"}, "dextra": {"meaning": "on the right", "grammar": "adjective, feminine plural nominative"}, "caelum": {"meaning": "the sky", "grammar": "noun, neutre accusative singular"}, "totidemque": {"meaning": "and as many", "grammar": "demonstrative adjective, neuter plural accusative"}, "sinistra": {"meaning": "on the left", "grammar": "adjective, feminine plural nominative"}, "secant": {"meaning": "cut", "grammar": "3rd person plural, present indicative"}, "zonae,": {"meaning": "zones", "grammar": "noun, feminine plural nominative"}, "quinta": {"meaning": "the fifth", "grammar": "adjective, feminine nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "ardentior": {"meaning": "hotter", "grammar": "adjective, feminine nominative singular"}, "illis,": {"meaning": "than these", "grammar": "demonstrative pronoun, feminine plural ablative"}, "onus": {"meaning": "burden", "grammar": "noun, neutre accusative singular"}, "inclusum": {"meaning": "enclosed", "grammar": "perfect passive participle, neuter accusative singular"}, "numero": {"meaning": "with the same number", "grammar": "noun, neutre ablative singular"}, "distinxit": {"meaning": "distinguished", "grammar": "3rd person singular, perfect indicative"}, "eodem": {"meaning": "by the same", "grammar": "adjective, neutre ablative singular"}, "cura": {"meaning": "by the care", "grammar": "noun, feminine ablative singular"}, "dei,": {"meaning": "of god", "grammar": "noun, masculine genitive singular"}, "plagae": {"meaning": "regions", "grammar": "noun, feminine plural nominative"}, "tellure": {"meaning": "by the earth", "grammar": "noun, feminine ablative singular"}, "premuntur.": {"meaning": "are pressed", "grammar": "3rd person plural, present passive"}, "quarum": {"meaning": "of which", "grammar": "relative pronoun, feminine plural genitive"}, "quae": {"meaning": "the middle", "grammar": "relative pronoun, feminine nominative singular"}, "media": {"meaning": "is", "grammar": "adjective, feminine nominative singular"}, "est,": {"meaning": "not", "grammar": "3rd person singular, present indicative of sum"}, "aestu;": {"meaning": "because of heat", "grammar": "noun, masculine ablative singular"}, "nix": {"meaning": "snow", "grammar": "noun, feminine nominative singular"}, "tegit": {"meaning": "covers", "grammar": "3rd person singular, present indicative"}, "alta": {"meaning": "high", "grammar": "adjective, feminine plural accusative"}, "duas;": {"meaning": "two", "grammar": "adjective, feminine plural accusative"}, "totidem": {"meaning": "between each", "grammar": "adverb"}, "inter": {"meaning": "of the two", "grammar": "preposition + accusative"}, "utramque": {"meaning": "he placed", "grammar": "pronoun, feminine accusative singular"}, "locavit": {"meaning": "with the same", "grammar": "3rd person singular, perfect indicative"}, "temperiemque": {"meaning": "and temperate", "grammar": "noun, feminine accusative singular"}, "dedit": {"meaning": "air", "grammar": "3rd person singular, perfect indicative"}, "mixta": {"meaning": "mixed", "grammar": "noun, feminine accusative singular"}, "cum": {"meaning": "with", "grammar": "perfect passive participle, feminine ablative singular"}, "frigore": {"meaning": "cold", "grammar": "preposition + ablative"}, "flamma.": {"meaning": "flame", "grammar": "noun, neutre ablative singular"}}', '- Perfect passive participle "dispositam, sectam, recepta, inclusum, mixta" - Purpose clause "ne...foret" with subjunctive - Accusative with infinitive "freta...diffundi, tumescere" - Ablative of separation "ab ipsa" - Comparative adjective "ardentior" with ablative "illis" - Perfect passive participles with instrumental meaning', 'Ovid describes the divine organization of geography and climate, creating the five world zones that will later determine where human civilizations develop.', 118, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-011', 'Caesar Gallic War 1.7-8', 'Caesari cum id nuntiatum esset, eos per provinciam nostram iter facere conari, maturat ab urbe proficisci et quam maximis potest itineribus in Galliam ulteriorem contendit et ad Genavam pervenit. Provinciae toti quam maximum potest militum numerum imperat (erat omnino in Gallia ulteriore legio una), pontem, qui erat ad Genavam, iubet rescindi. Ubi de eius adventu Helvetii certiores facti sunt, legatos ad eum mittunt nobilissimos civitatis, cuius legationis Nammeius et Verucloetius principem locum obtinebant, qui dicerent sibi esse in animo sine ullo maleficio iter per provinciam facere, propterea quod aliud iter haberent nullum: rogare ut eius voluntate id sibi facere liceat. Caesar, quod memoria tenebat L. Cassium consulem occisum exercitumque eius ab Helvetiis pulsum et sub iugum missum, concedendum non putabat; neque homines inimico animo, data facultate per provinciam itineris faciundi, temperaturos ab iniuria et maleficio existimabat.', 'When this had been reported to Caesar, that they were attempting to march through our province, he hastened to depart from the city and hurried into further Gaul by the greatest possible marches and reached Geneva. He orders the greatest possible number of soldiers for the entire province (there was altogether one legion in further Gaul), and orders the bridge, which was at Geneva, to be destroyed. When the Helvetii were made certain of his arrival, they send as ambassadors the most noble men of the state, of whose embassy Nammeius and Verucloetius held the first place, who said that they had it in mind to march through the province without any injury, because they had no other way: asking that with his consent they might do this. Caesar, because he remembered that L. Cassius the consul had been slain and his army driven by the Helvetii and sent under the yoke, did not think it should be granted; nor did he think that men, with an enemy spirit, given the faculty of marching through the province, would refrain from injury and crime.', '{"caesari": {"meaning": "to Caesar", "grammar": "proper noun, masculine dative singular"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "id": {"meaning": "this", "grammar": "demonstrative pronoun, neutre accusative singular"}, "nuntiatum": {"meaning": "reported", "grammar": "perfect passive participle, neutre accusative singular"}, "esset,": {"meaning": "had been", "grammar": "3rd person singular, imperfect subjunctive"}, "eos": {"meaning": "they", "grammar": "demonstrative pronoun, masculine accusative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "provinciam": {"meaning": "the province", "grammar": "noun, feminine accusative singular"}, "nostram": {"meaning": "our", "grammar": "possessive adjective, feminine accusative singular"}, "iter": {"meaning": "way", "grammar": "noun, neutre accusative singular"}, "facere": {"meaning": "to do", "grammar": "infinitive"}, "conari,": {"meaning": "to be attempting", "grammar": "infinitive"}, "maturat": {"meaning": "he hastened", "grammar": "3rd person singular, present indicative"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "urbe": {"meaning": "from the city", "grammar": "noun, feminine ablative singular"}, "proficisci": {"meaning": "to depart", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quam": {"meaning": "the greatest", "grammar": "adverb"}, "maximis": {"meaning": "greatest", "grammar": "adjective, neutre plural ablative"}, "potest": {"meaning": "he can", "grammar": "3rd person singular, present indicative"}, "itineribus": {"meaning": "by marches", "grammar": "noun, neutre plural ablative"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "galliam": {"meaning": "Gaul", "grammar": "proper noun, feminine accusative singular"}, "ulteriorem": {"meaning": "further", "grammar": "adjective, feminine accusative singular"}, "contendit": {"meaning": "he hurried", "grammar": "3rd person singular, present indicative"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "genavam": {"meaning": "Geneva", "grammar": "proper noun, feminine accusative singular"}, "pervenit.": {"meaning": "reached", "grammar": "3rd person singular, perfect indicative"}, "provinciae": {"meaning": "for the province", "grammar": "noun, feminine genitive singular"}, "toti": {"meaning": "entire", "grammar": "adjective, feminine dative singular"}, "maximum": {"meaning": "possible", "grammar": "adjective, neutre accusative singular"}, "militum": {"meaning": "of soldiers", "grammar": "noun, masculine plural genitive"}, "numerum": {"meaning": "number", "grammar": "noun, masculine accusative singular"}, "imperat": {"meaning": "orders", "grammar": "3rd person singular, present indicative"}, "(erat": {"meaning": "there was", "grammar": "3rd person singular, imperfect indicative of sum"}, "omnino": {"meaning": "altogether", "grammar": "adverb"}, "gallia": {"meaning": "Gaul", "grammar": "proper noun, feminine ablative singular"}, "ulteriore": {"meaning": "further", "grammar": "adjective, feminine ablative singular"}, "legio": {"meaning": "legion", "grammar": "noun, feminine nominative singular"}, "una),": {"meaning": "one", "grammar": "adjective, feminine nominative singular"}, "pontem,": {"meaning": "the bridge", "grammar": "noun, masculine accusative singular"}, "qui": {"meaning": "who", "grammar": "relative pronoun, masculine nominative plural"}, "erat": {"meaning": "was", "grammar": "3rd person singular, imperfect indicative of sum"}, "genavam,": {"meaning": "Geneva", "grammar": "proper noun, feminine accusative singular"}, "iubet": {"meaning": "orders", "grammar": "3rd person singular, present indicative"}, "rescindi.": {"meaning": "to be destroyed", "grammar": "infinitive passive"}, "ubi": {"meaning": "when", "grammar": "conjunction"}, "de": {"meaning": "of", "grammar": "preposition + ablative"}, "eius": {"meaning": "his", "grammar": "possessive adjective, neutre genitive singular"}, "adventu": {"meaning": "arrival", "grammar": "noun, masculine ablative singular"}, "helvetii": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural nominative"}, "certiores": {"meaning": "more certain", "grammar": "adjective, masculine plural nominative"}, "facti": {"meaning": "were made", "grammar": "perfect passive participle, masculine plural nominative"}, "sunt,": {"meaning": "they are", "grammar": "3rd person plural, present indicative of sum"}, "legatos": {"meaning": "as ambassadors", "grammar": "noun, masculine plural accusative"}, "eum": {"meaning": "him", "grammar": "demonstrative pronoun, masculine accusative singular"}, "mittunt": {"meaning": "send", "grammar": "3rd person plural, present indicative"}, "nobilissimos": {"meaning": "most noble", "grammar": "adjective, masculine plural accusative"}, "civitatis,": {"meaning": "of the state", "grammar": "noun, feminine genitive singular"}, "cuius": {"meaning": "of whose", "grammar": "relative pronoun, feminine genitive singular"}, "legationis": {"meaning": "of the embassy", "grammar": "noun, feminine genitive singular"}, "nammeius": {"meaning": "Nammeius", "grammar": "proper noun, masculine nominative singular"}, "verucloetius": {"meaning": "Verucloetius", "grammar": "proper noun, masculine nominative singular"}, "principem": {"meaning": "first", "grammar": "adjective, masculine accusative singular"}, "locum": {"meaning": "place", "grammar": "noun, masculine accusative singular"}, "obtinebant,": {"meaning": "held", "grammar": "3rd person plural, imperfect indicative"}, "dicerent": {"meaning": "said", "grammar": "3rd person plural, imperfect subjunctive"}, "sibi": {"meaning": "they", "grammar": "reflexive pronoun, dative singular"}, "esse": {"meaning": "it was", "grammar": "infinitive"}, "animo": {"meaning": "mind", "grammar": "noun, neutre ablative singular"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "ullo": {"meaning": "any", "grammar": "adjective, neutre ablative singular"}, "maleficio": {"meaning": "and crime", "grammar": "noun, neutre ablative singular"}, "facere,": {"meaning": "to make", "grammar": "infinitive"}, "propterea": {"meaning": "because", "grammar": "adverb"}, "quod": {"meaning": "because", "grammar": "conjunction"}, "aliud": {"meaning": "other", "grammar": "adjective, neutre accusative singular"}, "haberent": {"meaning": "they had", "grammar": "3rd person plural, imperfect subjunctive"}, "nullum:": {"meaning": "no", "grammar": "adjective, neutre accusative singular"}, "rogare": {"meaning": "asking", "grammar": "infinitive"}, "ut": {"meaning": "that", "grammar": "conjunction"}, "voluntate": {"meaning": "with his consent", "grammar": "noun, feminine ablative singular"}, "liceat.": {"meaning": "they might", "grammar": "3rd person singular, present subjunctive"}, "caesar,": {"meaning": "Caesar", "grammar": "proper noun, masculine nominative singular"}, "memoria": {"meaning": "memory", "grammar": "noun, feminine ablative singular"}, "tenebat": {"meaning": "he remembered", "grammar": "3rd person singular, imperfect indicative"}, "l.": {"meaning": "Lucius", "grammar": "abbreviation"}, "cassium": {"meaning": "Cassius", "grammar": "proper noun, masculine accusative singular"}, "consulem": {"meaning": "the consul", "grammar": "noun, masculine accusative singular"}, "occisum": {"meaning": "had been slain", "grammar": "perfect passive participle, masculine accusative singular"}, "exercitumque": {"meaning": "and his army", "grammar": "noun, neutre accusative singular"}, "helvetiis": {"meaning": "the Helvetii", "grammar": "proper noun, masculine plural ablative"}, "pulsum": {"meaning": "driven", "grammar": "perfect passive participle, masculine accusative singular"}, "sub": {"meaning": "under", "grammar": "preposition + accusative"}, "iugum": {"meaning": "the yoke", "grammar": "noun, neutre accusative singular"}, "missum,": {"meaning": "sent", "grammar": "perfect passive participle, masculine accusative singular"}, "concedendum": {"meaning": "it should be granted", "grammar": "gerundive, neutre accusative singular"}, "non": {"meaning": "not", "grammar": "adverb"}, "putabat;": {"meaning": "he did not think", "grammar": "3rd person singular, imperfect indicative"}, "neque": {"meaning": "nor", "grammar": "conjunction"}, "homines": {"meaning": "men", "grammar": "noun, masculine plural accusative"}, "inimico": {"meaning": "with enemy", "grammar": "adjective, neutre ablative singular"}, "animo,": {"meaning": "spirit", "grammar": "noun, neutre ablative singular"}, "data": {"meaning": "with the faculty", "grammar": "perfect passive participle, feminine ablative singular"}, "facultate": {"meaning": "given", "grammar": "noun, feminine ablative singular"}, "itineris": {"meaning": "of marching", "grammar": "noun, neutre genitive singular"}, "faciundi,": {"meaning": "to be made", "grammar": "gerundive, neutre genitive singular"}, "temperaturos": {"meaning": "they would refrain", "grammar": "future active participle, masculine plural accusative"}, "iniuria": {"meaning": "injury", "grammar": "noun, feminine ablative singular"}, "existimabat.": {"meaning": "he thought", "grammar": "3rd person singular, imperfect indicative"}}', '- Temporal clause "Caesari cum id nuntiatum esset" - Accusative with infinitive "eos...iter facere conari" - Relative clause "qui...obtinebant" - Subjunctive in indirect speech "dicerent" - Perfect passive participles "facti, occisum, pulsum, missum, data" - Gerundive "concedendum, faciundi" - Ablative absolute "memoria tenebat"', 'Caesar''s first direct encounter with the Helvetii, showing his military preparedness and historical awareness that guides his strategic decisions.', 158, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-012', 'Catullus Poem 31.1-8', 'Paene insularum, Sirmio, insularumque ocelle, quascumque in liquentibus stagnis marique vasto fert uterque Neptunus, quam te libenter quamque laetus inviso, vix mi ipse credens Thuniam atque Bithynos liquisse campos et videre te in tuto. O quid solutis est beatius curis, cum mens onus reponit, ac peregrino labore fessi venimus larem ad nostrum, desideratoque acquiescimus lecto? Hoc est quod unum est pro laboribus tantis. Salve, o venusta Sirmio, atque ero gaude gaudente, vosque, o Lydiae lacus undae, ridete quidquid est domi cachinnorum.', 'Almost of islands, Sirmio, and of islands'' little eye,', '{"paene": {"meaning": "almost", "grammar": "adverb"}, "insularum,": {"meaning": "of islands", "grammar": "noun, feminine plural genitive"}, "sirmio,": {"meaning": "Sirmio", "grammar": "proper noun, feminine vocative"}, "insularumque": {"meaning": "and of islands''", "grammar": "noun, feminine plural genitive"}, "ocelle,": {"meaning": "little eye", "grammar": "noun, neutre vocative singular"}, "quascumque": {"meaning": "which so many", "grammar": "relative pronoun, feminine plural accusative"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "liquentibus": {"meaning": "in liquid", "grammar": "adjective, neutre plural ablative"}, "stagnis": {"meaning": "lakes", "grammar": "noun, neutre plural ablative"}, "marique": {"meaning": "and in the sea", "grammar": "noun, neutre ablative singular"}, "vasto": {"meaning": "in vast", "grammar": "adjective, neutre ablative singular"}, "fert": {"meaning": "carries", "grammar": "3rd person singular, present indicative"}, "uterque": {"meaning": "the two", "grammar": "adjective, masculine nominative singular"}, "neptunus,": {"meaning": "Neptunes", "grammar": "noun, masculine plural nominative"}, "quam": {"meaning": "how", "grammar": "interrogative adverb"}, "te": {"meaning": "you", "grammar": "personal pronoun, accusative singular"}, "libenter": {"meaning": "gladly", "grammar": "adverb"}, "quamque": {"meaning": "and how", "grammar": "interrogative adverb"}, "laetus": {"meaning": "joyfully", "grammar": "adverb"}, "inviso,": {"meaning": "I see", "grammar": "1st person singular, present indicative"}, "vix": {"meaning": "hardly", "grammar": "adverb"}, "mi": {"meaning": "to myself", "grammar": "personal pronoun, dative singular"}, "ipse": {"meaning": "myself", "grammar": "reflexive pronoun, nominative singular"}, "credens": {"meaning": "believing", "grammar": "present participle, masculine nominative singular"}, "thuniam": {"meaning": "Thynia", "grammar": "proper noun, feminine accusative singular"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "bithynos": {"meaning": "Bithynian", "grammar": "adjective, feminine plural accusative"}, "liquisse": {"meaning": "to have left", "grammar": "infinitive perfect"}, "campos": {"meaning": "fields", "grammar": "noun, masculine plural accusative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "videre": {"meaning": "to see", "grammar": "infinitive"}, "tuto.": {"meaning": "in safety", "grammar": "adjective, neutre ablative singular"}, "o": {"meaning": "o", "grammar": "interjection"}, "quid": {"meaning": "what", "grammar": "interrogative pronoun, neutre accusative singular"}, "solutis": {"meaning": "set aside", "grammar": "perfect passive participle, feminine plural ablative"}, "est": {"meaning": "there is", "grammar": "3rd person singular, present indicative of sum"}, "beatius": {"meaning": "more blessed", "grammar": "adjective, neutre accusative singular comparative"}, "curis,": {"meaning": "than care", "grammar": "noun, feminine plural ablative"}, "cum": {"meaning": "when", "grammar": "conjunction"}, "mens": {"meaning": "the mind", "grammar": "noun, feminine nominative singular"}, "onus": {"meaning": "its burden", "grammar": "noun, neutre accusative singular"}, "reponit,": {"meaning": "lays down", "grammar": "3rd person singular, present indicative"}, "ac": {"meaning": "and", "grammar": "conjunction"}, "peregrino": {"meaning": "from foreign", "grammar": "adjective, neutre ablative singular"}, "labore": {"meaning": "having wearied", "grammar": "noun, neutre ablative singular"}, "fessi": {"meaning": "wearied", "grammar": "adjective, masculine plural nominative"}, "venimus": {"meaning": "we come", "grammar": "1st person plural, present indicative"}, "larem": {"meaning": "to our household god", "grammar": "noun, masculine accusative singular"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "nostrum,": {"meaning": "our", "grammar": "possessive adjective, neutre accusative singular"}, "desideratoque": {"meaning": "and on the desired", "grammar": "perfect passive participle, neutre ablative singular"}, "acquiescimus": {"meaning": "we rest", "grammar": "1st person plural, present indicative"}, "lecto?": {"meaning": "bed", "grammar": "noun, neutre ablative singular"}, "hoc": {"meaning": "this", "grammar": "demonstrative adjective, neutre accusative singular"}, "quod": {"meaning": "the one", "grammar": "demonstrative pronoun, neutre accusative singular"}, "unum": {"meaning": "thing", "grammar": "adjective, neutre accusative singular"}, "pro": {"meaning": "such great", "grammar": "preposition + ablative"}, "laboribus": {"meaning": "labors", "grammar": "noun, masculine plural ablative"}, "tantis.": {"meaning": "is", "grammar": "3rd person singular, present indicative of sum"}, "salve,": {"meaning": "Hail", "grammar": "interjection"}, "venusta": {"meaning": "lovely", "grammar": "adjective, feminine vocative"}, "ero": {"meaning": "be", "grammar": "2nd person singular, future imperative"}, "gaude": {"meaning": "joyful", "grammar": "noun, masculine vocative"}, "gaudente,": {"meaning": "who is joyful", "grammar": "present participle, masculine ablative singular"}, "vosque,": {"meaning": "and you", "grammar": "personal pronoun, vocative plural"}, "lydiae": {"meaning": "Lydian", "grammar": "adjective, feminine genitive singular"}, "lacus": {"meaning": "of the lake", "grammar": "noun, masculine genitive singular"}, "undae,": {"meaning": "waves", "grammar": "noun, feminine plural vocative"}, "ridete": {"meaning": "smile", "grammar": "2nd person plural, present imperative"}, "quidquid": {"meaning": "whatever", "grammar": "indefinite pronoun, neutre nominative singular"}, "domi": {"meaning": "at home", "grammar": "adverb"}, "cachinnorum.": {"meaning": "of laughter", "grammar": "noun, neutre plural genitive"}}', '- Vocative "Sirmio" in apostrophe - Genitive of quality "insularum ocelle" - Relative clause "quascumque...fert" - Infinitive perfect "liquisse" in indirect statement - Comparative adjective "beatius" - Perfect passive participle "solutis, desiderato" - Future imperative "ero gaude"', 'Catullus'' homecoming poem celebrates his return to his beloved villa, expressing the joy of coming home after arduous journeys and separations from loved ones.', 98, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-013', 'Ovid Metamorphoses 1.1-4', 'In nova fert animus mutatas dicere formas corpora; di, coeptis, nam vos mutastis et illas, aspirate meis primaque ab origine mundi ad mea perpetuum deducite tempora carmen.', 'My mind is bent to tell of bodies changed into new forms; gods, for you have changed those things too, inspire my undertakings and draw down an unbroken song from the first beginning of the world to my own times.', '{"in": {"meaning": "into", "grammar": "preposition + accusative"}, "nova": {"meaning": "new", "grammar": "adjective, neuter plural accusative"}, "fert": {"meaning": "carries/bears", "grammar": "3rd person singular present"}, "animus": {"meaning": "mind/spirit", "grammar": "nominative singular"}, "mutatas": {"meaning": "changed", "grammar": "perfect passive participle, feminine plural accusative"}, "dicere": {"meaning": "to tell/speak", "grammar": "infinitive"}, "formas": {"meaning": "forms/shapes", "grammar": "accusative plural"}, "corpora": {"meaning": "bodies", "grammar": "nominative/accusative plural"}, "di": {"meaning": "gods", "grammar": "nominative plural, vocative"}, "coeptis": {"meaning": "undertakings", "grammar": "dative/ablative plural"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "vos": {"meaning": "you", "grammar": "nominative plural"}, "mutastis": {"meaning": "you have changed", "grammar": "2nd person plural perfect"}, "et": {"meaning": "and", "grammar": "conjunction"}, "illas": {"meaning": "those", "grammar": "feminine accusative plural"}, "aspirate": {"meaning": "inspire/breathe upon", "grammar": "2nd person plural imperative"}, "meis": {"meaning": "my", "grammar": "dative/ablative plural"}, "primaque": {"meaning": "and first", "grammar": "feminine nominative/ablative singular"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "origine": {"meaning": "beginning/origin", "grammar": "ablative singular"}, "mundi": {"meaning": "of the world", "grammar": "genitive singular"}, "ad": {"meaning": "to/towards", "grammar": "preposition + accusative"}, "mea": {"meaning": "my", "grammar": "neuter plural accusative"}, "perpetuum": {"meaning": "unbroken/continuous", "grammar": "neuter accusative singular"}, "deducite": {"meaning": "draw down/lead down", "grammar": "2nd person plural imperative"}, "tempora": {"meaning": "times", "grammar": "neuter plural accusative"}, "carmen": {"meaning": "song/poem", "grammar": "neuter accusative singular"}}', '- **Infinitive of purpose**: "dicere" expresses the purpose of the mind''s inclination - **Perfect passive participle**: "mutatas" agrees with "formas" in accusative - **Vocative of direct address**: "di" calls upon the gods directly - **Causal conjunction**: "nam" introduces explanation for the invocation - **Imperative mood**: "aspirate" and "deducite" are commands to the gods - **Temporal expressions**: "primaque ab origine mundi ad mea tempora" shows extensive time span - **Transferred epithet**: "perpetuum carmen" - the song is continuous, not the times', 'This is the famous opening invocation of Ovid''s Metamorphoses, one of the most influential works of Latin literature. Ovid announces his theme of transformation and calls upon the gods for inspiration, following the epic tradition established by Homer and Vergil.', 28, 'Vergil', 'Metamorphoses');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-014', 'Caesar De Bello Gallico 1.1', 'Gallia est omnis divisa in partes tres, quarum unam incolunt Belgae, aliam Aquitani, tertiam qui ipsorum lingua Celtae, nostra Galli appellantur.', 'All Gaul is divided into three parts, one of which the Belgae inhabit, another the Aquitani, the third those who in their own language are called Celts, in our language Gauls.', '{"Gallia": {"meaning": "Gaul", "grammar": "nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}, "omnis": {"meaning": "all/whole", "grammar": "nominative singular"}, "divisa": {"meaning": "divided", "grammar": "perfect passive participle, nominative singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "partes": {"meaning": "parts", "grammar": "accusative plural"}, "tres": {"meaning": "three", "grammar": "accusative plural"}, "quarum": {"meaning": "of which", "grammar": "genitive plural relative pronoun"}, "unam": {"meaning": "one", "grammar": "accusative singular"}, "incolunt": {"meaning": "they inhabit", "grammar": "3rd person plural present"}, "Belgae": {"meaning": "the Belgae", "grammar": "nominative plural"}, "aliam": {"meaning": "another", "grammar": "accusative singular"}, "Aquitani": {"meaning": "the Aquitani", "grammar": "nominative plural"}, "tertiam": {"meaning": "third", "grammar": "accusative singular"}, "qui": {"meaning": "who", "grammar": "nominative plural relative pronoun"}, "ipsorum": {"meaning": "of themselves/their own", "grammar": "genitive plural"}, "lingua": {"meaning": "language", "grammar": "ablative singular"}, "Celtae": {"meaning": "Celts", "grammar": "nominative plural"}, "nostra": {"meaning": "our", "grammar": "ablative singular"}, "Galli": {"meaning": "Gauls", "grammar": "nominative plural"}, "appellantur": {"meaning": "they are called", "grammar": "3rd person plural present passive"}}', '- **Perfect passive participle**: "divisa" functions as predicate adjective with "est" - **Relative clauses**: "quarum" introduces relative clause describing the parts - **Substantive adjectives**: "unam, aliam, tertiam" function as nouns (one, another, third) - **Ablative of respect**: "lingua" shows in what respect they are called - **Passive voice**: "appellantur" shows how they are named by others - **Parallel structure**: Three groups presented in balanced clauses - **Ellipsis**: "partem" understood with "unam, aliam, tertiam"', 'This is the famous opening line of Caesar''s Commentaries on the Gallic Wars, providing ethnographic information about the tribes of Gaul before describing his military campaigns. The tripartite division became a standard way of understanding Gallic geography.', 26, 'Ovid', 'De Bello Gallico');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-015', 'Cicero Pro Archia 12', 'Quare quidni purissimis studiis et artibus, quibus ipsa natura humana delectatur, animum ad haec studia convertamus?', 'Therefore, why should we not turn our minds to these studies and to the purest pursuits and arts, in which human nature itself delights?', '{"quare": {"meaning": "therefore/why", "grammar": "interrogative adverb"}, "quidni": {"meaning": "why not", "grammar": "compound interrogative"}, "purissimis": {"meaning": "purest", "grammar": "superlative adjective, ablative plural"}, "studiis": {"meaning": "studies/pursuits", "grammar": "ablative plural"}, "et": {"meaning": "and", "grammar": "conjunction"}, "artibus": {"meaning": "arts", "grammar": "ablative plural"}, "quibus": {"meaning": "in which", "grammar": "ablative plural relative pronoun"}, "ipsa": {"meaning": "itself", "grammar": "emphatic pronoun, nominative singular"}, "natura": {"meaning": "nature", "grammar": "nominative singular"}, "humana": {"meaning": "human", "grammar": "adjective, nominative singular"}, "delectatur": {"meaning": "delights/takes pleasure", "grammar": "3rd person singular present deponent"}, "animum": {"meaning": "mind", "grammar": "accusative singular"}, "ad": {"meaning": "to/towards", "grammar": "preposition + accusative"}, "haec": {"meaning": "these", "grammar": "neuter plural accusative"}, "studia": {"meaning": "studies", "grammar": "neuter plural accusative"}, "convertamus": {"meaning": "we should turn", "grammar": "1st person plural present subjunctive"}}', '- **Rhetorical question**: "quidni" introduces emphatic interrogative expecting positive answer - **Superlative adjective**: "purissimis" emphasizes moral excellence of studies - **Ablative with preposition**: "studiis et artibus" shows means or manner - **Relative clause**: "quibus" refers back to "studiis et artibus" - **Deponent verb**: "delectatur" has passive form but active meaning - **Deliberative subjunctive**: "convertamus" in indirect question - **Emphatic positioning**: "ipsa natura humana" stresses natural human inclination', 'From Cicero''s speech defending the poet Archias, this passage argues for the value of liberal education and literary studies as natural to human beings. Cicero defends the importance of the humanities in Roman culture.', 16, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-016', 'Vergil Georgics 1.1-5', 'Quid faciat laetas segetes, quo sidere terram vertere, Maecenas, ulmisque adiungere vites conveniat, quae cura boum, quis cultus habendo pecori, apibus quanta experientia parcis, hinc canere incipiam.', 'What makes the crops joyful, under what star it is fitting to turn the earth, Maecenas, and to join vines to elms, what care of cattle, what cultivation in keeping flocks, how much experience in managing frugal bees - of these things I shall begin to sing.', '{"quid": {"meaning": "what", "grammar": "interrogative pronoun"}, "faciat": {"meaning": "makes", "grammar": "3rd person singular present subjunctive"}, "laetas": {"meaning": "joyful/fertile", "grammar": "accusative plural"}, "segetes": {"meaning": "crops", "grammar": "accusative plural"}, "quo": {"meaning": "under what/which", "grammar": "ablative singular interrogative"}, "sidere": {"meaning": "star/constellation", "grammar": "ablative singular"}, "terram": {"meaning": "earth/land", "grammar": "accusative singular"}, "vertere": {"meaning": "to turn/plow", "grammar": "infinitive"}, "Maecenas": {"meaning": "Maecenas", "grammar": "vocative"}, "ulmisque": {"meaning": "and to elms", "grammar": "dative plural with -que"}, "adiungere": {"meaning": "to join/attach", "grammar": "infinitive"}, "vites": {"meaning": "vines", "grammar": "accusative plural"}, "conveniat": {"meaning": "it is fitting", "grammar": "3rd person singular present subjunctive"}, "quae": {"meaning": "what", "grammar": "nominative singular interrogative"}, "cura": {"meaning": "care", "grammar": "nominative singular"}, "boum": {"meaning": "of cattle", "grammar": "genitive plural"}, "quis": {"meaning": "what", "grammar": "nominative singular interrogative"}, "cultus": {"meaning": "cultivation", "grammar": "nominative singular"}, "habendo": {"meaning": "in keeping", "grammar": "gerund, ablative"}, "pecori": {"meaning": "to flocks", "grammar": "dative singular"}, "apibus": {"meaning": "to bees", "grammar": "dative plural"}, "quanta": {"meaning": "how much", "grammar": "nominative singular interrogative"}, "experientia": {"meaning": "experience", "grammar": "nominative singular"}, "parcis": {"meaning": "frugal/thrifty", "grammar": "dative plural"}, "hinc": {"meaning": "from here/of these", "grammar": "adverb"}, "canere": {"meaning": "to sing", "grammar": "infinitive"}, "incipiam": {"meaning": "I shall begin", "grammar": "1st person singular future"}}', '- **Indirect questions**: Multiple "quid, quo, quae, quis, quanta" clauses - **Subjunctive in indirect question**: "faciat, conveniat" - **Gerund**: "habendo" expresses means or manner - **Future indicative**: "incipiam" states definite intention - **Dative of advantage**: "pecori, apibus" shows who benefits - **Vocative address**: "Maecenas" addresses the patron - **Correlative structure**: "hinc...incipiam" connects topics to intention', 'Opening of Vergil''s Georgics, a didactic poem on agriculture dedicated to Maecenas, Augustus''s minister. This work promotes the value of farming and rural life as fundamental to Roman identity and prosperity.', 25, 'Vergil', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-017', 'Horace Odes 1.11', 'Tu ne quaesieris, scire nefas, quem mihi, quem tibi finem di dederint, Leuconoe, nec Babylonios temptaris numeros. ut melius, quidquid erit, pati!', 'Do not ask, it is wrong to know, what end the gods have given to me, what to you, Leuconoe, nor attempt Babylonian calculations. How much better to endure whatever will be!', '{"tu": {"meaning": "you", "grammar": "nominative"}, "ne": {"meaning": "not", "grammar": "negative imperative particle"}, "quaesieris": {"meaning": "you should ask", "grammar": "2nd person singular perfect subjunctive"}, "scire": {"meaning": "to know", "grammar": "infinitive"}, "nefas": {"meaning": "wrong/impious", "grammar": "indeclinable noun"}, "quem": {"meaning": "what", "grammar": "accusative singular interrogative"}, "mihi": {"meaning": "to me", "grammar": "dative"}, "tibi": {"meaning": "to you", "grammar": "dative"}, "finem": {"meaning": "end", "grammar": "accusative singular"}, "di": {"meaning": "gods", "grammar": "nominative plural"}, "dederint": {"meaning": "they have given", "grammar": "3rd person plural perfect subjunctive"}, "Leuconoe": {"meaning": "Leuconoe", "grammar": "vocative"}, "nec": {"meaning": "nor", "grammar": "conjunction"}, "Babylonios": {"meaning": "Babylonian", "grammar": "accusative plural"}, "temptaris": {"meaning": "you should attempt", "grammar": "2nd person singular present subjunctive"}, "numeros": {"meaning": "numbers/calculations", "grammar": "accusative plural"}, "ut": {"meaning": "how", "grammar": "exclamatory"}, "melius": {"meaning": "better", "grammar": "comparative adverb"}, "quidquid": {"meaning": "whatever", "grammar": "indefinite pronoun"}, "erit": {"meaning": "it will be", "grammar": "3rd person singular future"}, "pati": {"meaning": "to endure", "grammar": "infinitive"}}', '- **Negative purpose clause**: "ne quaesieris" - prohibition with subjunctive - **Substantive infinitive**: "scire" as subject of "nefas" - **Indirect question**: "quem...finem" in subjunctive - **Perfect subjunctive**: "dederint" in indirect question - **Negative imperative**: "nec temptaris" continues prohibition - **Exclamatory expression**: "ut melius" + infinitive - **Indefinite relative**: "quidquid erit" - whatever may happen', 'Famous carpe diem poem by Horace advising against fortune-telling and astrology (Babylonian numbers), promoting acceptance of uncertainty and focus on the present moment. Reflects Epicurean philosophy.', 18, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-018', 'Livy Ab Urbe Condita 1.1.1', 'Facturusne operae pretium sim, si a primordio urbis res populi Romani perscripserim, nec satis scio nec, si sciam, dicere ausim.', 'Whether I shall accomplish something worthwhile, if I write out the history of the Roman people from the beginning of the city, I neither know sufficiently nor, if I did know, would I dare to say.', '{"facturusne": {"meaning": "whether I am about to do", "grammar": "future active participle + -ne"}, "operae": {"meaning": "of work/effort", "grammar": "genitive singular"}, "pretium": {"meaning": "price/worth", "grammar": "accusative singular"}, "sim": {"meaning": "I might be", "grammar": "1st person singular present subjunctive"}, "si": {"meaning": "if", "grammar": "conditional conjunction"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "primordio": {"meaning": "beginning", "grammar": "ablative singular"}, "urbis": {"meaning": "of the city", "grammar": "genitive singular"}, "res": {"meaning": "things/history", "grammar": "accusative plural"}, "populi": {"meaning": "of the people", "grammar": "genitive singular"}, "Romani": {"meaning": "Roman", "grammar": "genitive singular"}, "perscripserim": {"meaning": "I shall have written out", "grammar": "1st person singular future perfect"}, "nec": {"meaning": "neither", "grammar": "conjunction"}, "satis": {"meaning": "sufficiently", "grammar": "adverb"}, "scio": {"meaning": "I know", "grammar": "1st person singular present"}, "sciam": {"meaning": "I should know", "grammar": "1st person singular present subjunctive"}, "dicere": {"meaning": "to say", "grammar": "infinitive"}, "ausim": {"meaning": "I would dare", "grammar": "1st person singular present subjunctive"}}', '- **Indirect question**: "facturusne...sim" with subjunctive - **Future active participle**: "facturus" indicates intended action - **Genitive of value**: "operae pretium" - worth the effort - **Future perfect in temporal clause**: "perscripserim" - **Conditional sentences**: Mixed conditions with indicative and subjunctive - **Complementary infinitive**: "dicere" completes "ausim" - **Rhetorical uncertainty**: Author''s modesty topos', 'Opening of Livy''s monumental history of Rome, expressing the historian''s uncertainty about undertaking such a vast project. Reflects the Roman tradition of historical writing and authorial modesty.', 19, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-019', 'Tacitus Annales 1.1', 'Urbem Romam a principio reges habuere; libertatem et consulatum L. Brutus instituit.', 'Kings held the city of Rome from the beginning; Lucius Brutus established freedom and the consulship.', '{"urbem": {"meaning": "city", "grammar": "accusative singular"}, "Romam": {"meaning": "Rome", "grammar": "accusative singular"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "principio": {"meaning": "beginning", "grammar": "ablative singular"}, "reges": {"meaning": "kings", "grammar": "nominative plural"}, "habuere": {"meaning": "they held", "grammar": "3rd person plural perfect"}, "libertatem": {"meaning": "freedom", "grammar": "accusative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "consulatum": {"meaning": "consulship", "grammar": "accusative singular"}, "L.": {"meaning": "Lucius", "grammar": "abbreviation"}, "Brutus": {"meaning": "Brutus", "grammar": "nominative singular"}, "instituit": {"meaning": "he established", "grammar": "3rd person singular perfect"}}', '- **Direct object**: "urbem Romam" in accusative - **Ablative of time**: "a principio" shows starting point - **Perfect tense**: "habuere" and "instituit" for completed historical actions - **Zeugma**: "libertatem et consulatum" - two different types of objects with one verb - **Parallel structure**: Two balanced clauses contrasting monarchy and republic - **Historical infinitive potential**: Could use infinitives for vividness - **Condensed style**: Typical Tacitean brevity and antithesis', 'From Tacitus''s Annals, this passage contrasts the monarchy period with the Roman Republic established by Brutus after expelling the last king. Reflects Roman political consciousness and republican values.', 12, 'Classical Author', 'Republic');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-020', 'Sallust Bellum Catilinae 1.1', 'Omnis homines qui sese student praestare ceteris animalibus summa ope niti decet, ne vitam silentio transeant tamquam pecora.', 'It is fitting that all men who strive to excel other animals should endeavor with the greatest effort, lest they pass through life in silence like cattle.', '{"omnis": {"meaning": "all", "grammar": "accusative plural"}, "homines": {"meaning": "men", "grammar": "accusative plural"}, "qui": {"meaning": "who", "grammar": "nominative plural relative pronoun"}, "sese": {"meaning": "themselves", "grammar": "accusative reflexive"}, "student": {"meaning": "they strive", "grammar": "3rd person plural present"}, "praestare": {"meaning": "to excel/surpass", "grammar": "infinitive"}, "ceteris": {"meaning": "other", "grammar": "dative plural"}, "animalibus": {"meaning": "animals", "grammar": "dative plural"}, "summa": {"meaning": "greatest", "grammar": "ablative singular"}, "ope": {"meaning": "effort/power", "grammar": "ablative singular"}, "niti": {"meaning": "to strive", "grammar": "infinitive"}, "decet": {"meaning": "it is fitting", "grammar": "3rd person singular present impersonal"}, "ne": {"meaning": "lest", "grammar": "negative purpose conjunction"}, "vitam": {"meaning": "life", "grammar": "accusative singular"}, "silentio": {"meaning": "in silence", "grammar": "ablative singular"}, "transeant": {"meaning": "they should pass through", "grammar": "3rd person plural present subjunctive"}, "tamquam": {"meaning": "like/as if", "grammar": "adverb"}, "pecora": {"meaning": "cattle", "grammar": "nominative/accusative plural"}}', '- **Relative clause**: "qui sese student" describes "homines" - **Reflexive pronoun**: "sese" emphasizes self-direction - **Complementary infinitive**: "praestare" completes "student" - **Impersonal construction**: "decet" with accusative subject - **Purpose clause**: "ne transeant" with subjunctive - **Ablative of manner**: "silentio" and "summa ope" - **Comparison**: "tamquam pecora" - simile degrading humans to animals', 'Opening of Sallust''s work on Catiline''s conspiracy, emphasizing the human duty to achieve excellence and avoid a passive, unremarkable existence. Reflects Roman values of virtus and gloria.', 18, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-021', 'Pliny Letters 1.1.1', 'Frequenter hortatus es ut epistulas, si quas paulo curatius scripsissem, colligerem publicaremque.', 'You have frequently urged me to collect and publish letters, if I had written any with somewhat greater care.', '{"frequenter": {"meaning": "frequently", "grammar": "adverb"}, "hortatus": {"meaning": "having urged", "grammar": "perfect deponent participle"}, "es": {"meaning": "you are/have", "grammar": "2nd person singular present"}, "ut": {"meaning": "that", "grammar": "conjunction introducing indirect command"}, "epistulas": {"meaning": "letters", "grammar": "accusative plural"}, "si": {"meaning": "if", "grammar": "conditional conjunction"}, "quas": {"meaning": "which", "grammar": "accusative plural relative pronoun"}, "paulo": {"meaning": "somewhat", "grammar": "ablative of degree"}, "curatius": {"meaning": "more carefully", "grammar": "comparative adverb"}, "scripsissem": {"meaning": "I had written", "grammar": "1st person singular pluperfect subjunctive"}, "colligerem": {"meaning": "I should collect", "grammar": "1st person singular imperfect subjunctive"}, "publicaremque": {"meaning": "and publish", "grammar": "1st person singular imperfect subjunctive + -que"}}', '- **Perfect deponent**: "hortatus es" - deponent perfect with active meaning - **Indirect command**: "ut...colligerem publicaremque" with subjunctive - **Mixed condition**: "si scripsissem" - past contrary to fact condition - **Relative clause**: "quas" refers to "epistulas" - **Ablative of degree**: "paulo" modifies comparative "curatius" - **Pluperfect subjunctive**: "scripsissem" in conditional clause - **Compound predicate**: "colligerem publicaremque" - two verbs connected', 'From Pliny the Younger''s letter collection preface, showing the literary culture of the early Empire where educated Romans corresponded extensively and considered letters as potential literary works.', 12, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-022', 'Quintilian Institutio Oratoria 1.1.1', 'Nonnulli existimant non esse ante septemum annum discendis litteris initiandum puerum.', 'Some think that a boy should not be introduced to learning letters before the seventh year.', '{"nonnulli": {"meaning": "some people", "grammar": "nominative plural"}, "existimant": {"meaning": "they think", "grammar": "3rd person plural present"}, "non": {"meaning": "not", "grammar": "negative adverb"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "ante": {"meaning": "before", "grammar": "preposition + accusative"}, "septimum": {"meaning": "seventh", "grammar": "accusative singular ordinal"}, "annum": {"meaning": "year", "grammar": "accusative singular"}, "discendis": {"meaning": "to be learned", "grammar": "gerund, dative plural"}, "litteris": {"meaning": "letters/literature", "grammar": "dative plural"}, "initiandum": {"meaning": "to be introduced", "grammar": "gerundive, accusative singular"}, "puerum": {"meaning": "boy", "grammar": "accusative singular"}}', '- **Indirect statement**: "non esse...initiandum" with accusative subject "puerum" - **Gerund**: "discendis" expresses purpose - **Gerundive**: "initiandum" shows necessity/obligation - **Temporal expression**: "ante septimum annum" - time before which - **Dative with compound verb**: "litteris initiandum" - introduce to letters - **Passive periphrastic**: "initiandum esse" - expressing obligation - **Indefinite subject**: "nonnulli" - some authorities', 'From Quintilian''s treatise on education, discussing the proper age to begin formal education. Reflects Roman educational practices and the systematic approach to rhetoric training.', 11, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-023', 'Seneca Epistulae 1.1', 'Ita fac, mi Lucili: vindica te tibi, et tempus quod adhuc aut auferebatur aut subripiebatur aut excidebat collige et serva.', 'Do thus, my Lucilius: claim yourself for yourself, and gather and preserve the time which was until now either being taken away or stolen or was falling away.', '{"ita": {"meaning": "thus/so", "grammar": "adverb"}, "fac": {"meaning": "do", "grammar": "2nd person singular imperative"}, "mi": {"meaning": "my", "grammar": "vocative of possessive"}, "Lucili": {"meaning": "Lucilius", "grammar": "vocative"}, "vindica": {"meaning": "claim", "grammar": "2nd person singular imperative"}, "te": {"meaning": "yourself", "grammar": "accusative"}, "tibi": {"meaning": "for yourself", "grammar": "dative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "tempus": {"meaning": "time", "grammar": "accusative singular"}, "quod": {"meaning": "which", "grammar": "nominative singular relative"}, "adhuc": {"meaning": "until now", "grammar": "adverb"}, "aut": {"meaning": "either/or", "grammar": "conjunction"}, "auferebatur": {"meaning": "was being taken away", "grammar": "3rd person singular imperfect passive"}, "subripiebatur": {"meaning": "was being stolen", "grammar": "3rd person singular imperfect passive"}, "excidebat": {"meaning": "was falling away", "grammar": "3rd person singular imperfect"}, "collige": {"meaning": "gather", "grammar": "2nd person singular imperative"}, "serva": {"meaning": "preserve", "grammar": "2nd person singular imperative"}}', '- **Imperative series**: "fac, vindica, collige, serva" - commands building urgency - **Reflexive construction**: "vindica te tibi" - claim yourself for yourself - **Relative clause**: "quod" refers to "tempus" - **Triple disjunction**: "aut...aut...aut" - three ways time is lost - **Imperfect passive**: "auferebatur, subripiebatur" - ongoing past action - **Temporal adverb**: "adhuc" emphasizes the continuing nature until now - **Vocative of affection**: "mi Lucili" - intimate address', 'Opening of Seneca''s moral letters to Lucilius, emphasizing the Stoic principle of valuing time and self-possession. Reflects the philosophical correspondence tradition of the early Empire.', 19, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-024', 'Juvenal Satires 1.1-3', 'Semper ego auditor tantum? numquamne reponam vexatus totiens rauci Theseide Cordi?', 'Shall I always be only a listener? Shall I never strike back, vexed so often by hoarse Cordus''s Theseid?', '{"semper": {"meaning": "always", "grammar": "adverb"}, "ego": {"meaning": "I", "grammar": "nominative"}, "auditor": {"meaning": "listener", "grammar": "nominative singular"}, "tantum": {"meaning": "only", "grammar": "adverb"}, "numquamne": {"meaning": "never?", "grammar": "interrogative adverb + -ne"}, "reponam": {"meaning": "shall I strike back", "grammar": "1st person singular future"}, "vexatus": {"meaning": "having been vexed", "grammar": "perfect passive participle"}, "totiens": {"meaning": "so many times", "grammar": "adverb"}, "rauci": {"meaning": "hoarse", "grammar": "genitive singular"}, "Theseide": {"meaning": "Theseid", "grammar": "ablative singular - epic poem"}, "Cordi": {"meaning": "of Cordus", "grammar": "genitive singular"}}', '- **Rhetorical questions**: Two questions expressing frustration - **Future indicative**: "reponam" - deliberative future - **Perfect passive participle**: "vexatus" shows completed state - **Genitive of possession**: "rauci Cordi" - Cordus''s poem - **Ablative of means**: "Theseide" - by means of the epic - **Temporal adverb**: "totiens" emphasizes repetition - **Emphatic positioning**: "semper...tantum" frame the complaint', 'Opening of Juvenal''s first satire, expressing frustration with having to listen to bad poetry recitations, a common complaint in Roman literary culture where public readings were frequent social events.', 10, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-025', 'Petronius Satyricon 1.1', '"Num alio genere furiarum declamatores inquietantur, qui clamant: ''Haec vulnera pro libertate publica excepi''?"', 'Are the declaimers disturbed by a different kind of madness, who shout: ''These wounds I received for the public freedom''?', '{"alio": {"meaning": "different", "grammar": "ablative singular"}, "genere": {"meaning": "kind/type", "grammar": "ablative singular"}, "furiarum": {"meaning": "of madness/furies", "grammar": "genitive plural"}, "declamatores": {"meaning": "declaimers", "grammar": "nominative plural"}, "inquietantur": {"meaning": "are disturbed", "grammar": "3rd person plural present passive"}, "qui": {"meaning": "who", "grammar": "nominative plural relative"}, "clamant": {"meaning": "they shout", "grammar": "3rd person plural present"}, "haec": {"meaning": "these", "grammar": "neuter plural accusative"}, "vulnera": {"meaning": "wounds", "grammar": "neuter plural accusative"}, "pro": {"meaning": "for/in behalf of", "grammar": "preposition + ablative"}, "libertate": {"meaning": "freedom", "grammar": "ablative singular"}, "publica": {"meaning": "public", "grammar": "ablative singular"}, "excepi": {"meaning": "I received", "grammar": "1st person singular perfect"}}', '- **Interrogative with num**: Expects negative answer - **Ablative of means**: "alio genere furiarum" - by what kind of madness - **Relative clause**: "qui clamant" describes "declamatores" - **Direct quotation**: Embedded speech within the question - **Ablative of purpose**: "pro libertate publica" - for public freedom - **Perfect tense**: "excepi" in direct speech - **Demonstrative pronoun**: "haec vulnera" - these specific wounds', 'From Petronius''s satirical novel, mocking the artificial and bombastic style of rhetorical schools where students practiced declamations on heroic themes far removed from real life.', 14, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-026', 'Apuleius Metamorphoses 1.1', 'At ego tibi sermone isto Milesio varias fabulas conseram auresque tuas benivolas lepido susurro permulceam.', 'But I shall weave together various tales for you in this Milesian style and caress your kindly ears with charming whisper.', '{"at": {"meaning": "but", "grammar": "conjunction"}, "ego": {"meaning": "I", "grammar": "nominative"}, "tibi": {"meaning": "for you", "grammar": "dative"}, "sermone": {"meaning": "speech/style", "grammar": "ablative singular"}, "isto": {"meaning": "this", "grammar": "ablative singular demonstrative"}, "Milesio": {"meaning": "Milesian", "grammar": "ablative singular"}, "varias": {"meaning": "various", "grammar": "accusative plural"}, "fabulas": {"meaning": "tales/stories", "grammar": "accusative plural"}, "conseram": {"meaning": "I shall weave together", "grammar": "1st person singular future"}, "auresque": {"meaning": "and ears", "grammar": "accusative plural + -que"}, "tuas": {"meaning": "your", "grammar": "accusative plural"}, "benivolas": {"meaning": "kindly", "grammar": "accusative plural"}, "lepido": {"meaning": "charming", "grammar": "ablative singular"}, "susurro": {"meaning": "whisper", "grammar": "ablative singular"}, "permulceam": {"meaning": "I shall caress", "grammar": "1st person singular future"}}', '- **Future tense**: "conseram, permulceam" - promises of narrative action - **Dative of advantage**: "tibi" - for your benefit - **Ablative of manner**: "sermone isto Milesio" - in this style - **Direct address**: "auresque tuas" - intimate connection with reader - **Compound verb**: "permulceam" - stroke gently - **Transferred epithet**: "benivolas" applied to ears rather than person - **Metaphorical language**: Weaving stories, caressing ears', 'From Apuleius''s Golden Ass, the narrator promises entertaining tales in the Milesian style (erotic/fantastic stories). Reflects the Second Sophistic movement and novel-writing in the 2nd century CE.', 16, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-027', 'Augustine Confessions 1.1.1', 'Magnus es, domine, et laudabilis valde; magna virtus tua et sapientiae tuae non est numerus.', 'Great are you, Lord, and greatly to be praised; great is your power and of your wisdom there is no number.', '{"magnus": {"meaning": "great", "grammar": "nominative singular"}, "es": {"meaning": "you are", "grammar": "2nd person singular present"}, "domine": {"meaning": "Lord", "grammar": "vocative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "laudabilis": {"meaning": "praiseworthy", "grammar": "nominative singular"}, "valde": {"meaning": "greatly", "grammar": "adverb"}, "magna": {"meaning": "great", "grammar": "nominative singular"}, "virtus": {"meaning": "power/virtue", "grammar": "nominative singular"}, "tua": {"meaning": "your", "grammar": "nominative singular"}, "sapientiae": {"meaning": "of wisdom", "grammar": "genitive singular"}, "tuae": {"meaning": "your", "grammar": "genitive singular"}, "non": {"meaning": "not", "grammar": "negative adverb"}, "est": {"meaning": "there is", "grammar": "3rd person singular present"}, "numerus": {"meaning": "number/measure", "grammar": "nominative singular"}}', '- **Predicate adjectives**: "magnus, laudabilis" with copulative "es" - **Vocative address**: "domine" - direct address to God - **Gerundive**: "laudabilis" - expressing obligation/worthiness - **Parallel structure**: "magnus es...magna virtus tua" - **Genitive of possession**: "virtus tua, sapientiae tuae" - **Existential construction**: "non est numerus" - there is no measure - **Biblical/liturgical style**: Elevated, reverential tone', 'Opening of Augustine''s Confessions, the first major Christian autobiography, combining classical rhetorical training with Christian theology. Marks the transition from pagan to Christian Latin literature.', 14, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-028', 'Boethius Consolatio 1.1', 'Carmina qui quondam studio florente peregi, flebilis heu maestos cogor inire modos.', 'I who once completed songs with flourishing zeal, alas, am compelled to enter upon tearful sad measures.', '{"carmina": {"meaning": "songs/poems", "grammar": "accusative plural"}, "qui": {"meaning": "I who", "grammar": "nominative relative pronoun"}, "quondam": {"meaning": "once/formerly", "grammar": "adverb"}, "studio": {"meaning": "with zeal", "grammar": "ablative singular"}, "florente": {"meaning": "flourishing", "grammar": "present participle, ablative"}, "peregi": {"meaning": "I completed", "grammar": "1st person singular perfect"}, "flebilis": {"meaning": "tearful", "grammar": "nominative singular"}, "heu": {"meaning": "alas", "grammar": "interjection"}, "maestos": {"meaning": "sad", "grammar": "accusative plural"}, "cogor": {"meaning": "I am compelled", "grammar": "1st person singular present passive"}, "inire": {"meaning": "to enter upon", "grammar": "infinitive"}, "modos": {"meaning": "measures/songs", "grammar": "accusative plural"}}', '- **Relative pronoun**: "qui" refers to implied "ego" - **Ablative absolute**: "studio florente" - temporal circumstance - **Perfect tense**: "peregi" - completed past action - **Exclamatory interjection**: "heu" expresses emotion - **Passive voice**: "cogor" - external compulsion - **Complementary infinitive**: "inire" completes "cogor" - **Chiastic structure**: Contrast between past joy and present sorrow', 'Opening of Boethius''s Consolation of Philosophy, written while imprisoned and awaiting execution. Represents the end of classical antiquity and transition to medieval Latin literature.', 12, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-029', 'Ammianus Marcellinus 14.6.2', 'Post haec Gallus Caesar, cuius virtus cognita multiplici bellorum experientia clarebat, destinatus est ad orientales partes.', 'After these things, Gallus Caesar, whose virtue known through multiple experience of wars was shining forth, was destined for the eastern regions.', '{"post": {"meaning": "after", "grammar": "preposition + accusative"}, "haec": {"meaning": "these things", "grammar": "neuter plural accusative"}, "Gallus": {"meaning": "Gallus", "grammar": "nominative"}, "Caesar": {"meaning": "Caesar", "grammar": "nominative"}, "cuius": {"meaning": "whose", "grammar": "genitive singular relative"}, "virtus": {"meaning": "virtue/courage", "grammar": "nominative singular"}, "cognita": {"meaning": "known", "grammar": "perfect passive participle, nominative"}, "multiplici": {"meaning": "multiple", "grammar": "ablative singular"}, "bellorum": {"meaning": "of wars", "grammar": "genitive plural"}, "experientia": {"meaning": "experience", "grammar": "ablative singular"}, "clarebat": {"meaning": "was shining forth", "grammar": "3rd person singular imperfect"}, "destinatus": {"meaning": "destined", "grammar": "perfect passive participle, nominative"}, "est": {"meaning": "was", "grammar": "3rd person singular perfect passive"}, "ad": {"meaning": "to/toward", "grammar": "preposition + accusative"}, "orientales": {"meaning": "eastern", "grammar": "accusative plural"}, "partes": {"meaning": "regions", "grammar": "accusative plural"}}', '- **Temporal expression**: "post haec" - after these events - **Relative clause**: "cuius virtus...clarebat" describes Gallus - **Perfect passive participle**: "cognita" agrees with "virtus" - **Ablative of means**: "multiplici experientia" - through experience - **Imperfect tense**: "clarebat" - ongoing past reputation - **Passive periphrastic**: "destinatus est" - was appointed/sent - **Prepositional phrase**: "ad orientales partes" - direction', 'From Ammianus''s history of the late Roman Empire, describing military appointments in the 4th century CE. Represents the continuation of classical historical writing in Late Antiquity.', 16, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'expert', 'EXP-030', 'Claudian De Raptu Proserpinae 1.1-3', 'Infernas raptor Ditis secreta per umbras ducere temptavit caeco pallore viventem Persephonem.', 'The ravisher of Dis attempted to lead Persephone, living in blind pallor, through the secret shades of the underworld.', '{"infernas": {"meaning": "of the underworld", "grammar": "accusative plural"}, "raptor": {"meaning": "ravisher", "grammar": "nominative singular"}, "Ditis": {"meaning": "of Dis/Pluto", "grammar": "genitive singular"}, "secreta": {"meaning": "secrets/hidden places", "grammar": "accusative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "umbras": {"meaning": "shades/shadows", "grammar": "accusative plural"}, "ducere": {"meaning": "to lead", "grammar": "infinitive"}, "temptavit": {"meaning": "he attempted", "grammar": "3rd person singular perfect"}, "caeco": {"meaning": "blind", "grammar": "ablative singular"}, "pallore": {"meaning": "with pallor", "grammar": "ablative singular"}, "viventem": {"meaning": "living", "grammar": "present participle, accusative"}, "Persephonem": {"meaning": "Persephone", "grammar": "accusative"}}', '- **Genitive of possession**: "raptor Ditis" - Pluto the ravisher - **Adjective as noun**: "infernas secreta" - underworld secrets - **Ablative of manner**: "caeco pallore" - with blind pallor - **Present participle**: "viventem" describes Persephone''s state - **Complementary infinitive**: "ducere" completes "temptavit" - **Poetic word order**: Hyperbaton separates related words - **Mythological subject matter**: Classical divine narrative', 'From Claudian''s epic on the rape of Proserpina, representing the revival of classical mythological poetry in the late 4th century CE during the final flowering of pagan Latin literature.', 12, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-001', 'Simple Greeting', 'Salve, amice!', 'Hello, friend!', '{"salve": {"meaning": "hello/greetings", "grammar": "imperative singular"}, "amice": {"meaning": "friend", "grammar": "vocative singular"}}', '- **Imperative mood**: "salve" is a standard greeting form - **Vocative case**: "amice" is used for direct address - **Common greeting**: Most basic form of Latin salutation', 'Standard Roman greeting used between friends and equals. "Salve" literally means "be well" and was the most common informal greeting in ancient Rome.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-002', 'Simple Farewell', 'Vale bene!', 'Farewell well!', '{"vale": {"meaning": "farewell", "grammar": "imperative singular"}, "bene": {"meaning": "well", "grammar": "adverb"}}', '- **Imperative mood**: "vale" commands someone to be well - **Adverb**: "bene" modifies the imperative - **Parting formula**: Standard way to say goodbye', 'Common Roman farewell meaning "be well." Romans valued health and well-being, making this an appropriate way to part from someone.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-003', 'Basic Question', 'Quid agis?', 'How are you doing?', '{"quid": {"meaning": "what", "grammar": "interrogative pronoun"}, "agis": {"meaning": "you are doing", "grammar": "2nd person singular present"}}', '- **Interrogative pronoun**: "quid" asks "what" - **Present tense**: "agis" from "ago, agere" - to do/drive - **Direct question**: Simple inquiry about someone''s activities', 'Common Roman way of asking "how are you?" Literally means "what are you doing?" reflecting Roman focus on activity and accomplishment.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-004', 'Family Term', 'Mater mea', 'My mother', '{"mater": {"meaning": "mother", "grammar": "nominative singular"}, "mea": {"meaning": "my", "grammar": "possessive adjective, nominative singular"}}', '- **Possessive adjective**: "mea" agrees with "mater" in gender and case - **Nominative case**: Both words are in subject form - **Family relationship**: Basic kinship vocabulary', 'Family was central to Roman society. The mother (mater) held an important role in the household and in raising children.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-005', 'Basic Location', 'In urbe', 'In the city', '{"in": {"meaning": "in", "grammar": "preposition + ablative"}, "urbe": {"meaning": "city", "grammar": "ablative singular"}}', '- **Preposition**: "in" takes ablative case for location - **Ablative of place**: "urbe" shows where something is - **Feminine noun**: "urbs, urbis" is feminine', 'Rome was the ultimate city (urbs) for Romans. The phrase could refer to any city but often meant Rome specifically.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-006', 'Simple Declaration', 'Bona fortuna!', 'Good fortune!', '{"bona": {"meaning": "good", "grammar": "adjective, nominative singular"}, "fortuna": {"meaning": "fortune", "grammar": "nominative singular"}}', '- **Adjective agreement**: "bona" agrees with "fortuna" in gender, number, case - **Exclamatory phrase**: Expression of good wishes - **Nominative case**: Both words are in subject form', 'Romans believed strongly in fortuna (fortune/luck). Wishing someone good fortune was a common blessing and showed care for their well-being.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-007', 'Time Expression', 'Hodie non', 'Not today', '{"hodie": {"meaning": "today", "grammar": "adverb"}, "non": {"meaning": "not", "grammar": "negative adverb"}}', '- **Temporal adverb**: "hodie" indicates present day - **Negation**: "non" negates the time expression - **Simple denial**: Refusing something for today', 'Time consciousness was important to Romans. This phrase might be used to postpone activities or decline invitations.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-008', 'Basic Possession', 'Meus liber', 'My book', '{"meus": {"meaning": "my", "grammar": "possessive adjective, nominative singular"}, "liber": {"meaning": "book", "grammar": "nominative singular"}}', '- **Possessive adjective**: "meus" shows ownership - **Gender agreement**: "meus" is masculine to agree with "liber" - **Nominative case**: Both words in subject form', 'Books were valuable in the ancient world. Owning books showed education and wealth, as they were hand-copied and expensive.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-009', 'Simple Preference', 'Amo te', 'I love you', '{"amo": {"meaning": "I love", "grammar": "1st person singular present"}, "te": {"meaning": "you", "grammar": "accusative singular"}}', '- **Present tense**: "amo" expresses current feeling - **Direct object**: "te" receives the action of loving - **Personal pronoun**: "te" is accusative form of "tu"', 'While Romans could express deep affection, "amo" was a strong word. It appeared in poetry and intimate relationships.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-010', 'Basic Need', 'Aquam volo', 'I want water', '{"aquam": {"meaning": "water", "grammar": "accusative singular"}, "volo": {"meaning": "I want", "grammar": "1st person singular present"}}', '- **Direct object**: "aquam" is what is wanted - **Irregular verb**: "volo" is an irregular verb meaning "want/wish" - **Accusative case**: Object of the verb "volo"', 'Water was precious in the ancient world. Access to clean water was a constant concern, and aqueducts were major Roman achievements.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-011', 'Simple Direction', 'Veni huc!', 'Come here!', '{"veni": {"meaning": "come", "grammar": "2nd person singular imperative"}, "huc": {"meaning": "here/to this place", "grammar": "adverb"}}', '- **Imperative mood**: "veni" is a command - **Adverb of place**: "huc" indicates direction toward speaker - **Irregular verb**: "venio" has irregular imperative "veni"', 'Direct commands were common in Roman daily life. This phrase shows authority relationships in family, military, or social contexts.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-012', 'Basic Counting', 'Duo pueri', 'Two boys', '{"duo": {"meaning": "two", "grammar": "numeral"}, "pueri": {"meaning": "boys", "grammar": "nominative plural"}}', '- **Cardinal number**: "duo" is the number two - **Plural noun**: "pueri" is plural of "puer" - **Number agreement**: "duo" agrees with plural noun', 'Roman families often had multiple children. Boys (pueri) were especially valued as they would carry on the family name and inheritance.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-013', 'Simple Food', 'Panis bonus', 'Good bread', '{"panis": {"meaning": "bread", "grammar": "nominative singular"}, "bonus": {"meaning": "good", "grammar": "adjective, nominative singular"}}', '- **Adjective agreement**: "bonus" agrees with "panis" in case, number, gender - **Quality description**: "bonus" describes the bread''s quality - **Masculine gender**: Both words are masculine', 'Bread was the staple food of the Roman diet. Good bread was a sign of prosperity and proper household management.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-014', 'Weather Expression', 'Caelum clarum', 'Clear sky', '{"caelum": {"meaning": "sky", "grammar": "nominative singular"}, "clarum": {"meaning": "clear", "grammar": "adjective, nominative singular"}}', '- **Neuter noun**: "caelum" is neuter - **Adjective agreement**: "clarum" agrees in gender with "caelum" - **Weather description**: Describes atmospheric conditions', 'Romans were attentive to weather and celestial phenomena. Clear skies were favorable for travel, agriculture, and religious ceremonies.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-015', 'Simple Animal', 'Canis meus', 'My dog', '{"canis": {"meaning": "dog", "grammar": "nominative singular"}, "meus": {"meaning": "my", "grammar": "possessive adjective, nominative singular"}}', '- **Possessive adjective**: "meus" shows ownership - **Gender agreement**: "meus" is masculine like "canis" - **Common pet**: Dogs were familiar animals to Romans', 'Dogs were kept as pets, guards, and hunting companions in Roman households. They appear frequently in Roman art and literature.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-016', 'Basic Color', 'Rosa rubra', 'Red rose', '{"rosa": {"meaning": "rose", "grammar": "nominative singular"}, "rubra": {"meaning": "red", "grammar": "adjective, nominative singular"}}', '- **Feminine noun**: "rosa" is feminine - **Adjective agreement**: "rubra" agrees in gender with "rosa" - **Color description**: "rubra" describes the rose''s color', 'Roses were popular in Roman gardens and were associated with Venus, love, and beauty. Red roses had special significance in poetry and art.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-017', 'Simple Action', 'Curro celeriter', 'I run quickly', '{"curro": {"meaning": "I run", "grammar": "1st person singular present"}, "celeriter": {"meaning": "quickly", "grammar": "adverb"}}', '- **Present tense**: "curro" describes current action - **Adverb**: "celeriter" modifies the verb - **Manner expression**: Shows how the action is performed', 'Physical fitness was important to Romans, especially for military service. Running was part of athletic training and daily exercise.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-018', 'Time of Day', 'Mane est', 'It is morning', '{"mane": {"meaning": "morning", "grammar": "indeclinable noun"}, "est": {"meaning": "it is", "grammar": "3rd person singular present"}}', '- **Copulative verb**: "est" links subject and predicate - **Indeclinable noun**: "mane" doesn''t change form - **Time expression**: States the time of day', 'Romans divided the day into periods. Morning (mane) was the time for business, court proceedings, and formal activities.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-019', 'Basic Body Part', 'Oculi mei', 'My eyes', '{"oculi": {"meaning": "eyes", "grammar": "nominative plural"}, "mei": {"meaning": "my", "grammar": "possessive adjective, nominative plural"}}', '- **Plural noun**: "oculi" is plural of "oculus" - **Possessive agreement**: "mei" agrees with plural "oculi" - **Body part**: Basic anatomical vocabulary', 'Eyes were considered windows to the soul in Roman thought. Beautiful eyes were praised in poetry, and the evil eye was feared.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-020', 'Simple Gift', 'Donum tibi', 'A gift for you', '{"donum": {"meaning": "gift", "grammar": "nominative singular"}, "tibi": {"meaning": "for you", "grammar": "dative singular"}}', '- **Dative case**: "tibi" shows the recipient of the gift - **Neuter noun**: "donum" is neuter - **Gift giving**: Expression of generosity', 'Gift giving was important in Roman social relations. Gifts created bonds of obligation and friendship between people.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-021', 'Simple House', 'Domus magna', 'Large house', '{"domus": {"meaning": "house", "grammar": "nominative singular"}, "magna": {"meaning": "large", "grammar": "adjective, nominative singular"}}', '- **Irregular noun**: "domus" has mixed 2nd/4th declension forms - **Adjective agreement**: "magna" agrees with feminine "domus" - **Size description**: "magna" describes the house''s size', 'Roman houses varied greatly in size. A large house (domus magna) indicated wealth and social status in Roman society.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-022', 'Basic Clothing', 'Toga alba', 'White toga', '{"toga": {"meaning": "toga", "grammar": "nominative singular"}, "alba": {"meaning": "white", "grammar": "adjective, nominative singular"}}', '- **Feminine noun**: "toga" is feminine - **Color adjective**: "alba" describes the toga''s color - **Cultural garment**: Specifically Roman clothing', 'The toga was the distinctive garment of Roman citizens. White togas were worn for formal occasions and by those seeking political office.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-023', 'Simple Food Item', 'Vinum bonum', 'Good wine', '{"vinum": {"meaning": "wine", "grammar": "nominative singular"}, "bonum": {"meaning": "good", "grammar": "adjective, nominative singular"}}', '- **Neuter noun**: "vinum" is neuter - **Adjective agreement**: "bonum" agrees with neuter "vinum" - **Quality assessment**: "bonum" evaluates the wine', 'Wine was a daily beverage for Romans, mixed with water. Good wine was a sign of prosperity and proper hospitality.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-024', 'Basic Number', 'Tres libri', 'Three books', '{"tres": {"meaning": "three", "grammar": "numeral, nominative plural"}, "libri": {"meaning": "books", "grammar": "nominative plural"}}', '- **Cardinal number**: "tres" is the number three - **Plural agreement**: "tres" agrees with plural "libri" - **Counting objects**: Basic enumeration', 'Books were precious possessions. Owning multiple books indicated education and wealth, as books were hand-copied manuscripts.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-025', 'Simple Work', 'Labor durus', 'Hard work', '{"labor": {"meaning": "work", "grammar": "nominative singular"}, "durus": {"meaning": "hard/difficult", "grammar": "adjective, nominative singular"}}', '- **Masculine noun**: "labor" is masculine - **Descriptive adjective**: "durus" characterizes the work - **Quality description**: Expresses the nature of work', 'Romans valued hard work and industry. Labor was seen as virtuous, though physical labor was often done by slaves.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-026', 'Simple Emotion', 'Sum laetus', 'I am happy', '{"sum": {"meaning": "I am", "grammar": "1st person singular present of \"esse\""}, "laetus": {"meaning": "happy", "grammar": "adjective, nominative singular"}}', '- **Copulative verb**: "sum" links subject with predicate adjective - **Predicate adjective**: "laetus" describes the subject''s state - **Emotional state**: Expresses current feeling', 'Romans valued emotional moderation, but expressions of happiness were appropriate in private and celebratory contexts.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-027', 'Basic Direction', 'Ad forum', 'To the forum', '{"ad": {"meaning": "to/toward", "grammar": "preposition + accusative"}, "forum": {"meaning": "forum", "grammar": "accusative singular"}}', '- **Preposition**: "ad" indicates direction/motion toward - **Accusative case**: "forum" is accusative after "ad" - **Public space**: The forum was the center of Roman civic life', 'The Roman Forum was the heart of political, commercial, and social life. Going "to the forum" meant participating in public affairs.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-028', 'Simple Question', 'Ubi est?', 'Where is it?', '{"ubi": {"meaning": "where", "grammar": "interrogative adverb"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}}', '- **Interrogative adverb**: "ubi" asks about location - **Present tense**: "est" from irregular verb "esse" - **Direct question**: Simple inquiry about place', 'Location questions were common in daily Roman life, whether asking about people, objects, or places in the city.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-029', 'Basic Possession', 'Mea pecunia', 'My money', '{"mea": {"meaning": "my", "grammar": "possessive adjective, nominative singular"}, "pecunia": {"meaning": "money", "grammar": "nominative singular"}}', '- **Possessive adjective**: "mea" shows ownership - **Feminine noun**: "pecunia" is feminine - **Economic vocabulary**: Basic financial term', 'Money (pecunia) originally referred to wealth in cattle (pecus). Roman economy developed complex monetary systems for trade and commerce.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-030', 'Simple Gratitude', 'Gratias tibi!', 'Thanks to you!', '{"gratias": {"meaning": "thanks", "grammar": "accusative plural"}, "tibi": {"meaning": "to you", "grammar": "dative singular"}}', '- **Accusative of exclamation**: "gratias" in exclamatory expression - **Dative case**: "tibi" shows to whom thanks are given - **Idiomatic expression**: Standard way to express gratitude', 'Expressing gratitude was important in Roman social relations. Proper acknowledgment of favors maintained social bonds and obligations.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-001', 'Cicero Pro Archia 1-2', 'Si quid est in me ingeni, iudices, quod sentio quam sit exiguum, aut si qua exercitatio dicendi, in qua me non infitior mediocriter esse versatum, aut si huiusce rei ratio aliqua ab optimarum artium studiis ac disciplina profecta, a qua ego nullum confiteor aetatis meae tempus abhorruisse, earum rerum omnium vel in primis hic Aulus Licinius fructum a me repetere prope suo iure potest.', 'If there is any talent in me, judges, which I realize how small it is, or if there is any training in speaking, in which I do not deny that I have been moderately engaged, or if there is any method in this matter derived from the study and training of the finest arts, from which I confess that no period of my life has shrunk away, this Aulus Licinius can claim the benefit of all these things from me almost by his own right.', '{"si": {"meaning": "if", "grammar": "conditional conjunction"}, "quid": {"meaning": "anything", "grammar": "indefinite pronoun"}, "est": {"meaning": "there is", "grammar": "3rd person singular present"}, "ingeni": {"meaning": "of talent", "grammar": "genitive singular"}, "iudices": {"meaning": "judges", "grammar": "nominative plural, vocative"}, "quod": {"meaning": "which", "grammar": "accusative singular relative"}, "sentio": {"meaning": "I realize", "grammar": "1st person singular present"}, "quam": {"meaning": "how", "grammar": "exclamatory adverb"}, "sit": {"meaning": "it is", "grammar": "3rd person singular present subjunctive"}, "exiguum": {"meaning": "small", "grammar": "nominative singular neuter"}, "aut": {"meaning": "or", "grammar": "conjunction"}, "qua": {"meaning": "any", "grammar": "ablative singular"}, "exercitatio": {"meaning": "training", "grammar": "nominative singular"}, "dicendi": {"meaning": "of speaking", "grammar": "gerund, genitive"}, "me": {"meaning": "myself", "grammar": "accusative"}, "non": {"meaning": "not", "grammar": "negative"}, "infitior": {"meaning": "I deny", "grammar": "1st person singular present deponent"}, "mediocriter": {"meaning": "moderately", "grammar": "adverb"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "versatum": {"meaning": "engaged", "grammar": "perfect passive participle"}, "huiusce": {"meaning": "of this", "grammar": "genitive singular"}, "rei": {"meaning": "thing/matter", "grammar": "genitive singular"}, "ratio": {"meaning": "method", "grammar": "nominative singular"}, "aliqua": {"meaning": "some", "grammar": "nominative singular"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "optimarum": {"meaning": "finest", "grammar": "genitive plural"}, "artium": {"meaning": "arts", "grammar": "genitive plural"}, "studiis": {"meaning": "studies", "grammar": "ablative plural"}, "ac": {"meaning": "and", "grammar": "conjunction"}, "disciplina": {"meaning": "training", "grammar": "ablative singular"}, "profecta": {"meaning": "derived", "grammar": "perfect participle"}, "ego": {"meaning": "I", "grammar": "nominative"}, "nullum": {"meaning": "no", "grammar": "accusative singular"}, "confiteor": {"meaning": "I confess", "grammar": "1st person singular present deponent"}, "aetatis": {"meaning": "of age", "grammar": "genitive singular"}, "meae": {"meaning": "my", "grammar": "genitive singular"}, "tempus": {"meaning": "time/period", "grammar": "accusative singular"}, "abhorruisse": {"meaning": "to have shrunk away", "grammar": "perfect infinitive"}, "earum": {"meaning": "of these", "grammar": "genitive plural"}, "rerum": {"meaning": "things", "grammar": "genitive plural"}, "omnium": {"meaning": "all", "grammar": "genitive plural"}, "vel": {"meaning": "even", "grammar": "adverb"}, "hic": {"meaning": "this", "grammar": "nominative singular"}, "Aulus": {"meaning": "Aulus", "grammar": "nominative"}, "Licinius": {"meaning": "Licinius", "grammar": "nominative"}, "fructum": {"meaning": "benefit", "grammar": "accusative singular"}, "repetere": {"meaning": "to claim", "grammar": "infinitive"}, "prope": {"meaning": "almost", "grammar": "adverb"}, "suo": {"meaning": "his own", "grammar": "ablative singular"}, "iure": {"meaning": "by right", "grammar": "ablative singular"}, "potest": {"meaning": "he can", "grammar": "3rd person singular present"}}', '- **Complex conditional structure**: Multiple "si" clauses with varying conditions - **Indirect statement**: "sentio quam sit exiguum" with subjunctive - **Relative clauses**: "quod sentio", "in qua me...versatum", "a qua ego...abhorruisse" - **Perfect passive participle**: "versatum", "profecta" used in passive constructions - **Deponent verbs**: "infitior", "confiteor" have passive form, active meaning - **Genitive of characteristic**: "ingeni", "exercitatio dicendi" - **Perfect infinitive**: "abhorruisse" in indirect statement - **Partitive genitive**: "earum rerum omnium" - of all these things', 'Opening of Cicero''s speech defending the poet Archias''s Roman citizenship. Demonstrates the rhetorical strategy of modesty (captatio benevolentiae) where the orator claims limited ability while establishing his credentials. Shows the importance of education and liberal arts in Roman culture.', 85, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-002', 'Caesar De Bello Gallico 1.1-2', 'Gallia est omnis divisa in partes tres, quarum unam incolunt Belgae, aliam Aquitani, tertiam qui ipsorum lingua Celtae, nostra Galli appellantur. Hi omnes lingua, institutis, legibus inter se differunt. Gallos ab Aquitanis Garumna flumen, a Belgis Matrona et Sequana dividit.', 'All Gaul is divided into three parts, one of which the Belgae inhabit, another the Aquitani, the third those who in their own language are called Celts, in ours Gauls. All these differ from each other in language, customs, and laws. The Garonne river separates the Gauls from the Aquitani, the Marne and Seine from the Belgae.', '{"Gallia": {"meaning": "Gaul", "grammar": "nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}, "omnis": {"meaning": "all", "grammar": "nominative singular"}, "divisa": {"meaning": "divided", "grammar": "perfect passive participle"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "partes": {"meaning": "parts", "grammar": "accusative plural"}, "tres": {"meaning": "three", "grammar": "accusative plural"}, "quarum": {"meaning": "of which", "grammar": "genitive plural relative"}, "unam": {"meaning": "one", "grammar": "accusative singular"}, "incolunt": {"meaning": "they inhabit", "grammar": "3rd person plural present"}, "Belgae": {"meaning": "Belgae", "grammar": "nominative plural"}, "aliam": {"meaning": "another", "grammar": "accusative singular"}, "Aquitani": {"meaning": "Aquitani", "grammar": "nominative plural"}, "tertiam": {"meaning": "third", "grammar": "accusative singular"}, "qui": {"meaning": "who", "grammar": "nominative plural relative"}, "ipsorum": {"meaning": "their own", "grammar": "genitive plural"}, "lingua": {"meaning": "language", "grammar": "ablative singular"}, "Celtae": {"meaning": "Celts", "grammar": "nominative plural"}, "nostra": {"meaning": "our", "grammar": "ablative singular"}, "Galli": {"meaning": "Gauls", "grammar": "nominative plural"}, "appellantur": {"meaning": "they are called", "grammar": "3rd person plural present passive"}, "hi": {"meaning": "these", "grammar": "nominative plural"}, "omnes": {"meaning": "all", "grammar": "nominative plural"}, "institutis": {"meaning": "customs", "grammar": "ablative plural"}, "legibus": {"meaning": "laws", "grammar": "ablative plural"}, "differunt": {"meaning": "they differ", "grammar": "3rd person plural present"}, "Gallos": {"meaning": "Gauls", "grammar": "accusative plural"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "Aquitanis": {"meaning": "Aquitani", "grammar": "ablative plural"}, "Garumna": {"meaning": "Garonne", "grammar": "nominative singular"}, "flumen": {"meaning": "river", "grammar": "nominative singular"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "Belgis": {"meaning": "Belgae", "grammar": "ablative plural"}, "Matrona": {"meaning": "Marne", "grammar": "nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "Sequana": {"meaning": "Seine", "grammar": "nominative singular"}, "dividit": {"meaning": "divides", "grammar": "3rd person singular present"}}', '- **Perfect passive participle**: "divisa" with "est" forms perfect passive - **Relative clauses**: "quarum unam", "qui...appellantur" provide additional information - **Substantive adjectives**: "unam, aliam, tertiam" function as nouns - **Ablative of respect**: "lingua, institutis, legibus" show in what respect they differ - **Ablative with prepositions**: "ab Aquitanis, a Belgis" show separation - **Passive voice**: "appellantur" shows how they are named - **Reflexive pronoun**: "inter se" emphasizes mutual difference', 'Famous opening of Caesar''s Commentaries on the Gallic Wars, providing ethnographic background before military narrative. Demonstrates Roman geographical knowledge and administrative thinking about conquered territories.', 57, 'Ovid', 'De Bello Gallico');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-003', 'Vergil Aeneid 1.1-7', 'Arma virumque cano, Troiae qui primus ab oris Italiam fato profugus Laviniaque venit litora, multum ille et terris iactatus et alto vi superum, saevae memorem Iunonis ob iram, multa quoque et bello passus, dum conderet urbem inferretque deos Latio; genus unde Latinum Albanique patres atque altae moenia Romae.', 'I sing of arms and the man, who first from the shores of Troy, exiled by fate, came to Italy and Lavinian shores, much tossed both on lands and on the deep by the force of the gods above, because of the mindful wrath of savage Juno, having also suffered much in war, until he could found a city and bring his gods to Latium; whence came the Latin race and Alban fathers and the walls of lofty Rome.', '{"arma": {"meaning": "arms/weapons", "grammar": "accusative plural"}, "virumque": {"meaning": "and the man", "grammar": "accusative singular + -que"}, "cano": {"meaning": "I sing", "grammar": "1st person singular present"}, "Troiae": {"meaning": "of Troy", "grammar": "genitive singular"}, "qui": {"meaning": "who", "grammar": "nominative singular relative"}, "primus": {"meaning": "first", "grammar": "nominative singular"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "oris": {"meaning": "shores", "grammar": "ablative plural"}, "Italiam": {"meaning": "Italy", "grammar": "accusative singular"}, "fato": {"meaning": "by fate", "grammar": "ablative singular"}, "profugus": {"meaning": "exiled", "grammar": "nominative singular"}, "Laviniaque": {"meaning": "and Lavinian", "grammar": "accusative plural + -que"}, "venit": {"meaning": "he came", "grammar": "3rd person singular perfect"}, "litora": {"meaning": "shores", "grammar": "accusative plural"}, "multum": {"meaning": "much", "grammar": "accusative singular"}, "ille": {"meaning": "he", "grammar": "nominative singular"}, "et": {"meaning": "both...and", "grammar": "correlative"}, "terris": {"meaning": "on lands", "grammar": "ablative plural"}, "iactatus": {"meaning": "tossed", "grammar": "perfect passive participle"}, "alto": {"meaning": "on the deep", "grammar": "ablative singular"}, "vi": {"meaning": "by force", "grammar": "ablative singular"}, "superum": {"meaning": "of the gods above", "grammar": "genitive plural"}, "saevae": {"meaning": "savage", "grammar": "genitive singular"}, "memorem": {"meaning": "mindful", "grammar": "accusative singular"}, "Iunonis": {"meaning": "of Juno", "grammar": "genitive singular"}, "ob": {"meaning": "because of", "grammar": "preposition + accusative"}, "iram": {"meaning": "wrath", "grammar": "accusative singular"}, "multa": {"meaning": "much", "grammar": "accusative plural"}, "quoque": {"meaning": "also", "grammar": "adverb"}, "bello": {"meaning": "in war", "grammar": "ablative singular"}, "passus": {"meaning": "having suffered", "grammar": "perfect participle"}, "dum": {"meaning": "until", "grammar": "temporal conjunction"}, "conderet": {"meaning": "he could found", "grammar": "3rd person singular imperfect subjunctive"}, "urbem": {"meaning": "city", "grammar": "accusative singular"}, "inferretque": {"meaning": "and bring", "grammar": "3rd person singular imperfect subjunctive + -que"}, "deos": {"meaning": "gods", "grammar": "accusative plural"}, "Latio": {"meaning": "to Latium", "grammar": "dative singular"}, "genus": {"meaning": "race", "grammar": "nominative singular"}, "unde": {"meaning": "whence", "grammar": "relative adverb"}, "Latinum": {"meaning": "Latin", "grammar": "nominative singular"}, "Albanique": {"meaning": "and Alban", "grammar": "nominative plural + -que"}, "patres": {"meaning": "fathers", "grammar": "nominative plural"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "altae": {"meaning": "lofty", "grammar": "genitive singular"}, "moenia": {"meaning": "walls", "grammar": "nominative plural"}, "Romae": {"meaning": "of Rome", "grammar": "genitive singular"}}', '- **Epic opening formula**: "Arma virumque cano" follows traditional epic pattern - **Relative clause**: "qui primus...venit" describes the hero - **Ablative absolute**: "fato profugus" - circumstances of exile - **Perfect passive participle**: "iactatus" describes past suffering - **Causal expression**: "ob iram" explains reason for suffering - **Temporal clause**: "dum conderet" with subjunctive expressing purpose - **Result clause implied**: "unde genus...Romae" shows ultimate outcome - **Poetic word order**: Hyperbaton separates grammatically related words', 'Opening invocation of Vergil''s Aeneid, the national epic of Rome. Establishes the themes of war, wandering, divine intervention, and Rome''s destiny. Links Trojan origins to Roman greatness, fulfilling Augustus''s cultural program.', 49, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-004', 'Ovid Metamorphoses 1.1-4', 'In nova fert animus mutatas dicere formas corpora; di, coeptis, nam vos mutastis et illas, aspirate meis primaque ab origine mundi ad mea perpetuum deducite tempora carmen.', 'My mind is bent to tell of bodies changed into new forms; gods, for you have changed those things too, inspire my undertakings and draw down an unbroken song from the first beginning of the world to my own times.', '{"in": {"meaning": "into", "grammar": "preposition + accusative"}, "nova": {"meaning": "new", "grammar": "neuter plural accusative"}, "fert": {"meaning": "carries/bears", "grammar": "3rd person singular present"}, "animus": {"meaning": "mind/spirit", "grammar": "nominative singular"}, "mutatas": {"meaning": "changed", "grammar": "perfect passive participle, feminine plural accusative"}, "dicere": {"meaning": "to tell/speak", "grammar": "infinitive"}, "formas": {"meaning": "forms/shapes", "grammar": "accusative plural"}, "corpora": {"meaning": "bodies", "grammar": "nominative/accusative plural"}, "di": {"meaning": "gods", "grammar": "nominative plural, vocative"}, "coeptis": {"meaning": "undertakings", "grammar": "dative/ablative plural"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "vos": {"meaning": "you", "grammar": "nominative plural"}, "mutastis": {"meaning": "you have changed", "grammar": "2nd person plural perfect"}, "et": {"meaning": "and", "grammar": "conjunction"}, "illas": {"meaning": "those", "grammar": "feminine accusative plural"}, "aspirate": {"meaning": "inspire/breathe upon", "grammar": "2nd person plural imperative"}, "meis": {"meaning": "my", "grammar": "dative/ablative plural"}, "primaque": {"meaning": "and first", "grammar": "feminine nominative/ablative singular + -que"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "origine": {"meaning": "beginning/origin", "grammar": "ablative singular"}, "mundi": {"meaning": "of the world", "grammar": "genitive singular"}, "ad": {"meaning": "to/towards", "grammar": "preposition + accusative"}, "mea": {"meaning": "my", "grammar": "neuter plural accusative"}, "perpetuum": {"meaning": "unbroken/continuous", "grammar": "neuter accusative singular"}, "deducite": {"meaning": "draw down/lead down", "grammar": "2nd person plural imperative"}, "tempora": {"meaning": "times", "grammar": "neuter plural accusative"}, "carmen": {"meaning": "song/poem", "grammar": "neuter accusative singular"}}', '- **Infinitive of purpose**: "dicere" expresses the purpose of the mind''s inclination - **Perfect passive participle**: "mutatas" agrees with "formas" in accusative - **Vocative of direct address**: "di" calls upon the gods directly - **Causal conjunction**: "nam" introduces explanation for the invocation - **Imperative mood**: "aspirate" and "deducite" are commands to the gods - **Temporal expressions**: "primaque ab origine mundi ad mea tempora" shows extensive time span - **Transferred epithet**: "perpetuum carmen" - the song is continuous, not the times - **Chiastic structure**: "mutatas...formas / corpora" arranged in interlocking pattern', 'Opening invocation of Ovid''s Metamorphoses, announcing the theme of transformation that runs throughout the work. Follows epic convention of divine invocation while introducing the unique focus on change and mutation.', 28, 'Ovid', 'Metamorphoses');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-005', 'Catullus 5', 'Vivamus mea Lesbia, atque amemus, rumoresque senum severiorum omnes unius aestimemus assis! soles occidere et redire possunt: nobis cum semel occidit brevis lux, nox est perpetua una dormienda.', 'Let us live, my Lesbia, and let us love, and let us value all the rumors of stricter old men at one penny! Suns can set and return: for us, when once our brief light has set, there is one perpetual night to be slept.', '{"vivamus": {"meaning": "let us live", "grammar": "1st person plural present subjunctive"}, "mea": {"meaning": "my", "grammar": "feminine vocative"}, "Lesbia": {"meaning": "Lesbia", "grammar": "feminine vocative"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "amemus": {"meaning": "let us love", "grammar": "1st person plural present subjunctive"}, "rumoresque": {"meaning": "and rumors", "grammar": "accusative plural + -que"}, "senum": {"meaning": "of old men", "grammar": "genitive plural"}, "severiorum": {"meaning": "stricter", "grammar": "genitive plural comparative"}, "omnes": {"meaning": "all", "grammar": "accusative plural"}, "unius": {"meaning": "one", "grammar": "genitive singular"}, "aestimemus": {"meaning": "let us value", "grammar": "1st person plural present subjunctive"}, "assis": {"meaning": "penny", "grammar": "genitive singular"}, "soles": {"meaning": "suns", "grammar": "nominative plural"}, "occidere": {"meaning": "to set", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "redire": {"meaning": "to return", "grammar": "infinitive"}, "possunt": {"meaning": "they can", "grammar": "3rd person plural present"}, "nobis": {"meaning": "for us", "grammar": "dative plural"}, "cum": {"meaning": "when", "grammar": "temporal conjunction"}, "semel": {"meaning": "once", "grammar": "adverb"}, "occidit": {"meaning": "has set", "grammar": "3rd person singular perfect"}, "brevis": {"meaning": "brief", "grammar": "nominative singular"}, "lux": {"meaning": "light", "grammar": "nominative singular"}, "nox": {"meaning": "night", "grammar": "nominative singular"}, "est": {"meaning": "there is", "grammar": "3rd person singular present"}, "perpetua": {"meaning": "perpetual", "grammar": "nominative singular"}, "una": {"meaning": "one", "grammar": "nominative singular"}, "dormienda": {"meaning": "to be slept", "grammar": "gerundive, nominative singular"}}', '- **Hortatory subjunctive**: "vivamus, amemus, aestimemus" - exhortations - **Genitive of value**: "unius assis" - worth one penny - **Complementary infinitives**: "occidere et redire" complete "possunt" - **Temporal clause**: "cum semel occidit" with perfect indicative - **Metaphorical language**: "soles" vs "brevis lux" contrasts cosmic and human time - **Passive periphrastic**: "dormienda est" - expressing necessity - **Chiastic structure**: Life/love vs. criticism/death themes interwoven', 'Famous carpe diem poem by Catullus addressing his lover Lesbia (probably Clodia). Advocates for immediate pleasure against social criticism, using astronomical imagery to emphasize human mortality. Reflects Hellenistic influence on Roman poetry.', 29, 'Catullus', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-006', 'Horace Odes 1.11', 'Tu ne quaesieris, scire nefas, quem mihi, quem tibi finem di dederint, Leuconoe, nec Babylonios temptaris numeros. ut melius, quidquid erit, pati! seu pluris hiemes seu tribuit Iuppiter ultimam, quae nunc oppositis debilitat pumicibus mare Tyrrhenum: sapias, vina liques, et spatio brevi spem longam reseces. dum loquimur, fugerit invida aetas: carpe diem, quam minimum credula postero.', 'Do not ask, it is wrong to know, what end the gods have given to me, what to you, Leuconoe, nor attempt Babylonian calculations. How much better to endure whatever will be! Whether Jupiter has granted more winters or the last one, which now weakens the Tyrrhenian sea against the opposing rocks: be wise, strain wine, and in brief space cut short long hope. While we speak, envious time will have fled: seize the day, trusting as little as possible in tomorrow.', '{"tu": {"meaning": "you", "grammar": "nominative"}, "ne": {"meaning": "not", "grammar": "negative imperative particle"}, "quaesieris": {"meaning": "you should ask", "grammar": "2nd person singular perfect subjunctive"}, "scire": {"meaning": "to know", "grammar": "infinitive"}, "nefas": {"meaning": "wrong/impious", "grammar": "indeclinable noun"}, "quem": {"meaning": "what", "grammar": "accusative singular interrogative"}, "mihi": {"meaning": "to me", "grammar": "dative"}, "tibi": {"meaning": "to you", "grammar": "dative"}, "finem": {"meaning": "end", "grammar": "accusative singular"}, "di": {"meaning": "gods", "grammar": "nominative plural"}, "dederint": {"meaning": "they have given", "grammar": "3rd person plural perfect subjunctive"}, "Leuconoe": {"meaning": "Leuconoe", "grammar": "vocative"}, "nec": {"meaning": "nor", "grammar": "conjunction"}, "Babylonios": {"meaning": "Babylonian", "grammar": "accusative plural"}, "temptaris": {"meaning": "you should attempt", "grammar": "2nd person singular present subjunctive"}, "numeros": {"meaning": "numbers/calculations", "grammar": "accusative plural"}, "ut": {"meaning": "how", "grammar": "exclamatory"}, "melius": {"meaning": "better", "grammar": "comparative adverb"}, "quidquid": {"meaning": "whatever", "grammar": "indefinite pronoun"}, "erit": {"meaning": "it will be", "grammar": "3rd person singular future"}, "pati": {"meaning": "to endure", "grammar": "infinitive"}, "seu": {"meaning": "whether", "grammar": "conjunction"}, "pluris": {"meaning": "more", "grammar": "accusative plural"}, "hiemes": {"meaning": "winters", "grammar": "accusative plural"}, "tribuit": {"meaning": "has granted", "grammar": "3rd person singular perfect"}, "Iuppiter": {"meaning": "Jupiter", "grammar": "nominative"}, "ultimam": {"meaning": "last", "grammar": "accusative singular"}, "quae": {"meaning": "which", "grammar": "nominative singular relative"}, "nunc": {"meaning": "now", "grammar": "adverb"}, "oppositis": {"meaning": "opposing", "grammar": "ablative plural participle"}, "debilitat": {"meaning": "weakens", "grammar": "3rd person singular present"}, "pumicibus": {"meaning": "rocks", "grammar": "ablative plural"}, "mare": {"meaning": "sea", "grammar": "accusative singular"}, "Tyrrhenum": {"meaning": "Tyrrhenian", "grammar": "accusative singular"}, "sapias": {"meaning": "be wise", "grammar": "2nd person singular present subjunctive"}, "vina": {"meaning": "wines", "grammar": "accusative plural"}, "liques": {"meaning": "strain", "grammar": "2nd person singular present subjunctive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "spatio": {"meaning": "in space", "grammar": "ablative singular"}, "brevi": {"meaning": "brief", "grammar": "ablative singular"}, "spem": {"meaning": "hope", "grammar": "accusative singular"}, "longam": {"meaning": "long", "grammar": "accusative singular"}, "reseces": {"meaning": "cut short", "grammar": "2nd person singular present subjunctive"}, "dum": {"meaning": "while", "grammar": "temporal conjunction"}, "loquimur": {"meaning": "we speak", "grammar": "1st person plural present"}, "fugerit": {"meaning": "will have fled", "grammar": "3rd person singular future perfect"}, "invida": {"meaning": "envious", "grammar": "nominative singular"}, "aetas": {"meaning": "time/age", "grammar": "nominative singular"}, "carpe": {"meaning": "seize", "grammar": "2nd person singular imperative"}, "diem": {"meaning": "day", "grammar": "accusative singular"}, "quam": {"meaning": "as...as possible", "grammar": "with superlative"}, "minimum": {"meaning": "least", "grammar": "superlative"}, "credula": {"meaning": "trusting", "grammar": "nominative singular"}, "postero": {"meaning": "in tomorrow", "grammar": "ablative singular"}}', '- **Negative purpose clause**: "ne quaesieris" - prohibition with subjunctive - **Substantive infinitive**: "scire" as subject of "nefas" - **Indirect question**: "quem...finem" in subjunctive - **Perfect subjunctive**: "dederint" in indirect question - **Exclamatory expression**: "ut melius" + infinitive - **Relative clause**: "quae nunc...mare" describes winter - **Temporal clause**: "dum loquimur" with present indicative - **Future perfect**: "fugerit" - action completed in future', 'Famous carpe diem ode by Horace, advising against fortune-telling and astrology while promoting acceptance of uncertainty and focus on present pleasures. Reflects Epicurean philosophy adapted to Roman sensibilities.', 68, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-007', 'Tacitus Annales 1.1-2', 'Urbem Romam a principio reges habuere; libertatem et consulatum L. Brutus instituit. dictaturae ad tempus sumebantur; neque decemviralis potestas ultra biennium, neque tribunorum militum consulare ius diu valuit. non Cinnae, non Sullae longa dominatio; et Pompei Crassique potentia cito in Caesarem, Lepidi atque Antonii arma in Augustum cesserunt, qui cuncta discordiis civilibus fessa nomine principis sub imperium accepit.', 'Kings held the city of Rome from the beginning; Lucius Brutus established freedom and the consulship. Dictatorships were assumed for a time; neither did the power of the decemvirs last beyond two years, nor did the consular authority of the military tribunes endure long. Not long was the domination of Cinna, not of Sulla; and the power of Pompey and Crassus quickly yielded to Caesar, the arms of Lepidus and Antony to Augustus, who received all things exhausted by civil discord under the name of princeps into his command.', '{"urbem": {"meaning": "city", "grammar": "accusative singular"}, "Romam": {"meaning": "Rome", "grammar": "accusative singular"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "principio": {"meaning": "beginning", "grammar": "ablative singular"}, "reges": {"meaning": "kings", "grammar": "nominative plural"}, "habuere": {"meaning": "they held", "grammar": "3rd person plural perfect"}, "libertatem": {"meaning": "freedom", "grammar": "accusative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "consulatum": {"meaning": "consulship", "grammar": "accusative singular"}, "L.": {"meaning": "Lucius", "grammar": "abbreviation"}, "Brutus": {"meaning": "Brutus", "grammar": "nominative singular"}, "instituit": {"meaning": "he established", "grammar": "3rd person singular perfect"}, "dictaturae": {"meaning": "dictatorships", "grammar": "nominative plural"}, "ad": {"meaning": "for", "grammar": "preposition + accusative"}, "tempus": {"meaning": "time", "grammar": "accusative singular"}, "sumebantur": {"meaning": "were assumed", "grammar": "3rd person plural imperfect passive"}, "neque": {"meaning": "neither", "grammar": "conjunction"}, "decemviralis": {"meaning": "of the decemvirs", "grammar": "nominative singular"}, "potestas": {"meaning": "power", "grammar": "nominative singular"}, "ultra": {"meaning": "beyond", "grammar": "preposition + accusative"}, "biennium": {"meaning": "two years", "grammar": "accusative singular"}, "tribunorum": {"meaning": "of tribunes", "grammar": "genitive plural"}, "militum": {"meaning": "military", "grammar": "genitive plural"}, "consulare": {"meaning": "consular", "grammar": "nominative singular"}, "ius": {"meaning": "authority", "grammar": "nominative singular"}, "diu": {"meaning": "long", "grammar": "adverb"}, "valuit": {"meaning": "endured", "grammar": "3rd person singular perfect"}, "non": {"meaning": "not", "grammar": "negative"}, "Cinnae": {"meaning": "of Cinna", "grammar": "genitive singular"}, "Sullae": {"meaning": "of Sulla", "grammar": "genitive singular"}, "longa": {"meaning": "long", "grammar": "nominative singular"}, "dominatio": {"meaning": "domination", "grammar": "nominative singular"}, "Pompei": {"meaning": "of Pompey", "grammar": "genitive singular"}, "Crassique": {"meaning": "and of Crassus", "grammar": "genitive singular + -que"}, "potentia": {"meaning": "power", "grammar": "nominative singular"}, "cito": {"meaning": "quickly", "grammar": "adverb"}, "in": {"meaning": "to", "grammar": "preposition + accusative"}, "Caesarem": {"meaning": "Caesar", "grammar": "accusative singular"}, "Lepidi": {"meaning": "of Lepidus", "grammar": "genitive singular"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "Antonii": {"meaning": "of Antony", "grammar": "genitive singular"}, "arma": {"meaning": "arms", "grammar": "nominative plural"}, "Augustum": {"meaning": "Augustus", "grammar": "accusative singular"}, "cesserunt": {"meaning": "yielded", "grammar": "3rd person plural perfect"}, "qui": {"meaning": "who", "grammar": "nominative singular relative"}, "cuncta": {"meaning": "all things", "grammar": "accusative plural"}, "discordiis": {"meaning": "by discord", "grammar": "ablative plural"}, "civilibus": {"meaning": "civil", "grammar": "ablative plural"}, "fessa": {"meaning": "exhausted", "grammar": "accusative plural participle"}, "nomine": {"meaning": "under the name", "grammar": "ablative singular"}, "principis": {"meaning": "of princeps", "grammar": "genitive singular"}, "sub": {"meaning": "under", "grammar": "preposition + accusative"}, "imperium": {"meaning": "command", "grammar": "accusative singular"}, "accepit": {"meaning": "he received", "grammar": "3rd person singular perfect"}}', '- **Historical infinitive**: "habuere" could be infinitive for vividness - **Zeugma**: "libertatem et consulatum instituit" - different objects with same verb - **Passive voice**: "sumebantur" shows how dictatorships were taken - **Ellipsis**: "longa [erat]" - verb understood - **Genitive of possession**: "Cinnae, Sullae dominatio" - **Perfect tense**: Multiple perfects show completed historical sequence - **Relative clause**: "qui cuncta...accepit" describes Augustus - **Ablative absolute**: "discordiis civilibus" shows circumstances - **Prepositional phrase**: "nomine principis" - manner of assumption', 'Opening of Tacitus''s Annals, surveying Roman political history from monarchy through republic to empire. Demonstrates Tacitus''s analytical approach to the transition from republic to principate under Augustus.', 78, 'Classical Author', 'Republic');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-008', 'Livy Ab Urbe Condita 1.1', 'Facturusne operae pretium sim, si a primordio urbis res populi Romani perscripserim, nec satis scio nec, si sciam, dicere ausim, cum et vetustas res plus famae quam fidei habeat et nova scriptorum semper excellere conetur priorum gloria. utcumque erit, iuvabit tamen rerum gestarum memoriae me quoque nomen nostrae gentis conditoribus addidisse.', 'Whether I shall accomplish something worthwhile, if I write out the history of the Roman people from the beginning of the city, I neither know sufficiently nor, if I did know, would I dare to say, since both antiquity brings more fame than credibility to affairs and new writers always strive to exceed the glory of their predecessors. However it will be, it will nevertheless be pleasant to have added my name too to the memory of accomplished deeds among the founders of our race.', '{"facturusne": {"meaning": "whether I am about to do", "grammar": "future active participle + -ne"}, "operae": {"meaning": "of work/effort", "grammar": "genitive singular"}, "pretium": {"meaning": "price/worth", "grammar": "accusative singular"}, "sim": {"meaning": "I might be", "grammar": "1st person singular present subjunctive"}, "si": {"meaning": "if", "grammar": "conditional conjunction"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "primordio": {"meaning": "beginning", "grammar": "ablative singular"}, "urbis": {"meaning": "of the city", "grammar": "genitive singular"}, "res": {"meaning": "things/history", "grammar": "accusative plural"}, "populi": {"meaning": "of the people", "grammar": "genitive singular"}, "Romani": {"meaning": "Roman", "grammar": "genitive singular"}, "perscripserim": {"meaning": "I shall have written out", "grammar": "1st person singular future perfect"}, "nec": {"meaning": "neither", "grammar": "conjunction"}, "satis": {"meaning": "sufficiently", "grammar": "adverb"}, "scio": {"meaning": "I know", "grammar": "1st person singular present"}, "sciam": {"meaning": "I should know", "grammar": "1st person singular present subjunctive"}, "dicere": {"meaning": "to say", "grammar": "infinitive"}, "ausim": {"meaning": "I would dare", "grammar": "1st person singular present subjunctive"}, "cum": {"meaning": "since", "grammar": "causal conjunction"}, "et": {"meaning": "both", "grammar": "conjunction"}, "vetustas": {"meaning": "antiquity", "grammar": "nominative singular"}, "plus": {"meaning": "more", "grammar": "comparative"}, "famae": {"meaning": "fame", "grammar": "genitive singular"}, "quam": {"meaning": "than", "grammar": "comparative conjunction"}, "fidei": {"meaning": "credibility", "grammar": "genitive singular"}, "habeat": {"meaning": "brings", "grammar": "3rd person singular present subjunctive"}, "nova": {"meaning": "new", "grammar": "nominative plural"}, "scriptorum": {"meaning": "writers", "grammar": "genitive plural"}, "semper": {"meaning": "always", "grammar": "adverb"}, "excellere": {"meaning": "to exceed", "grammar": "infinitive"}, "conetur": {"meaning": "strives", "grammar": "3rd person singular present subjunctive"}, "priorum": {"meaning": "predecessors", "grammar": "genitive plural"}, "gloria": {"meaning": "glory", "grammar": "ablative singular"}, "utcumque": {"meaning": "however", "grammar": "indefinite adverb"}, "erit": {"meaning": "it will be", "grammar": "3rd person singular future"}, "iuvabit": {"meaning": "it will be pleasant", "grammar": "3rd person singular future"}, "tamen": {"meaning": "nevertheless", "grammar": "conjunction"}, "rerum": {"meaning": "things", "grammar": "genitive plural"}, "gestarum": {"meaning": "accomplished", "grammar": "perfect passive participle, genitive plural"}, "memoriae": {"meaning": "to memory", "grammar": "dative singular"}, "me": {"meaning": "my", "grammar": "accusative"}, "quoque": {"meaning": "too", "grammar": "adverb"}, "nomen": {"meaning": "name", "grammar": "accusative singular"}, "nostrae": {"meaning": "our", "grammar": "genitive singular"}, "gentis": {"meaning": "race", "grammar": "genitive singular"}, "conditoribus": {"meaning": "founders", "grammar": "ablative plural"}, "addidisse": {"meaning": "to have added", "grammar": "perfect infinitive"}}', '- **Indirect question**: "facturusne...sim" with subjunctive - **Future active participle**: "facturus" indicates intended action - **Genitive of value**: "operae pretium" - worth the effort - **Future perfect in temporal clause**: "perscripserim" - **Conditional sentences**: Mixed conditions with indicative and subjunctive - **Causal clause**: "cum...habeat" with subjunctive - **Comparative construction**: "plus famae quam fidei" - **Perfect infinitive**: "addidisse" in indirect statement', 'Preface to Livy''s monumental history of Rome, expressing authorial uncertainty about undertaking such a vast project while defending the value of historical writing. Reflects Augustan cultural program promoting Roman historical consciousness.', 69, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-009', 'Sallust Bellum Catilinae 1-2', 'Omnis homines qui sese student praestare ceteris animalibus summa ope niti decet, ne vitam silentio transeant tamquam pecora, quae natura prona atque ventri oboedientia finxit. sed nostra omnis vis in animo et corpore sita est: animi imperio, corporis servitio magis utimur; alterum nobis cum dis, alterum cum beluis commune est.', 'It is fitting that all men who strive to excel other animals should endeavor with the greatest effort, lest they pass through life in silence like cattle, which nature formed prone and obedient to the belly. But all our strength is situated in mind and body: we use the command of the mind, the service of the body more; the one is common to us with the gods, the other with beasts.', '{"omnis": {"meaning": "all", "grammar": "nominative singular"}, "homines": {"meaning": "men", "grammar": "accusative plural"}, "qui": {"meaning": "who", "grammar": "nominative plural relative pronoun"}, "sese": {"meaning": "themselves", "grammar": "accusative reflexive"}, "student": {"meaning": "they strive", "grammar": "3rd person plural present"}, "praestare": {"meaning": "to excel/surpass", "grammar": "infinitive"}, "ceteris": {"meaning": "other", "grammar": "dative plural"}, "animalibus": {"meaning": "animals", "grammar": "dative plural"}, "summa": {"meaning": "greatest", "grammar": "ablative singular"}, "ope": {"meaning": "effort/power", "grammar": "ablative singular"}, "niti": {"meaning": "to strive", "grammar": "infinitive"}, "decet": {"meaning": "it is fitting", "grammar": "3rd person singular present impersonal"}, "ne": {"meaning": "lest", "grammar": "negative purpose conjunction"}, "vitam": {"meaning": "life", "grammar": "accusative singular"}, "silentio": {"meaning": "in silence", "grammar": "ablative singular"}, "transeant": {"meaning": "they should pass through", "grammar": "3rd person plural present subjunctive"}, "tamquam": {"meaning": "like/as if", "grammar": "adverb"}, "pecora": {"meaning": "cattle", "grammar": "nominative/accusative plural"}, "quae": {"meaning": "which", "grammar": "accusative plural relative"}, "natura": {"meaning": "nature", "grammar": "nominative singular"}, "prona": {"meaning": "prone", "grammar": "accusative plural"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "ventri": {"meaning": "to the belly", "grammar": "dative singular"}, "oboedientia": {"meaning": "obedient", "grammar": "accusative plural"}, "finxit": {"meaning": "formed", "grammar": "3rd person singular perfect"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "nostra": {"meaning": "our", "grammar": "nominative singular"}, "vis": {"meaning": "strength", "grammar": "nominative singular"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "animo": {"meaning": "mind", "grammar": "ablative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "corpore": {"meaning": "body", "grammar": "ablative singular"}, "sita": {"meaning": "situated", "grammar": "perfect participle, nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}, "animi": {"meaning": "of mind", "grammar": "genitive singular"}, "imperio": {"meaning": "command", "grammar": "ablative singular"}, "corporis": {"meaning": "of body", "grammar": "genitive singular"}, "servitio": {"meaning": "service", "grammar": "ablative singular"}, "magis": {"meaning": "more", "grammar": "comparative adverb"}, "utimur": {"meaning": "we use", "grammar": "1st person plural present deponent"}, "alterum": {"meaning": "the other", "grammar": "nominative singular"}, "nobis": {"meaning": "to us", "grammar": "dative plural"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "dis": {"meaning": "gods", "grammar": "ablative plural"}, "beluis": {"meaning": "beasts", "grammar": "ablative plural"}, "commune": {"meaning": "common", "grammar": "nominative singular"}}', '- **Relative clause**: "qui sese student" describes "homines" - **Reflexive pronoun**: "sese" emphasizes self-direction - **Complementary infinitive**: "praestare" completes "student" - **Impersonal construction**: "decet" with accusative subject - **Purpose clause**: "ne transeant" with subjunctive - **Relative clause**: "quae natura...finxit" describes cattle - **Perfect passive participle**: "sita" describes location of strength - **Ablative of means**: "imperio, servitio" show how mind and body are used - **Deponent verb**: "utimur" has passive form, active meaning', 'Opening of Sallust''s monograph on Catiline''s conspiracy, establishing philosophical framework about human nature and the duty to achieve excellence. Reflects influence of Greek philosophy on Roman historical writing.', 72, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-010', 'Pliny Letters 1.1', 'Frequenter hortatus es ut epistulas, si quas paulo curatius scripsissem, colligerem publicaremque. collegi non servato temporis ordine, neque enim historiam componebam, sed ut quaeque in manus venerat. superest ut nec te consilii nec me paeniteat obsequii. ita enim fiet ut eas quae adhuc neglectae iacent requiram et si quas addidero non supprimam.', 'You have frequently urged me to collect and publish letters, if I had written any with somewhat greater care. I have collected them without preserving order of time, for I was not composing a history, but as each had come to hand. It remains that neither you regret the advice nor I the compliance. For thus it will happen that I shall seek out those which still lie neglected and if I add any I shall not suppress them.', '{"frequenter": {"meaning": "frequently", "grammar": "adverb"}, "hortatus": {"meaning": "having urged", "grammar": "perfect deponent participle"}, "es": {"meaning": "you are/have", "grammar": "2nd person singular present"}, "ut": {"meaning": "as", "grammar": "comparative"}, "epistulas": {"meaning": "letters", "grammar": "accusative plural"}, "si": {"meaning": "if", "grammar": "conditional conjunction"}, "quas": {"meaning": "which", "grammar": "accusative plural relative pronoun"}, "paulo": {"meaning": "somewhat", "grammar": "ablative of degree"}, "curatius": {"meaning": "more carefully", "grammar": "comparative adverb"}, "scripsissem": {"meaning": "I had written", "grammar": "1st person singular pluperfect subjunctive"}, "colligerem": {"meaning": "I should collect", "grammar": "1st person singular imperfect subjunctive"}, "publicaremque": {"meaning": "and publish", "grammar": "1st person singular imperfect subjunctive + -que"}, "collegi": {"meaning": "I have collected", "grammar": "1st person singular perfect"}, "non": {"meaning": "not", "grammar": "negative"}, "servato": {"meaning": "preserved", "grammar": "ablative absolute"}, "temporis": {"meaning": "of time", "grammar": "genitive singular"}, "ordine": {"meaning": "order", "grammar": "ablative singular"}, "neque": {"meaning": "for...not", "grammar": "conjunction"}, "enim": {"meaning": "for", "grammar": "conjunction"}, "historiam": {"meaning": "history", "grammar": "accusative singular"}, "componebam": {"meaning": "I was composing", "grammar": "1st person singular imperfect"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "quaeque": {"meaning": "each", "grammar": "nominative singular"}, "in": {"meaning": "into", "grammar": "preposition + accusative"}, "manus": {"meaning": "hands", "grammar": "accusative plural"}, "venerat": {"meaning": "had come", "grammar": "3rd person singular pluperfect"}, "superest": {"meaning": "it remains", "grammar": "3rd person singular present"}, "nec": {"meaning": "neither", "grammar": "conjunction"}, "te": {"meaning": "you", "grammar": "accusative"}, "consilii": {"meaning": "of advice", "grammar": "genitive singular"}, "me": {"meaning": "me", "grammar": "accusative"}, "paeniteat": {"meaning": "regret", "grammar": "3rd person singular present subjunctive"}, "obsequii": {"meaning": "of compliance", "grammar": "genitive singular"}, "ita": {"meaning": "thus", "grammar": "adverb"}, "fiet": {"meaning": "it will happen", "grammar": "3rd person singular future"}, "eas": {"meaning": "those", "grammar": "accusative plural"}, "adhuc": {"meaning": "still", "grammar": "adverb"}, "neglectae": {"meaning": "neglected", "grammar": "accusative plural participle"}, "iacent": {"meaning": "lie", "grammar": "3rd person plural present"}, "requiram": {"meaning": "I shall seek out", "grammar": "1st person singular future"}, "addidero": {"meaning": "I shall have added", "grammar": "1st person singular future perfect"}, "supprimam": {"meaning": "I shall suppress", "grammar": "1st person singular future"}}', '- **Perfect deponent**: "hortatus es" - deponent perfect with active meaning - **Indirect command**: "ut...colligerem publicaremque" with subjunctive - **Mixed condition**: "si scripsissem" - past contrary to fact condition - **Ablative absolute**: "non servato temporis ordine" - circumstances - **Result clause**: "ut nec te...paeniteat" with subjunctive - **Temporal clause**: "ut quaeque...venerat" - as each had come - **Future perfect**: "addidero" in temporal clause', 'Preface to Pliny the Younger''s published correspondence, explaining editorial decisions and literary purpose. Shows the careful curation of personal letters as literary works in Roman culture.', 64, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-011', 'Quintilian Institutio Oratoria 1.1.1-3', 'Nonnulli existimant non esse ante septimum annum discendis litteris initiandum puerum, quod ea demum aetas et intellectu praeceptorum capax sit et laborem pati possit. in qua sententia plerique veterum fuere, quorum princeps Hesiodus. melius autem ei qui Chrysippum sequuntur placet: nam is usque ab ipsis incunabulis formandum putat animum.', 'Some think that a boy should not be introduced to learning letters before the seventh year, because that age at last is both capable of understanding instructions and able to endure labor. Most of the ancients were of this opinion, chief among whom was Hesiod. But those who follow Chrysippus have a better view: for he thinks the mind should be formed from the very cradle.', '{"nonnulli": {"meaning": "some people", "grammar": "nominative plural"}, "existimant": {"meaning": "they think", "grammar": "3rd person plural present"}, "non": {"meaning": "not", "grammar": "negative adverb"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "ante": {"meaning": "before", "grammar": "preposition + accusative"}, "septimum": {"meaning": "seventh", "grammar": "accusative singular ordinal"}, "annum": {"meaning": "year", "grammar": "accusative singular"}, "discendis": {"meaning": "to be learned", "grammar": "gerund, dative plural"}, "litteris": {"meaning": "letters/literature", "grammar": "dative plural"}, "initiandum": {"meaning": "to be introduced", "grammar": "gerundive, accusative singular"}, "puerum": {"meaning": "boy", "grammar": "accusative singular"}, "quod": {"meaning": "because", "grammar": "causal conjunction"}, "ea": {"meaning": "that", "grammar": "nominative singular"}, "demum": {"meaning": "at last", "grammar": "adverb"}, "aetas": {"meaning": "age", "grammar": "nominative singular"}, "et": {"meaning": "both...and", "grammar": "correlative"}, "intellectu": {"meaning": "understanding", "grammar": "ablative singular"}, "praeceptorum": {"meaning": "of instructions", "grammar": "genitive plural"}, "capax": {"meaning": "capable", "grammar": "nominative singular"}, "sit": {"meaning": "is", "grammar": "3rd person singular present subjunctive"}, "laborem": {"meaning": "labor", "grammar": "accusative singular"}, "pati": {"meaning": "to endure", "grammar": "infinitive"}, "possit": {"meaning": "is able", "grammar": "3rd person singular present subjunctive"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "qua": {"meaning": "which", "grammar": "ablative singular relative"}, "sententia": {"meaning": "opinion", "grammar": "ablative singular"}, "plerique": {"meaning": "most", "grammar": "nominative plural"}, "veterum": {"meaning": "of the ancients", "grammar": "genitive plural"}, "fuere": {"meaning": "were", "grammar": "3rd person plural perfect"}, "quorum": {"meaning": "of whom", "grammar": "genitive plural relative"}, "princeps": {"meaning": "chief", "grammar": "nominative singular"}, "Hesiodus": {"meaning": "Hesiod", "grammar": "nominative"}, "melius": {"meaning": "better", "grammar": "comparative"}, "autem": {"meaning": "however", "grammar": "conjunction"}, "ei": {"meaning": "to those", "grammar": "dative plural"}, "qui": {"meaning": "who", "grammar": "nominative plural relative"}, "Chrysippum": {"meaning": "Chrysippus", "grammar": "accusative"}, "sequuntur": {"meaning": "follow", "grammar": "3rd person plural present"}, "placet": {"meaning": "it pleases", "grammar": "3rd person singular present"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "is": {"meaning": "he", "grammar": "nominative"}, "usque": {"meaning": "right", "grammar": "adverb"}, "ab": {"meaning": "from", "grammar": "preposition + ablative"}, "ipsis": {"meaning": "the very", "grammar": "ablative plural"}, "incunabulis": {"meaning": "cradle", "grammar": "ablative plural"}, "formandum": {"meaning": "to be formed", "grammar": "gerundive, accusative singular"}, "putat": {"meaning": "thinks", "grammar": "3rd person singular present"}, "animum": {"meaning": "mind", "grammar": "accusative singular"}}', '- **Indirect statement**: "non esse...initiandum" with accusative subject "puerum" - **Gerund**: "discendis" expresses purpose - **Gerundive**: "initiandum" shows necessity/obligation - **Causal clause**: "quod ea...possit" with subjunctive - **Relative clause**: "in qua sententia" refers to the opinion - **Genitive of characteristic**: "intellectu praeceptorum capax" - **Complementary infinitive**: "pati" completes "possit" - **Passive periphrastic**: "formandum esse" understood', 'From Quintilian''s comprehensive treatise on education and rhetoric, discussing optimal age for beginning formal education. Reflects systematic Roman approach to pedagogy and intellectual development.', 56, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-012', 'Seneca Epistulae 1.1-2', 'Ita fac, mi Lucili: vindica te tibi, et tempus quod adhuc aut auferebatur aut subripiebatur aut excidebat collige et serva. persuade tibi hoc sic esse ut scribo: quaedam tempora eripiuntur nobis, quaedam subducuntur, quaedam effluunt. turpissima tamen est iactura quae per neglegentiam fit.', 'Do thus, my Lucilius: claim yourself for yourself, and gather and preserve the time which was until now either being taken away or stolen or was falling away. Convince yourself that this is so as I write: some times are snatched from us, some are stolen away, some flow out. Most shameful, however, is the loss which happens through negligence.', '{"ita": {"meaning": "thus/so", "grammar": "adverb"}, "fac": {"meaning": "do", "grammar": "2nd person singular imperative"}, "mi": {"meaning": "my", "grammar": "vocative of possessive"}, "Lucili": {"meaning": "Lucilius", "grammar": "vocative"}, "vindica": {"meaning": "claim", "grammar": "2nd person singular imperative"}, "te": {"meaning": "yourself", "grammar": "accusative"}, "tibi": {"meaning": "for yourself", "grammar": "dative"}, "et": {"meaning": "and", "grammar": "conjunction"}, "tempus": {"meaning": "time", "grammar": "accusative singular"}, "quod": {"meaning": "which", "grammar": "nominative singular relative"}, "adhuc": {"meaning": "until now", "grammar": "adverb"}, "aut": {"meaning": "either/or", "grammar": "conjunction"}, "auferebatur": {"meaning": "was being taken away", "grammar": "3rd person singular imperfect passive"}, "subripiebatur": {"meaning": "was being stolen", "grammar": "3rd person singular imperfect passive"}, "excidebat": {"meaning": "was falling away", "grammar": "3rd person singular imperfect"}, "collige": {"meaning": "gather", "grammar": "2nd person singular imperative"}, "serva": {"meaning": "preserve", "grammar": "2nd person singular imperative"}, "persuade": {"meaning": "convince", "grammar": "2nd person singular imperative"}, "hoc": {"meaning": "this", "grammar": "accusative singular"}, "sic": {"meaning": "so", "grammar": "adverb"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "ut": {"meaning": "as", "grammar": "comparative"}, "scribo": {"meaning": "I write", "grammar": "1st person singular present"}, "quaedam": {"meaning": "some", "grammar": "nominative plural"}, "tempora": {"meaning": "times", "grammar": "nominative plural"}, "eripiuntur": {"meaning": "are snatched", "grammar": "3rd person plural present passive"}, "nobis": {"meaning": "from us", "grammar": "ablative/dative plural"}, "subducuntur": {"meaning": "are stolen away", "grammar": "3rd person plural present passive"}, "effluunt": {"meaning": "flow out", "grammar": "3rd person plural present"}, "turpissima": {"meaning": "most shameful", "grammar": "nominative singular superlative"}, "tamen": {"meaning": "however", "grammar": "conjunction"}, "iactura": {"meaning": "loss", "grammar": "nominative singular"}, "quae": {"meaning": "which", "grammar": "nominative singular relative"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "neglegentiam": {"meaning": "negligence", "grammar": "accusative singular"}, "fit": {"meaning": "happens", "grammar": "3rd person singular present"}}', '- **Imperative series**: "fac, vindica, collige, serva" - escalating commands - **Reflexive construction**: "vindica te tibi" - claim yourself for yourself - **Relative clause**: "quod adhuc...excidebat" describes time - **Triple alternative**: "aut...aut...aut" - three ways time is lost - **Indirect statement**: "hoc sic esse" with infinitive - **Parallel structure**: "quaedam...quaedam...quaedam" - systematic analysis - **Superlative**: "turpissima" emphasizes worst type of loss', 'Opening of Seneca''s first moral letter to Lucilius, establishing the central Stoic theme of valuing time and self-possession. Reflects philosophical correspondence tradition of Roman imperial period.', 58, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-013', 'Augustine Confessions 1.1.1', 'Magnus es, domine, et laudabilis valde; magna virtus tua et sapientiae tuae non est numerus. et laudare te vult homo, aliqua portio creaturae tuae, et homo circumferens mortalitatem suam, circumferens testimonium peccati sui et testimonium quia resistis superbis: et tamen laudare te vult homo, aliqua portio creaturae tuae.', 'Great are you, Lord, and greatly to be praised; great is your power and of your wisdom there is no number. And man wants to praise you, some portion of your creation, and man carrying around his mortality, carrying around the testimony of his sin and the testimony that you resist the proud: and yet man wants to praise you, some portion of your creation.', '{"magnus": {"meaning": "great", "grammar": "nominative singular"}, "es": {"meaning": "you are", "grammar": "2nd person singular present"}, "domine": {"meaning": "Lord", "grammar": "vocative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "laudabilis": {"meaning": "praiseworthy", "grammar": "nominative singular"}, "valde": {"meaning": "greatly", "grammar": "adverb"}, "magna": {"meaning": "great", "grammar": "nominative singular"}, "virtus": {"meaning": "power/virtue", "grammar": "nominative singular"}, "tua": {"meaning": "your", "grammar": "nominative singular"}, "sapientiae": {"meaning": "of wisdom", "grammar": "genitive singular"}, "tuae": {"meaning": "your", "grammar": "genitive singular"}, "non": {"meaning": "not", "grammar": "negative adverb"}, "est": {"meaning": "there is", "grammar": "3rd person singular present"}, "numerus": {"meaning": "number/measure", "grammar": "nominative singular"}, "laudare": {"meaning": "to praise", "grammar": "infinitive"}, "te": {"meaning": "you", "grammar": "accusative"}, "vult": {"meaning": "wants", "grammar": "3rd person singular present"}, "homo": {"meaning": "man", "grammar": "nominative singular"}, "aliqua": {"meaning": "some", "grammar": "nominative singular"}, "portio": {"meaning": "portion", "grammar": "nominative singular"}, "creaturae": {"meaning": "of creation", "grammar": "genitive singular"}, "circumferens": {"meaning": "carrying around", "grammar": "present participle"}, "mortalitatem": {"meaning": "mortality", "grammar": "accusative singular"}, "suam": {"meaning": "his own", "grammar": "accusative singular"}, "testimonium": {"meaning": "testimony", "grammar": "accusative singular"}, "peccati": {"meaning": "of sin", "grammar": "genitive singular"}, "sui": {"meaning": "his own", "grammar": "genitive singular"}, "quia": {"meaning": "that", "grammar": "conjunction"}, "resistis": {"meaning": "you resist", "grammar": "2nd person singular present"}, "superbis": {"meaning": "the proud", "grammar": "dative plural"}, "tamen": {"meaning": "yet", "grammar": "conjunction"}}', '- **Predicate adjectives**: "magnus, laudabilis" with copulative "es" - **Vocative address**: "domine" - direct address to God - **Gerundive**: "laudabilis" - expressing obligation/worthiness - **Existential construction**: "non est numerus" - there is no measure - **Complementary infinitive**: "laudare" completes "vult" - **Present participle**: "circumferens" describes ongoing action - **Indirect statement**: "quia resistis" introduces known fact - **Repetition**: "et tamen...creaturae tuae" emphasizes paradox', 'Opening of Augustine''s Confessions, the first major Christian autobiography, combining classical rhetorical training with Christian theology. Expresses the paradox of finite beings praising infinite God.', 56, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-014', 'Boethius Consolatio 1.1.1-3', 'Carmina qui quondam studio florente peregi, flebilis heu maestos cogor inire modos. ecce mihi lacerae dictant scribenda Camenae et veris elegi fletibus ora rigant. has saltem nullus potuit pervincere terror, ne nostrum comites prosequerentur iter.', 'I who once completed songs with flourishing zeal, alas, am compelled to enter upon tearful sad measures. Behold, the torn Muses dictate things to be written to me and true elegies wet my face with tears. At least no terror could overcome these, that they should not accompany our journey as companions.', '{"carmina": {"meaning": "songs/poems", "grammar": "accusative plural"}, "qui": {"meaning": "I who", "grammar": "nominative relative pronoun"}, "quondam": {"meaning": "once/formerly", "grammar": "adverb"}, "studio": {"meaning": "with zeal", "grammar": "ablative singular"}, "florente": {"meaning": "flourishing", "grammar": "present participle, ablative"}, "peregi": {"meaning": "I completed", "grammar": "1st person singular perfect"}, "flebilis": {"meaning": "tearful", "grammar": "nominative singular"}, "heu": {"meaning": "alas", "grammar": "interjection"}, "maestos": {"meaning": "sad", "grammar": "accusative plural"}, "cogor": {"meaning": "I am compelled", "grammar": "1st person singular present passive"}, "inire": {"meaning": "to enter upon", "grammar": "infinitive"}, "modos": {"meaning": "measures/songs", "grammar": "accusative plural"}, "ecce": {"meaning": "behold", "grammar": "interjection"}, "mihi": {"meaning": "to me", "grammar": "dative"}, "lacerae": {"meaning": "torn", "grammar": "nominative plural"}, "dictant": {"meaning": "dictate", "grammar": "3rd person plural present"}, "scribenda": {"meaning": "to be written", "grammar": "gerundive, accusative plural"}, "Camenae": {"meaning": "Muses", "grammar": "nominative plural"}, "veris": {"meaning": "true", "grammar": "ablative plural"}, "elegi": {"meaning": "elegies", "grammar": "nominative plural"}, "fletibus": {"meaning": "with tears", "grammar": "ablative plural"}, "ora": {"meaning": "face", "grammar": "accusative plural"}, "rigant": {"meaning": "wet", "grammar": "3rd person plural present"}, "has": {"meaning": "these", "grammar": "accusative plural"}, "saltem": {"meaning": "at least", "grammar": "adverb"}, "nullus": {"meaning": "no", "grammar": "nominative singular"}, "potuit": {"meaning": "could", "grammar": "3rd person singular perfect"}, "pervincere": {"meaning": "overcome", "grammar": "infinitive"}, "terror": {"meaning": "terror", "grammar": "nominative singular"}, "ne": {"meaning": "that...not", "grammar": "negative purpose"}, "nostrum": {"meaning": "our", "grammar": "accusative singular"}, "comites": {"meaning": "companions", "grammar": "nominative plural"}, "prosequerentur": {"meaning": "they should accompany", "grammar": "3rd person plural imperfect subjunctive"}, "iter": {"meaning": "journey", "grammar": "accusative singular"}}', '- **Relative pronoun**: "qui" refers to implied "ego" - **Ablative absolute**: "studio florente" - temporal circumstance - **Perfect tense**: "peregi" - completed past action - **Passive voice**: "cogor" - external compulsion - **Complementary infinitive**: "inire" completes "cogor" - **Present participle**: "florente" in ablative absolute - **Gerundive**: "scribenda" - things that must be written - **Purpose clause**: "ne...prosequerentur" with subjunctive', 'Opening of Boethius''s Consolation of Philosophy, written while imprisoned awaiting execution. Represents the end of classical antiquity and synthesis of pagan learning with Christian thought.', 43, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-015', 'Claudian De Raptu Proserpinae 1.1-6', 'Infernas raptor Ditis secreta per umbras ducere temptavit caeco pallore viventem Persephonem. tremuit Chaos horriferis ullis vocibus et Stygio personuit atra sono Tartara; concussae maerent Acheronte carinae.', 'Why not?', '{"infernas": {"meaning": "of the underworld", "grammar": "accusative plural"}, "raptor": {"meaning": "ravisher", "grammar": "nominative singular"}, "Ditis": {"meaning": "of Dis/Pluto", "grammar": "genitive singular"}, "secreta": {"meaning": "secrets/hidden places", "grammar": "accusative plural"}, "per": {"meaning": "through", "grammar": "preposition + accusative"}, "umbras": {"meaning": "shades/shadows", "grammar": "accusative plural"}, "ducere": {"meaning": "to lead", "grammar": "infinitive"}, "temptavit": {"meaning": "he attempted", "grammar": "3rd person singular perfect"}, "caeco": {"meaning": "blind", "grammar": "ablative singular"}, "pallore": {"meaning": "with pallor", "grammar": "ablative singular"}, "viventem": {"meaning": "living", "grammar": "present participle, accusative"}, "Persephonem": {"meaning": "Persephone", "grammar": "accusative"}, "tremuit": {"meaning": "trembled", "grammar": "3rd person singular perfect"}, "Chaos": {"meaning": "Chaos", "grammar": "nominative singular"}, "horriferis": {"meaning": "horrible", "grammar": "ablative plural"}, "ullis": {"meaning": "wailing", "grammar": "ablative plural"}, "vocibus": {"meaning": "voices", "grammar": "ablative plural"}, "et": {"meaning": "and", "grammar": "conjunction"}, "Stygio": {"meaning": "Stygian", "grammar": "ablative singular"}, "personuit": {"meaning": "resounded", "grammar": "3rd person singular perfect"}, "atra": {"meaning": "dark", "grammar": "nominative plural"}, "sono": {"meaning": "with sound", "grammar": "ablative singular"}, "Tartara": {"meaning": "Tartarus", "grammar": "nominative plural"}, "concussae": {"meaning": "shaken", "grammar": "nominative plural participle"}, "maerent": {"meaning": "grieve", "grammar": "3rd person plural present"}, "Acheronte": {"meaning": "by Acheron", "grammar": "ablative singular"}, "carinae": {"meaning": "boats", "grammar": "nominative plural"}}', '- **Genitive of possession**: "raptor Ditis" - Pluto the ravisher - **Adjective as noun**: "infernas secreta" - underworld secrets - **Ablative of manner**: "caeco pallore" - with blind pallor - **Present participle**: "viventem" describes Persephone''s state - **Complementary infinitive**: "ducere" completes "temptavit" - **Ablative of cause**: "horriferis...vocibus" - because of voices - **Perfect tense**: "tremuit, personuit" - completed actions - **Ablative of place**: "Acheronte" - by the river Acheron', 'From Claudian''s epic on the rape of Proserpina, representing the revival of classical mythological poetry in late antiquity. Shows the continuing power of traditional themes in Christian-era literature.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-032', 'Basic Emotion', 'Tristis sum', 'I am sad', '{"tristis": {"meaning": "sad", "grammar": "adjective, nominative singular"}, "sum": {"meaning": "I am", "grammar": "1st person singular present"}}', '- **Predicate adjective**: "tristis" describes the subject''s emotional state - **Copulative verb**: "sum" links subject with adjective - **Emotional expression**: Basic feeling vocabulary', 'Romans valued emotional control but recognized natural human feelings. Expressing sadness was acceptable in appropriate contexts.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-033', 'Simple Command', 'Audi me!', 'Listen to me!', '{"audi": {"meaning": "listen", "grammar": "2nd person singular imperative"}, "me": {"meaning": "me", "grammar": "accusative singular"}}', '- **Imperative mood**: "audi" gives a direct command - **Direct object**: "me" receives the action of listening - **Urgent request**: Demands immediate attention', 'Direct commands were common in Roman household and social relationships, reflecting hierarchical social structure.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-034', 'Basic Number', 'Quattuor equi', 'Four horses', '{"quattuor": {"meaning": "four", "grammar": "indeclinable numeral"}, "equi": {"meaning": "horses", "grammar": "nominative plural"}}', '- **Cardinal number**: "quattuor" is indeclinable - **Plural noun**: "equi" is plural of "equus" - **Counting animals**: Basic enumeration', 'Horses were valuable animals for transportation, warfare, and sport. Four-horse chariots (quadrigae) were used in races and triumphs.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'BEG-035', 'Simple Weather', 'Pluit hodie', 'It''s raining today', '{"pluit": {"meaning": "it rains", "grammar": "3rd person singular present"}, "hodie": {"meaning": "today", "grammar": "adverb"}}', '- **Impersonal verb**: "pluit" has no personal subject - **Weather expression**: Describes current atmospheric conditions - **Present tense**: Ongoing action', 'Weather was important for agriculture, travel, and daily activities. Romans paid close attention to meteorological signs.', 2, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-036', 'Catullus Poem 85', 'Odi et amo. quare id faciam, fortasse requiris?', 'I hate and I love. Why I do this, perhaps you ask?', '{"odi": {"meaning": "I hate", "grammar": "perfect with present meaning"}, "et": {"meaning": "and", "grammar": "conjunction"}, "amo": {"meaning": "I love", "grammar": "1st person singular present"}, "quare": {"meaning": "why", "grammar": "interrogative adverb"}, "id": {"meaning": "this", "grammar": "accusative singular"}, "faciam": {"meaning": "I do", "grammar": "1st person singular present subjunctive"}, "fortasse": {"meaning": "perhaps", "grammar": "adverb"}, "requiris": {"meaning": "you ask", "grammar": "2nd person singular present"}}', '- **Defective verb**: "odi" is perfect in form, present in meaning - **Paradox structure**: "odi et amo" creates emotional contradiction - **Deliberative subjunctive**: "faciam" in indirect question - **Direct address**: "requiris" assumes reader''s curiosity', 'Famous paradox from Catullus expressing the complexity of love relationships. Shows the poet''s psychological insight and emotional honesty.', 8, 'Catullus', 'Poems');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-037', 'Caesar''s Famous Quote', 'Veni, vidi, vici.', 'I came, I saw, I conquered.', '{"veni": {"meaning": "I came", "grammar": "1st person singular perfect"}, "vidi": {"meaning": "I saw", "grammar": "1st person singular perfect"}, "vici": {"meaning": "I conquered", "grammar": "1st person singular perfect"}}', '- **Perfect tense**: All three verbs show completed action - **Tricolon**: Three parallel clauses create rhetorical effect - **Asyndeton**: No conjunctions between clauses for impact - **Historical present effect**: Perfect suggests immediate past', 'Caesar''s famous message to the Senate after his swift victory at Zela. Exemplifies Roman military efficiency and laconic communication style.', 3, 'Caesar', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-038', 'Basic Roman Greeting', 'Ave atque vale, amice care!', 'Hail and farewell, dear friend!', '{"ave": {"meaning": "hail", "grammar": "imperative greeting"}, "atque": {"meaning": "and", "grammar": "conjunction"}, "vale": {"meaning": "farewell", "grammar": "imperative"}, "amice": {"meaning": "friend", "grammar": "vocative singular"}, "care": {"meaning": "dear", "grammar": "vocative singular"}}', '- **Greeting formulas**: "ave" and "vale" are standard expressions - **Coordinating conjunction**: "atque" links the greetings - **Vocative case**: "amice care" in direct address - **Adjective agreement**: "care" agrees with "amice"', 'Traditional Roman formula combining greeting and farewell, often used on tombstones. Shows Roman awareness of life''s transitory nature.', 5, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-039', 'Simple Philosophical Statement', 'Vita brevis est, ars longa.', 'Life is short, art is long.', '{"vita": {"meaning": "life", "grammar": "nominative singular"}, "brevis": {"meaning": "short", "grammar": "nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}, "ars": {"meaning": "art", "grammar": "nominative singular"}, "longa": {"meaning": "long", "grammar": "nominative singular"}}', '- **Predicate adjectives**: "brevis" and "longa" describe subjects - **Parallel structure**: Two balanced statements create contrast - **Copulative verb**: "est" understood in second clause - **Philosophical maxim**: Expresses universal truth', 'Adaptation of Hippocrates'' medical aphorism. Romans valued both practical skills and enduring artistic achievement.', 5, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'intermediate', 'INT-040', 'Basic Question About Location', 'Unde venis? Ad forum eo.', 'Where do you come from? I''m going to the forum.', '{"unde": {"meaning": "from where", "grammar": "interrogative adverb"}, "venis": {"meaning": "you come", "grammar": "2nd person singular present"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "forum": {"meaning": "forum", "grammar": "accusative singular"}, "eo": {"meaning": "I go", "grammar": "1st person singular present"}}', '- **Interrogative of place**: "unde" asks about origin - **Prepositional phrase**: "ad forum" indicates destination - **Conversational exchange**: Question and answer pattern - **Irregular verb**: "eo" from "ire" - to go', 'Typical daily conversation in Roman city. The forum was the center of civic, commercial, and social life.', 6, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-041', 'Cicero on Friendship', 'Amicitia nihil aliud nisi omnium divinarum humanarumque rerum cum benevolentia et caritate consensio.', 'Friendship is nothing other than agreement in all divine and human affairs with goodwill and affection.', '{"amicitia": {"meaning": "friendship", "grammar": "nominative singular"}, "nihil": {"meaning": "nothing", "grammar": "indeclinable"}, "aliud": {"meaning": "other", "grammar": "nominative singular neuter"}, "nisi": {"meaning": "except/other than", "grammar": "conjunction"}, "omnium": {"meaning": "of all", "grammar": "genitive plural"}, "divinarum": {"meaning": "divine", "grammar": "genitive plural"}, "humanarumque": {"meaning": "and human", "grammar": "genitive plural + -que"}, "rerum": {"meaning": "affairs", "grammar": "genitive plural"}, "cum": {"meaning": "with", "grammar": "preposition + ablative"}, "benevolentia": {"meaning": "goodwill", "grammar": "ablative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "caritate": {"meaning": "affection", "grammar": "ablative singular"}, "consensio": {"meaning": "agreement", "grammar": "nominative singular"}}', '- **Predicate nominative**: "consensio" defines "amicitia" - **Genitive of specification**: "omnium...rerum" specifies scope - **Coordinated adjectives**: "divinarum humanarumque" modify "rerum" - **Prepositional phrase**: "cum benevolentia et caritate" shows manner - **Abstract nouns**: All key terms express philosophical concepts', 'From Cicero''s "De Amicitia," defining the nature of true friendship. Reflects Roman values of loyalty and shared principles in relationships.', 13, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-042', 'Seneca on Time', 'Non est quod putes ullum tempus tibi vacuum esse a virtutibus, si omnes horas occupaveris.', 'There is no reason why you should think any time empty of virtues for yourself, if you have occupied all hours.', '{"non": {"meaning": "not", "grammar": "negative"}, "est": {"meaning": "there is", "grammar": "3rd person singular present"}, "quod": {"meaning": "reason why", "grammar": "neuter relative"}, "putes": {"meaning": "you should think", "grammar": "2nd person singular present subjunctive"}, "ullum": {"meaning": "any", "grammar": "accusative singular"}, "tempus": {"meaning": "time", "grammar": "accusative singular"}, "tibi": {"meaning": "for you", "grammar": "dative singular"}, "vacuum": {"meaning": "empty", "grammar": "accusative singular"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "a": {"meaning": "from", "grammar": "preposition + ablative"}, "virtutibus": {"meaning": "virtues", "grammar": "ablative plural"}, "si": {"meaning": "if", "grammar": "conditional conjunction"}, "omnes": {"meaning": "all", "grammar": "accusative plural"}, "horas": {"meaning": "hours", "grammar": "accusative plural"}, "occupaveris": {"meaning": "you have occupied", "grammar": "2nd person singular future perfect"}}', '- **Relative clause**: "quod putes" - reason why you think - **Subjunctive in relative clause**: "putes" shows characteristic - **Accusative + infinitive**: "tempus...esse" in indirect statement - **Conditional sentence**: "si occupaveris" with future perfect - **Ablative of separation**: "vacuum a virtutibus"', 'From Seneca''s moral philosophy, emphasizing constant cultivation of virtue. Reflects Stoic teaching about productive use of time.', 15, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-043', 'Vergil on Fame', 'Fama, malum qua non aliud velocius ullum: mobilitate viget viresque acquirit eundo.', 'Rumor, than which no other evil is swifter: it thrives on movement and gains strength by going.', '{"fama": {"meaning": "rumor/fame", "grammar": "nominative singular"}, "malum": {"meaning": "evil", "grammar": "nominative singular"}, "qua": {"meaning": "than which", "grammar": "ablative relative"}, "non": {"meaning": "not", "grammar": "negative"}, "aliud": {"meaning": "other", "grammar": "nominative singular"}, "velocius": {"meaning": "swifter", "grammar": "comparative neuter"}, "ullum": {"meaning": "any", "grammar": "nominative singular"}, "mobilitate": {"meaning": "by movement", "grammar": "ablative singular"}, "viget": {"meaning": "thrives", "grammar": "3rd person singular present"}, "viresque": {"meaning": "and strength", "grammar": "accusative plural + -que"}, "acquirit": {"meaning": "gains", "grammar": "3rd person singular present"}, "eundo": {"meaning": "by going", "grammar": "gerund, ablative"}}', '- **Relative clause**: "qua non aliud velocius" - than which nothing faster - **Comparative construction**: "velocius" with implied comparison - **Ablative of means**: "mobilitate" and "eundo" show how it operates - **Personification**: Fame treated as living entity - **Alliteration**: "velocius...viget...viresque" creates sound effect', 'From Vergil''s Aeneid, describing the nature of rumor. Shows Roman understanding of how information spreads rapidly.', 12, 'Vergil', 'Aeneid');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-044', 'Ovid on Love', 'Militat omnis amans, et habet sua castra Cupido: Attice, crede mihi, militat omnis amans.', 'Every lover is a soldier, and Cupid has his own camp: Atticus, believe me, every lover is a soldier.', '{"militat": {"meaning": "serves as soldier", "grammar": "3rd person singular present"}, "omnis": {"meaning": "every", "grammar": "nominative singular"}, "amans": {"meaning": "lover", "grammar": "nominative singular"}, "et": {"meaning": "and", "grammar": "conjunction"}, "habet": {"meaning": "has", "grammar": "3rd person singular present"}, "sua": {"meaning": "his own", "grammar": "accusative plural"}, "castra": {"meaning": "camp", "grammar": "accusative plural"}, "Cupido": {"meaning": "Cupid", "grammar": "nominative singular"}, "Attice": {"meaning": "Atticus", "grammar": "vocative"}, "crede": {"meaning": "believe", "grammar": "2nd person singular imperative"}, "mihi": {"meaning": "me", "grammar": "dative singular"}}', '- **Present participle as noun**: "amans" - lover - **Military metaphor**: "militat" compares love to warfare - **Possessive adjective**: "sua castra" - his own camp - **Repetition**: "militat omnis amans" repeated for emphasis - **Vocative address**: "Attice" calls attention to reader - **Imperative**: "crede mihi" seeks credibility', 'From Ovid''s "Amores," using military imagery for love. Reflects Roman cultural connection between warfare and romance.', 13, 'Ovid', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'advanced', 'ADV-045', 'Horace on Moderation', 'Est modus in rebus, sunt certi denique fines, quos ultra citraque nequit consistere rectum.', 'There is a measure in things, there are finally certain boundaries, beyond and on this side of which right cannot stand.', '{"est": {"meaning": "there is", "grammar": "3rd person singular present"}, "modus": {"meaning": "measure", "grammar": "nominative singular"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "rebus": {"meaning": "things", "grammar": "ablative plural"}, "sunt": {"meaning": "there are", "grammar": "3rd person plural present"}, "certi": {"meaning": "certain", "grammar": "nominative plural"}, "denique": {"meaning": "finally", "grammar": "adverb"}, "fines": {"meaning": "boundaries", "grammar": "nominative plural"}, "quos": {"meaning": "which", "grammar": "accusative plural relative"}, "ultra": {"meaning": "beyond", "grammar": "preposition + accusative"}, "citraque": {"meaning": "and on this side", "grammar": "adverb + -que"}, "nequit": {"meaning": "cannot", "grammar": "3rd person singular present"}, "consistere": {"meaning": "stand", "grammar": "infinitive"}, "rectum": {"meaning": "right", "grammar": "nominative singular"}}', '- **Existential construction**: "est modus" - there is measure - **Relative clause**: "quos ultra citraque" - beyond which - **Correlative structure**: "ultra citraque" - on both sides - **Complementary infinitive**: "consistere" completes "nequit" - **Abstract nouns**: "modus," "fines," "rectum" express philosophical concepts', 'From Horace''s Satires, advocating the golden mean. Reflects Roman adaptation of Greek philosophical ideals about moderation.', 15, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-016', 'Juvenal Satires 10.356-366', 'Orandum est ut sit mens sana in corpore sano. fortem posce animum mortis terrore carentem, qui spatium vitae extremum inter munera ponat naturae, qui ferre queat quoscumque labores, nesciat irasci, cupiat nihil et potiores Herculis aerumnas credat saevosque labores et venere et cenis et pluma Sardanapalli.', 'One should pray that there be a sound mind in a sound body. Ask for a brave spirit lacking fear of death, which places life''s final span among nature''s gifts, which can bear whatever labors, knows not how to be angry, desires nothing, and believes the hardships of Hercules and his savage labors better than the love and feasts and luxury of Sardanapalus.', '{"orandum": {"meaning": "one should pray", "grammar": "gerundive"}, "est": {"meaning": "it is", "grammar": "3rd person singular present"}, "ut": {"meaning": "that", "grammar": "conjunction"}, "sit": {"meaning": "there be", "grammar": "3rd person singular present subjunctive"}, "mens": {"meaning": "mind", "grammar": "nominative singular"}, "sana": {"meaning": "sound", "grammar": "nominative singular"}, "in": {"meaning": "in", "grammar": "preposition + ablative"}, "corpore": {"meaning": "body", "grammar": "ablative singular"}, "sano": {"meaning": "sound", "grammar": "ablative singular"}, "fortem": {"meaning": "brave", "grammar": "accusative singular"}, "posce": {"meaning": "ask for", "grammar": "2nd person singular imperative"}, "animum": {"meaning": "spirit", "grammar": "accusative singular"}, "mortis": {"meaning": "of death", "grammar": "genitive singular"}, "terrore": {"meaning": "fear", "grammar": "ablative singular"}, "carentem": {"meaning": "lacking", "grammar": "present participle, accusative"}, "qui": {"meaning": "which", "grammar": "nominative singular relative"}, "spatium": {"meaning": "span", "grammar": "accusative singular"}, "vitae": {"meaning": "of life", "grammar": "genitive singular"}, "extremum": {"meaning": "final", "grammar": "accusative singular"}, "inter": {"meaning": "among", "grammar": "preposition + accusative"}, "munera": {"meaning": "gifts", "grammar": "accusative plural"}, "ponat": {"meaning": "places", "grammar": "3rd person singular present subjunctive"}, "naturae": {"meaning": "of nature", "grammar": "genitive singular"}, "ferre": {"meaning": "to bear", "grammar": "infinitive"}, "queat": {"meaning": "can", "grammar": "3rd person singular present subjunctive"}, "quoscumque": {"meaning": "whatever", "grammar": "accusative plural"}, "labores": {"meaning": "labors", "grammar": "accusative plural"}, "nesciat": {"meaning": "knows not", "grammar": "3rd person singular present subjunctive"}, "irasci": {"meaning": "to be angry", "grammar": "infinitive"}, "cupiat": {"meaning": "desires", "grammar": "3rd person singular present subjunctive"}, "nihil": {"meaning": "nothing", "grammar": "indeclinable"}, "et": {"meaning": "and", "grammar": "conjunction"}, "potiores": {"meaning": "better", "grammar": "accusative plural comparative"}, "Herculis": {"meaning": "of Hercules", "grammar": "genitive singular"}, "aerumnas": {"meaning": "hardships", "grammar": "accusative plural"}, "credat": {"meaning": "believes", "grammar": "3rd person singular present subjunctive"}, "saevosque": {"meaning": "and savage", "grammar": "accusative plural + -que"}, "venere": {"meaning": "love", "grammar": "ablative singular"}, "cenis": {"meaning": "feasts", "grammar": "ablative plural"}, "pluma": {"meaning": "luxury", "grammar": "ablative singular"}, "Sardanapalli": {"meaning": "of Sardanapalus", "grammar": "genitive singular"}}', '- **Passive periphrastic**: "orandum est" - one must pray - **Purpose clause**: "ut sit" with subjunctive - **Imperative**: "posce" commands the reader - **Present participle**: "carentem" describes the spirit - **Relative clauses**: Multiple "qui" clauses characterize the ideal spirit - **Subjunctive in relative clause**: Shows characteristic or result - **Complementary infinitives**: "ferre," "irasci" complete modal verbs - **Comparative**: "potiores" compares two lifestyles', 'From Juvenal''s tenth satire on the vanity of human wishes, presenting the famous "mens sana in corpore sano" ideal. Contrasts Stoic virtue with decadent luxury, using mythological examples.', 42, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-017', 'Petronius Satyricon 111', 'Abiit ad plures. Eheu fugaces, Postume, Postume, labuntur anni, nec pietas moram rugis et instanti senectae adferet, heu, nec morte domabili vitae!', 'He has gone to join the majority. Alas, fleeting years slip away, Postumus, Postumus, and piety will not bring delay to wrinkles and approaching old age, alas, nor to the life that death can master!', '{"abiit": {"meaning": "he has gone", "grammar": "3rd person singular perfect"}, "ad": {"meaning": "to", "grammar": "preposition + accusative"}, "plures": {"meaning": "more/majority", "grammar": "accusative plural"}, "eheu": {"meaning": "alas", "grammar": "interjection"}, "fugaces": {"meaning": "fleeting", "grammar": "nominative plural"}, "Postume": {"meaning": "Postumus", "grammar": "vocative, repeated"}, "labuntur": {"meaning": "slip away", "grammar": "3rd person plural present"}, "anni": {"meaning": "years", "grammar": "nominative plural"}, "nec": {"meaning": "neither", "grammar": "conjunction"}, "pietas": {"meaning": "piety", "grammar": "nominative singular"}, "moram": {"meaning": "delay", "grammar": "accusative singular"}, "rugis": {"meaning": "to wrinkles", "grammar": "dative plural"}, "et": {"meaning": "and", "grammar": "conjunction"}, "instanti": {"meaning": "approaching", "grammar": "dative singular participle"}, "senectae": {"meaning": "old age", "grammar": "dative singular"}, "adferet": {"meaning": "will bring", "grammar": "3rd person singular future"}, "heu": {"meaning": "alas", "grammar": "interjection"}, "morte": {"meaning": "by death", "grammar": "ablative singular"}, "domabili": {"meaning": "that can be mastered", "grammar": "ablative singular"}, "vitae": {"meaning": "life", "grammar": "dative singular"}}', '- **Euphemistic expression**: "abiit ad plures" - died - **Apostrophe**: "Postume, Postume" - emotional address - **Present tense**: "labuntur" for vivid ongoing action - **Future tense**: "adferet" shows what will not happen - **Dative of disadvantage**: "rugis...senectae...vitae" - harm to these - **Participle**: "instanti" describes approaching old age - **Ablative of agent**: "morte" with passive adjective', 'Parody of Horace''s serious meditation on mortality, showing Petronius''s satirical style. Reflects Roman consciousness of time''s passage and death''s inevitability.', 22, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-018', 'Ammianus Marcellinus 31.2.1-2', 'Hunni autem, ultra paludes Meoticas habitantes, omnem modum feritatis excedunt. Et quoniam apud eos infantum tenera adhuc et sine barba ora ferro sulcantur, ut vim pilorum suppressa incrementa cicatricum hebetent, senescunt imberbes sine venustate ulla, eunuchis similes.', 'The Huns, however, dwelling beyond the Maeotic marshes, exceed every measure of savagery. And since among them the tender faces of infants, still beardless, are furrowed with iron, so that the suppressed growth of scars may blunt the force of hair, they grow old beardless without any beauty, similar to eunuchs.', '{"Hunni": {"meaning": "Huns", "grammar": "nominative plural"}, "autem": {"meaning": "however", "grammar": "conjunction"}, "ultra": {"meaning": "beyond", "grammar": "preposition + accusative"}, "paludes": {"meaning": "marshes", "grammar": "accusative plural"}, "Meoticas": {"meaning": "Maeotic", "grammar": "accusative plural"}, "habitantes": {"meaning": "dwelling", "grammar": "present participle, nominative plural"}, "omnem": {"meaning": "every", "grammar": "accusative singular"}, "modum": {"meaning": "measure", "grammar": "accusative singular"}, "feritatis": {"meaning": "of savagery", "grammar": "genitive singular"}, "excedunt": {"meaning": "exceed", "grammar": "3rd person plural present"}, "et": {"meaning": "and", "grammar": "conjunction"}, "quoniam": {"meaning": "since", "grammar": "conjunction"}, "apud": {"meaning": "among", "grammar": "preposition + accusative"}, "eos": {"meaning": "them", "grammar": "accusative plural"}, "infantum": {"meaning": "of infants", "grammar": "genitive plural"}, "tenera": {"meaning": "tender", "grammar": "nominative plural"}, "adhuc": {"meaning": "still", "grammar": "adverb"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "barba": {"meaning": "beard", "grammar": "ablative singular"}, "ora": {"meaning": "faces", "grammar": "nominative plural"}, "ferro": {"meaning": "with iron", "grammar": "ablative singular"}, "sulcantur": {"meaning": "are furrowed", "grammar": "3rd person plural present passive"}, "ut": {"meaning": "so that", "grammar": "purpose conjunction"}, "vim": {"meaning": "force", "grammar": "accusative singular"}, "pilorum": {"meaning": "of hair", "grammar": "genitive plural"}, "suppressa": {"meaning": "suppressed", "grammar": "nominative plural participle"}, "incrementa": {"meaning": "growth", "grammar": "nominative plural"}, "cicatricum": {"meaning": "of scars", "grammar": "genitive plural"}, "hebetent": {"meaning": "may blunt", "grammar": "3rd person plural present subjunctive"}, "senescunt": {"meaning": "they grow old", "grammar": "3rd person plural present"}, "imberbes": {"meaning": "beardless", "grammar": "nominative plural"}, "venustate": {"meaning": "beauty", "grammar": "ablative singular"}, "ulla": {"meaning": "any", "grammar": "ablative singular"}, "eunuchis": {"meaning": "to eunuchs", "grammar": "dative plural"}, "similes": {"meaning": "similar", "grammar": "nominative plural"}}', '- **Present participle**: "habitantes" describes the Huns - **Genitive of possession**: "infantum...ora" - faces of infants - **Ablative of means**: "ferro" - by means of iron - **Purpose clause**: "ut...hebetent" with subjunctive - **Perfect passive participle**: "suppressa" modifies "incrementa" - **Ablative of separation**: "sine barba," "sine venustate" - **Comparison**: "eunuchis similes" - like eunuchs', 'From Ammianus''s account of barbarian peoples threatening the late Roman Empire. Shows Roman ethnographic interest and cultural prejudices about "barbarian" customs.', 39, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-019', 'Jerome Letters 22.30', 'Scientia inflat, caritas vero aedificat. Melius est rustice loqui et simpliciter sentire quam Aristotelica subtilitate verborum populorum corda decipere. Ego te non philosophum esse volo sed Christianum.', 'Knowledge puffs up, but love truly builds up. It is better to speak rustically and think simply than to deceive the hearts of peoples with Aristotelian subtlety of words. I want you to be not a philosopher but a Christian.', '{"scientia": {"meaning": "knowledge", "grammar": "nominative singular"}, "inflat": {"meaning": "puffs up", "grammar": "3rd person singular present"}, "caritas": {"meaning": "love", "grammar": "nominative singular"}, "vero": {"meaning": "truly", "grammar": "adverb"}, "aedificat": {"meaning": "builds up", "grammar": "3rd person singular present"}, "melius": {"meaning": "better", "grammar": "comparative neuter"}, "est": {"meaning": "it is", "grammar": "3rd person singular present"}, "rustice": {"meaning": "rustically", "grammar": "adverb"}, "loqui": {"meaning": "to speak", "grammar": "infinitive"}, "et": {"meaning": "and", "grammar": "conjunction"}, "simpliciter": {"meaning": "simply", "grammar": "adverb"}, "sentire": {"meaning": "to think", "grammar": "infinitive"}, "quam": {"meaning": "than", "grammar": "comparative conjunction"}, "Aristotelica": {"meaning": "Aristotelian", "grammar": "ablative singular"}, "subtilitate": {"meaning": "subtlety", "grammar": "ablative singular"}, "verborum": {"meaning": "of words", "grammar": "genitive plural"}, "populorum": {"meaning": "of peoples", "grammar": "genitive plural"}, "corda": {"meaning": "hearts", "grammar": "accusative plural"}, "decipere": {"meaning": "to deceive", "grammar": "infinitive"}, "ego": {"meaning": "I", "grammar": "nominative"}, "te": {"meaning": "you", "grammar": "accusative"}, "non": {"meaning": "not", "grammar": "negative"}, "philosophum": {"meaning": "philosopher", "grammar": "accusative singular"}, "esse": {"meaning": "to be", "grammar": "infinitive"}, "volo": {"meaning": "I want", "grammar": "1st person singular present"}, "sed": {"meaning": "but", "grammar": "conjunction"}, "Christianum": {"meaning": "Christian", "grammar": "accusative singular"}}', '- **Antithesis**: "scientia inflat" vs "caritas aedificat" - **Comparative construction**: "melius est...quam" - **Complementary infinitives**: "loqui," "sentire," "decipere" - **Ablative of means**: "Aristotelica subtilitate verborum" - **Indirect statement**: "te non philosophum esse...sed Christianum" - **Emphatic positioning**: "ego" at beginning of sentence', 'From Jerome''s letters defending Christian simplicity against pagan philosophical complexity. Reflects the tension between classical learning and Christian faith in late antiquity.', 28, 'Classical Author', 'Classical Work');
INSERT INTO exercises (language, difficulty_level, exercise_code, title, original_text, english_translation, vocabulary, grammar_notes, cultural_context, word_count, author, work) VALUES
('latin', 'beginner', 'MAS-020', 'Cassiodorus Variae 1.1', 'Inter aequales honor dulcis est, inter superiores gloriosus, inter minores suspectus. Nam cum pares nostros cernimus elatos, sine invidia gaudemus; cum maiores, veneratione prosequimur; cum inferiores, sollicitudine perturbamur, ne forte de nobis male suspicentur.', 'Among equals honor is sweet, among superiors glorious, among inferiors suspect. For when we see our peers elevated, we rejoice without envy; when we see superiors, we follow with veneration; when inferiors, we are disturbed with anxiety, lest perhaps they suspect evil of us.', '{"inter": {"meaning": "among", "grammar": "preposition + accusative"}, "aequales": {"meaning": "equals", "grammar": "accusative plural"}, "honor": {"meaning": "honor", "grammar": "nominative singular"}, "dulcis": {"meaning": "sweet", "grammar": "nominative singular"}, "est": {"meaning": "is", "grammar": "3rd person singular present"}, "superiores": {"meaning": "superiors", "grammar": "accusative plural"}, "gloriosus": {"meaning": "glorious", "grammar": "nominative singular"}, "minores": {"meaning": "inferiors", "grammar": "accusative plural"}, "suspectus": {"meaning": "suspect", "grammar": "nominative singular"}, "nam": {"meaning": "for", "grammar": "conjunction"}, "cum": {"meaning": "when", "grammar": "temporal conjunction"}, "pares": {"meaning": "peers", "grammar": "accusative plural"}, "nostros": {"meaning": "our", "grammar": "accusative plural"}, "cernimus": {"meaning": "we see", "grammar": "1st person plural present"}, "elatos": {"meaning": "elevated", "grammar": "accusative plural participle"}, "sine": {"meaning": "without", "grammar": "preposition + ablative"}, "invidia": {"meaning": "envy", "grammar": "ablative singular"}, "gaudemus": {"meaning": "we rejoice", "grammar": "1st person plural present"}, "maiores": {"meaning": "superiors", "grammar": "accusative plural"}, "veneratione": {"meaning": "with veneration", "grammar": "ablative singular"}, "prosequimur": {"meaning": "we follow", "grammar": "1st person plural present"}, "inferiores": {"meaning": "inferiors", "grammar": "accusative plural"}, "sollicitudine": {"meaning": "with anxiety", "grammar": "ablative singular"}, "perturbamur": {"meaning": "we are disturbed", "grammar": "1st person plural present passive"}, "ne": {"meaning": "lest", "grammar": "negative conjunction"}, "forte": {"meaning": "perhaps", "grammar": "adverb"}, "de": {"meaning": "about", "grammar": "preposition + ablative"}, "nobis": {"meaning": "us", "grammar": "ablative plural"}, "male": {"meaning": "badly", "grammar": "adverb"}, "suspicentur": {"meaning": "they suspect", "grammar": "3rd person plural present subjunctive"}}', '- **Parallel structure**: "inter aequales...inter superiores...inter minores" - **Predicate adjectives**: "dulcis," "gloriosus," "suspectus" - **Temporal clauses**: Multiple "cum" clauses with indicative - **Perfect passive participle**: "elatos" describes peers - **Ablative of manner**: "sine invidia," "veneratione," "sollicitudine" - **Purpose clause**: "ne...suspicentur" with subjunctive', 'From Cassiodorus''s official letters during Ostrogothic rule in Italy. Reflects the complex social hierarchies and psychological dynamics of late antique aristocratic society.', 42, 'Classical Author', 'Classical Work');
